#define PI 3.14159265359
#define random(x) (fract(sin(x) * 43758.545))
#define smoothsaw(x) (smoothstep(0.0, 1.0, fract(x)))
#define steprandom(x) (random(floor(x)))
#define randomsaw(x) (smoothsaw(x) * steprandom(x))
#define noise(x) (randomsaw(x) - randomsaw(x - 1.) + steprandom(x - 1.))
#define fractalnoise(x) (noise((x)) + .5*noise((x)/.5) + .25*noise((x)/.25) + .125*noise((x)/.125) + .0625*noise((x)/.0625))
vec2 c;
#define rectangle(center, size, uv) (c = 1.-step(size, abs(uv - center)), c.x * c.y)

#define f(x) (sin( (x) * cos(x) ) * cos(iTime + (x)))
#define g(x) (sin( ((x) * 3.) * cos(iTime) * sin(x) ))
float f2(vec2 x) {
    float r = length(x);
    float a = atan(x.y,x.x);
    return r - 1.0 + 0.5*sin(3.0*a+2.0*r*r);
}     

const vec2 h = vec2(0.001, 0.0);	//set this with less precision if lines are weird
//analytical gradient, needs to be written for any particular function
#define grad1(x) (vec2(1.0, cos(x)*sin(x*x) + 2.0*x*cos(x*x)*sin(x)))
#define grad2(x, func) (vec2(1.0, (func(x+h) - func(x-h)) / (2.0*h)))
#define grad2d(uv, func) (vec2( func(uv+h.xy) - func(uv-h.xy), func(uv+h.yx) - func(uv-h.yx) ) / (2.0*h.x))
#define sdf(uv, g, func) ((func(uv.x) - uv.y) / length(g))
#define sdf2d(uv, g, func) ((func(uv)) / length(g))

float scale = 6.;
float eps;


mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}
