void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //https://www.shadertoy.com/view/lsXGzf
    //scale += 5.0*texelFetch( iChannel0, ivec2(KEY_UP,2),0 ).x;
    //scale -= 4.0*texelFetch( iChannel0, ivec2(KEY_DOWN,2),0 ).x;
    float aspect = iResolution.x / iResolution.y;
    vec2 mouse = iMouse.xy/iResolution.y;
    mouse = (mouse == vec2(.0) ? vec2(aspect, 1.) / 2. : mouse);	//for initialization
    vec2 uv = fragCoord/iResolution.y;
    eps = scale / iResolution.y; 	//pixel size, for constant thickness independent of resolution

    mouse -= vec2(aspect/2., .5);
    uv -= vec2(aspect/2., .5);
    uv *= scale;
    mouse *= scale;
    uv -= mouse;

    //dont uncomment this
    //uv = shake(uv);

    vec2 rotuv = rotate2d(5.*sin(iTime / 2.) * PI) * uv;

    vec2 g1 = grad2(uv, f);
    vec2 g2 = grad2(uv.x, g);
    float de = sdf(uv, g1, f);
    float de2 = sdf(uv, g2, g);

    vec3 color, color2;
    color = mix(vec3(175.0,238.0,238.0)/255.0, 
                mix(vec3(.0), vec3(.9, .97, .8), (1. - smoothstep(-2.*eps, .0, de)) ),
                1. - smoothstep(.0, 2.*eps, de));
    color2 = mix(vec3(175.0,238.0,238.0)/255.0, 
                mix(vec3(.0), vec3(.9, .97, .8), (1. - smoothstep(-2.*eps, .0, de2)) ),
                1. - smoothstep(.0, 2.*eps, de2));
    color = min(color, color2);  

    //axes
    color = mix(color, color * vec3(.1, .5, .6), smoothstep(-eps, 0., uv.x) - smoothstep(0., eps, uv.x));	
    color = mix(color, color * vec3(.1, .5, .6), smoothstep(-eps, 0., uv.y) - smoothstep(0., eps, uv.y));

    //ruler
    if(abs(uv.x) > 8. * eps || abs(uv.y) > 8. * eps) {    //dont draw in the origin
        color = mix(color, color * vec3(.1, .5, .6), rectangle(vec2(.0, round(uv.y)), vec2(5.*eps, eps), uv));
        color = mix(color, color * vec3(.1, .5, .6), rectangle(vec2(round(uv.x), .0), vec2(eps, 5.*eps), uv));
    }

    fragColor = vec4(color, 1.0);
}

#define MAX_DIST 100.
#define T iTime/5.
#define PI 3.1415927

float cyl(vec3 p, vec3 d)
{
    return length(p.xz-d.xy)-d.z;
}

float vine(vec3 p, float r)
{
    return cyl(p+vec3(cos(p.y+r),0.,sin(p.y+r)),vec3(1.,1.,.5*cos(clamp(T-p.y/5.+1.,-2.,0.))));
}

vec2 map(vec3 p)
{
    vec3 cell = vec3(18.);
    p.xz = mod(p.xz,cell.xz)-cell.xz/2.;

    float m=0.;
    float c = cyl(p,vec3(1,1,1));

    float v1 = vine(p,0.);
    float v2 = vine(p,PI*.5);
    float v3 = vine(p,PI);
    float v4 = vine(p,PI*1.5);

    float r=min(c,v1);
    r=min(r,v2);
    r=min(r,v3);
    r=min(r,v4);
    if(r==c)m=2.;
    else m=1.;
    return vec2(r,m);

}

vec2 ray(vec3 ro, vec3 rd)
{
    float m=0.,t=0.;
    for(int i=0;i<128;i++)
    {
        vec3 p = ro+rd*t;
        vec2 s = map(p);
        m=s.y;
        if(s.x<0.00001)break;
        t+=s.x/3.;
        if(t>MAX_DIST){t=-1.; break;}
    }
    return vec2(t,m);
}


vec3 normal(vec3 p)
{
    vec2 e=vec2(0.0005,0.);
    return normalize(vec3(
        map(p+e.xyy).x-map(p-e.xyy).x,
        map(p+e.yxy).x-map(p-e.yxy).x,
        map(p+e.yyx).x-map(p-e.yyx).x
        ));
}

void mainImage( out vec4 c, in vec2 f)
{
    vec2 p = (2.*f-iResolution.xy)/iResolution.y;

    float h = T*5.+6.;
    vec3 ro = vec3(0.1,h-1.9,0);
    vec3 ta = vec3(0,h-1.83,0);
    vec3 cf = normalize(ta-ro);
    vec3 cu = normalize(cross(cf,vec3(0,1,0)));
    vec3 cr = normalize(cross(cu,cf));
    vec3 rd = vec3(p.x*cu+p.y*cr+1.*cf);

    vec2 r = ray(ro,rd);

    vec3 col =vec3(0.);
    if(r.x>0.)
    {
        vec3 p = ro+rd*r.x;
        vec3 n = normal(p);

        vec3 mate = vec3(1.0);
        vec2 az=vec2(atan(p.y,p.x),length(p));
        if(r.y<1.5)mate=vec3(0,1,0)+texture(iChannel1,vec2(az.y,p.x)).rgb/3.;
        else if (r.y<2.5)mate=vec3(1,0.5,.4)+texture(iChannel0,vec2(abs(az.y),(-.6+p.x))).rgb;

        vec3 sun = normalize(vec3(3.,0.3,1.));
        float dif = clamp(dot(sun,n),0.0,1.0);        
        col = mate*vec3(0.2,0.4,0.5)*dif*(1.-r.x/MAX_DIST);
    }

    c.rgb=col;
}

