    return fbm(vec4(x, y, 0., 0.));
}

float fbm(float x, float y, float z){
    return fbm(vec4(x, y, z, 0.));
}

float fbm(float x, float y, float z, float w){
    return fbm(vec4(x, y, z, w));
}


