    vec2 originalUV = uv.xy;
//    uv = fract(uv*5.)-.5;
    uv = fract(uv*5.)-.5;
    uv *= 30.;
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv)+.5;
    float distanceFromTileCenter = length(id*30.);
    float distanceFromOriginalCenter = length(originalUV*.2);
    float rawNoise = fbm(
        distanceFromTileCenter, distanceFromOriginalCenter,
        timeOrigin.x+timeRadius*cos(t), timeOrigin.y+timeRadius*sin(t)
    );
    float smoothNoise = smoothstep(-0.5,1.5,rawNoise);
    float fadeToBlack = smoothstep(0.0, 1.1, length(originalUV));
    float hueStart = .5;
    float hueRange = 0.75;
    vec3 color = rgb(
        hueStart+hueRange*smoothNoise,
        1.-smoothNoise,
        clamp(pow(smoothNoise, 0.4), 0., 1.)
    );
    fragColor = vec4(color-fadeToBlack, 1.);
}

vec2 rotate(vec2 v, float angle) {
    angle = angle * 3.1415;
	float s = sin(angle);
	float c = cos(angle);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}

float invert(float inUV) {
    inUV = min(1., max(0., inUV));
	return 1.-inUV;
}

float HexDist(vec2 p) {
	p = abs(p);

    float c = dot(p, normalize(vec2(1,1.73205080757)));
    c = max(c, p.x);

    return c;
}

vec4 HexCoords(vec2 uv) {
	vec2 r = vec2(1, 1.73205080757);
    vec2 h = r*.5;

    vec2 a = mod(uv, r)-h;
    vec2 b = mod(uv-h, r)-h;

    vec2 gv = dot(a, a) <= dot(b,b) ? a : b;

    float x = atan(gv.x, gv.y);
    float y = .5-HexDist(gv);
    vec2 id = floor(uv-gv + vec2(.00001, .00001));
    return vec4(x, y, id.x,id.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float stepTime = floor(iTime);
    float sawTime = mod(stepTime, 2.0);
    float halfSawTime = mod(floor(iTime/2.), 2.);
    float sawTimeInv = 1.- sawTime;
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    uv = rotate(uv, 4. *floor(stepTime/2.)/6.);

    vec3 col = vec3(0);

    uv *= 4.;

    vec2 motionDir = vec2(-1./2.,1.73205080757/2.);

    float hexmotion = iTime;
    vec2 uvSten = uv;
    uvSten = rotate(uvSten, 2./3.);
    uvSten = mod(floor(uvSten/.86602540378), 2.0);


    vec4 hc1 = HexCoords(uv);
    vec4 hc2 = HexCoords(uv+(motionDir * hexmotion));
    vec4 hc3 = HexCoords(uv+(motionDir * -hexmotion));
    vec4 hc4 = mix(hc2, hc3, uvSten.y);

    vec4 hc = mix(hc1, hc4, sawTimeInv);

    float rotUV = ((hc.x/3.1415) + 1.) /2.;
    float rotTime = (mod(iTime, 1.0) /3.) + 1./3.;

    float c1 = step(rotUV, 1./3.);
    float c2 = step(rotUV, rotTime);
    float c = mix(c1, c2, sawTime);
    float r = invert(c);
    c = mix(c, r, halfSawTime);

    col = vec3(c);


    fragColor = vec4(col,1.0);

}

#define radius_static .06 
#define BGB true

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xy; //Center on screen (-.5 to .5)
	uv.x = uv.x * (iResolution.x/iResolution.y); //Adjust x cord for aspect ratio


    vec3 ro = vec3(0);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    vec3 s = vec3(sin(iTime)/6.,0, .3 - cos(iTime)/8.);

    float t = dot(s-ro, rd);

    vec3 p = ro + rd*t;

    float y = length(p-s);

    float radius_distort = (sin((fragCoord.y)/6.+iTime)+cos((fragCoord.x)/6.+iTime))/600.; 
    // float radius_distort = sin((fragCoord.x*fragCoord.y)/4000.+iTime)/500.; // Liner

    float radius = radius_static + radius_distort;

    float x = sqrt(radius*radius - y*y);

    float t1 = x + t;

    float t2 = x - t;

    float dist = length(ro +rd*t1);

    float blink_bg = BGB ? abs(sin(iTime)) : 1.;

    //vec3 ballcolor = vec3(1, 0, 0);
    vec3 ballcolor = vec3(float((int(fragCoord.x)*2)^(int(fragCoord.y)*2))/((iResolution.x*iResolution.y)/300.),.2,0); // Transparent

  	vec3 col = y > radius ? vec3(0,0,float(int(fragCoord.x)^int(fragCoord.y))/((iResolution.x*iResolution.y)/300./blink_bg) ) : //Generate background
    	mix(vec3(x*20. - dist), ballcolor, .4);//"Raytraced" ball

    // Output to screen
    fragColor = vec4(col,1.0);
}

