#define PI 3.1415927

float cir(vec2 p, float r)
{
    return smoothstep(9./iResolution.y,0.,length(p)-r);
}

void mainImage( out vec4 c, in vec2 f )
{
    vec2 p = 2.*((2.*f-iResolution.xy)/iResolution.y), c1, c2;
    float t = iTime*2.+atan(p.x,p.y)-atan(p.y,p.x), r = .5, o = .25, b=cos(t)*r, z= sin(t)*r;
    for(float x=-1.; x<=1.; x++)
        for(float y=-1.;y<=1.;y++)
        {
            c1 = (p-vec2(x+o,y+o))+vec2(z,b);
            c2 = (p-vec2(x-o,y-o))-vec2(z,b);
            c.rgb += vec3(cir(c2,.1)+cir(c1,.1));
         }

}

#define R .4
#define PI 3.1415927
#define SPEED 1.2
#define DIST .25
#define GLOW 1.9
#define ROTSPEED .02
#define LAYERS 20.
#define PHI - PI / 9.
#define BARS 32.
#define SS(a,b,t) smoothstep(a,b,t)

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float speed(float x) {
    x *= BARS;
    x = fract(floor(abs(x)) / BARS);
	return SPEED + (pow(texture(iChannel0, vec2(x, .25)).r, 3.) - .5);
}

vec2 random2(vec2 st){
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}

// Value Noise by Inigo Quilez - iq
// https://www.shadertoy.com/view/lsf3WH
float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

float windTexture(vec3 uvw, float speed) {
	/*
    return texture(iChannel0,
        uvw.xy /
            (1. + uvw.z) + // the nearer the less dense
        vec2(uvw.z + iTime * SPEED * .5 * uvw.z, // the nearer the quicker
            uvw.z)).r;
	*/
    vec2 st = vec2(
        uvw.xy /
        	(1. + uvw.z) +
        vec2(uvw.z + iTime * speed * .5 * (uvw.z + .1),
             uvw.z)) * 72.;

    return pow(noise(st) * 3.2, 2.);
}

float wind(vec3 uvw) {
    float col = 0.;
    float dist = length(uvw);
    float circle = SS(-.02, .01, 1. - dist);

    // rotate
    uvw.xy = uvw.xy * rotate2d(PHI);

    // glow
    float d = max(0., DIST - max(0., dist - 1.)) / DIST;
    float gx = max(0.2, uvw.x);
    float glowspeed = speed(uvw.y);

    // "around" planet
    uvw -= d * normalize(uvw) * .5;
    uvw.y *= 1. + 
        SS(0., 1. + abs(uvw.z) / R, max(0., -uvw.x * dist)) + // after
        SS(0., .3 + abs(uvw.z) / R, max(0., uvw.x * dist)); // before

    col = SS(.1, 1., windTexture(uvw, SPEED)) *
        (1. - circle); // don't draw into the planet

    return (1. + d * gx * GLOW * glowspeed) * col + d * gx * gx * GLOW * glowspeed;
}

vec3 planet(vec2 uv, float wcol, float speed) {
    vec3 uvw = vec3(uv, sqrt(1. - dot(uv, uv)));

    // "hot air"
    vec2 hot = vec2(-cos(PHI), sin(PHI)) * (wcol - .5) * speed * .1;

    vec2 longLat = vec2(
        (atan(uvw.x, uvw.z) / PI + 1.0) * 0.5,
        (asin(uvw.y) / PI + 0.5)
    );

    vec4 surface = 
        sqrt(uvw.z) * // sphere
        texture(iChannel1, longLat - hot + ROTSPEED * vec2(iTime, 0.)) * // sphere mapped texture
		max(.2, ((cos(-PHI) * uvw.x + .5) + (sin(-PHI) * uvw.y + .5) + uvw.z) / 2.); // sun direction

   	return surface.rgb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates (0, 0 in the center)
    vec2 uv = fragCoord.xy / iResolution.xy -.5;
    uv.x *= iResolution.x / iResolution.y;

    uv = uv / R;

    vec3 uvw = vec3(0.);
    float wcol = 0.;

    for (float w = 0.01; w < 1.5; w += 1.5 / LAYERS) {
    	uvw = vec3(uv, w);
    	wcol += wind(uvw) / LAYERS;
    }

    // TODO
    // - wind color

    vec3 col = vec3(wcol * 1.2, wcol * wcol, wcol * wcol * wcol * .8) + max(vec3(0.), .5 * planet(uv, wcol, SPEED));

    float vignette = min(.4 * (3.2 - length(uv)), 1.);

    fragColor = vec4(vignette * col, 1.);
}

/*

	IQ RENDER ENGINE

	Documented and Composed by Dirk Stockton

	This Shader is a reconstruction and documentation of IQ's Happy Jumping Ray marching Tutorial.

	SHADERTOY ~ https://www.shadertoy.com/view/3lsSzf
	YOUTUBE ~ https://www.youtube.com/watch?v=Cfe5UQ-1L9Q&t=7560s

	I hope it is as helpful to you as it was to me :)

*/


//SDF( Signed Distance Functions )

//SPHERE
float sdSphere( in vec3 pos, float rad )
{
 	return length(pos) - rad;   
}

//ELIPSOID
float sdEllipsoid( in vec3 pos, vec3 rad )
{
 	float k0 = length(pos/rad);
 	float k1 = length(pos/rad/rad);
    return k0*(k0-1.0)/k1;
}

//STICK
float sdStick( in vec3 p, vec3 a, vec3 b, float ra, float rb )
{
	vec3 ba = b-a;
    vec3 pa = p-a;

    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );

    float r = mix( ra, rb, h );
    return length(pa-h*ba) - r;
}

//SMOOTH MIN
float smin( in float a, in float b, float k )
{
    float h = max( k - abs(a-b), 0.0 );
 	return min(a,b) - h*h/(k*4.0);   
}

//MODEL( Bat )
vec2 sdBat( in vec3 pos )
{
    float t = fract(iTime/2.0);  				//Loop time with FRACT

 	float y = 2.0*t*(1.0-t);				//Motion Function
    float dy = 4.0*(1.0-2.0*t);				//Motion Derivitive( to find tangent )

    vec2 u = vec2(1.0,dy);					//U axis of UV system
    vec2 v = vec2( -dy,1.0);				//V axis of UV system

    vec3 cen = vec3(0.0,y,0.0);				//Move center

    float sy = 0.5 + 0.5*y;					//Horizontal stretch
    float sz = 0.5 + 0.2/sy;						//Vertical Stretch

    vec3 rad = vec3(0.25,0.25*sy,0.25*sz);	//Radius of Stretched Sphere

    vec3 q = pos - cen;						//Coordinate System Refence to Origin

    float d = sdEllipsoid(q,rad);			//Body Sphere

    vec3 h = q;								//Head Location

    vec3 sh = vec3( abs(h.x), h.yz );		//Reflection about X

    //Head
    float d2 = sdEllipsoid(h-vec3(0.0,0.28,0.0),vec3(0.2));  //Head Shape

    d = smin(d,d2,0.1);										//Add Head to Body

    //Horns
    vec3 ho = sh-vec3(0.15,0.35,0.15);						//Horn Position with refletion
    ho.xy = (mat2(3,-4,4,3)/5.0)*ho.xy;						//Horn Rotation( Pythagrian Triples )

    d2 = sdEllipsoid(ho,vec3(0.06,0.015,0.015));			//Horn Shape
    d = smin(d,d2,0.04);									//Add Horns to Body

    //Stick
    d2 = sdStick( sh, vec3(0.2,0.01,0.1),vec3(0.3,0.4*t-0.2,0.1), 0.01, 0.03 );	//Arm Shape with Reflection about X
    d = smin(d,d2,0.04);														//Add Arms to Body			

    vec2 res = vec2(d,2.0);	//Material

    float d4 = sdSphere( sh - vec3(0.1,0.25,0.15), 0.05 );	//EYES
    if( d4<d )												//Surface Eye material
    {
     	res = vec2(d4,3.0 );   
    }

    return res;	//Return Model Distance field
}

//MAP - Returns Distance field based on SDF( Signed Distance Functions ) combined using min.
vec2 map(in vec3 pos )				
{
	vec2 d1 = sdBat(pos); 					//Sphere Distance

    float fh = -0.1 + 0.05*(sin(2.0*pos.x) + sin(2.0*pos.z));
    float d2 = pos.y - fh;	   			//Floor Distance

    return (d2<d1.x) ? vec2(d2,1.0) : d1;	//Total Scene Depth
}

//CALCULATE NORMAL - Returns surface angle of passed point
vec3 calcNormal( in vec3 pos ) 				
{

	vec2 e = vec2(0.0001,0.0);				//Perspective Precision
 	return normalize( 
    	vec3(map(pos+e.xyy).x-map(pos-e.xyy).x,   	//Perspective Math
             map(pos+e.yxy).x-map(pos-e.yxy).x,		//...
             map(pos+e.yyx).x-map(pos-e.yyx).x ) );	//...
}

//CAST RAY - from Ray Origin( ro ) about the Ray Direction( rd )
vec2 castRay( in vec3 ro, vec3 rd )
{
    float m = -1.0;
    float t = 0.0;					//Ray Length

    for( int i=0; i<100; i++ ){		//Ray Steps about Z into the horizon

    	vec3 pos = ro + t*rd;		//Ray position formula	

        vec2 h = map(pos);			//Map position( returns depth of point )

        m = h.y;					//Assign Material ID to 'm'

        if( h.x<0.001 ) break;		//Near Clipping Plane( too close to render )

        t+= h.x;					//Progress the ray

        if( t>20.0 ) break;			//Far clipping plane( render the sky )
    }
    if( t>20.0) m=-1.0;				//Disguard overshot rays

    return vec2(t,m);				//Return Ray Distance
}


//MAIN IMAGE composes the camera, lights, distances, and textures into a final pixar 
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2  p = (2.0*fragCoord-iResolution.xy)/iResolution.y;	//Center Screen Coordinates

    float an = 10.0*iMouse.x/iResolution.x;					//User Input Facing Angle;

   	vec3 ta = vec3(0.0,0.55,0.0);							//Target Angle
    vec3  ro = ta + vec3(1.5*sin(an),0.0,1.5*cos(an)); 			//Camera Position

    vec3 ww = normalize( ta - ro );							//Rotation Math
    vec3 uu = normalize( cross(ww,vec3(0,1,0)));			//Rotation Math
    vec3 vv = normalize( cross(uu,ww) );					//Rotation Math

    float len = 1.5;										//Lens Aperture
    vec3  rd = normalize( p.x*uu + p.y*vv + len*ww );		//Camara Direction( NORMALIZED )

    vec3  col = vec3(0.8,0.4,0.3) - 0.7*rd.y;				//Sky Color
    col = mix( col,vec3(0.8,0.8,0.7), exp(-10.0*rd.y));		//Sky Color 2 + Gradient Mixture

	//TRACE SCENE
    vec2 tm = castRay( ro, rd );							
    if( tm.y>0.0 )							//If( Beyond Near Clipping Plane )
    {
        float t = tm.x;						//Distance
        vec3 pos = ro + t*rd;				//Depth Map
        vec3 nor = calcNormal(pos);			//Normal Map

        vec3 mate = vec3(0.18);				//Base Material

        if( tm.y<1.5 )						//Grass Material
        {
         	mate = vec3(0.02,0.04,0.04);
            float f = -1.0+2.0*smoothstep( -0.1,0.1,sin(18.0*pos.x)+sin(18.0*pos.y)+sin(18.0*pos.z));
            mate += f*0.05*vec3(0.5,0.8,0.8);
        }
        else
        if( tm.y<2.5 )						//Skin Material
        {
         	mate = vec3(0.4,0.4,0.5);      	//Cream
        }
        else								//Eye Material
        {
         	mate = vec3(0.01,0.01,0.01);    //Black
        }

        //Build Lights
        vec3  sun_dir = normalize( vec3(0.8,0.4,0.2) );							//Sun Direction
        float sun_dif = clamp( dot(nor,sun_dir),0.0,1.0);						//Sun Difuse
        float sun_sha = step(castRay( pos+nor*0.001,sun_dir ).y,0.0);			//Sun Shadow
        float sky_dif = clamp( 0.5 + 0.5*dot(nor,vec3(0.0,1.0,0.0)),0.0,1.0);	//Sky Difuse
        float bou_dif = clamp( 0.5 + 0.5*dot(nor,vec3(0.0,-1.0,0.0)),0.0,1.0);	//Bounce Difuse

        //Apply Lights
        col =  mate*vec3(7.0,4.5,3.0)*sun_dif*sun_sha;	//Sun Color
        col += mate*vec3(0.5,0.8,0.9)*sky_dif;			//Sky Color
        col += mate*vec3(0.7,0.3,0.2)*bou_dif;			//Earth Color

        col *= 0.9; //Gamma Correction
    }

    col = pow( col, vec3(0.4545) ); //Alpha Correction

    //RENDER
    fragColor = vec4(col,1.0);
}

float H21(vec2 p) {
    vec3 h = fract(p.xyx * vec3(141.212, 373.184, 107.63));
    h *= dot(h, vec3(p, 1848.177));
    return fract(h.x + (h.y * h.z));
}

vec2 rotate2d(vec2 p, float theta) {
    return p * mat2(cos(theta), -sin(theta), sin(theta), cos(theta));
}


// https://github.com/hughsk/glsl-hsv2rgb/blob/master/index.glsl
vec3 hsv2rgb(vec3 c) {
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // -1.0 <> 1.0
    vec2 uv = (fragCoord - iResolution.xy * 0.5)/iResolution.y;

    float gridScalar = 30.0;

    uv *= gridScalar;

    uv += gridScalar * 3.0;

    uv = rotate2d(uv, 3.14159 * -0.25);


    vec2 id = floor(uv);
    vec2 st = fract(uv) - 0.5;


    float d = length(st);

    vec3 col = hsv2rgb(vec3(H21(id) * d, 0.5, 1.0));

    float radius = sin(iTime * H21(id) * 5.0) * 0.22 + 0.22;
    float border = 0.09;


    float inner = smoothstep(radius - border, radius, d);
    float outer = smoothstep(radius, radius + border, d);


  	float mask = inner - outer;
    col *= mask;

    //col.b = smoothstep(0.09, 0.07, d);
    //col = vec3(H21(uv));

    fragColor = vec4(col, 1.0);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float r,g,b;

    r=0.2;
    g=0.2;
    b=0.57;

    float time=iTime*4.0;
 	float ot1=5.0;
    float ot3=2.0;
    float ot5=0.1;
    float ot7=0.01;
    float ot9=0.025;
 	float Q=5000.;
	vec2 uv = fragCoord/iResolution.xy;
    float amnt;
	float nd;
	float ip;
	float alpha;
	vec4 cbuff = vec4(0.0);
	for(float i=0.0; i<10.0;i++){

	ip=i-2.0;
	  nd = 1.0/4.0*ot1*sin(uv.x*2.0*3.14+ip*0.4+time*0.05)/2.0;

	  nd += 1.0/4.0*ot3*sin(3.0*uv.x*2.0*3.14+ip*0.4)/2.0;

	  nd += 1.0/4.0*ot5*sin(5.0*uv.x*2.0*3.14+ip*0.4)/2.0;

	  nd += 1.0/4.0*ot7*sin(7.0*uv.x*2.0*3.14+ip*0.4)/2.0;

	  nd/=5.0;
	  nd+=0.5;
	  amnt = 1.0/abs(nd-uv.y)*0.01;
      amnt= smoothstep(0.01, 0.5+10.0*uv.y, amnt)*5.5;
	  alpha=(10.0-i)/5.0;
	  cbuff += vec4(amnt*alpha*0.3, amnt*0.3*alpha , amnt*uv.y*alpha,0) ;
	}


	fragColor = vec4(cbuff[0]*r,cbuff[1]*g,cbuff[2]*b,1.0);

}

