vec3 green = vec3(0.42,0.55,0.33).xxx;
vec3 blue = vec3(0.12,0.33,0.45);


float rand(vec2 co){ return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453); }

float sat(float a)
{
    return clamp(a, 0., 1.);
}
bool inRange(float a, float mi, float ma)
{
  return a> mi &&a<ma;
}
float lenny(vec2 v)
{
  return abs(v.x)+abs(v.y);
}
float lenny(vec3 v)
{
  return abs(v.x)+abs(v.y)+abs(v.z);
}

float inC(vec2 uv, vec2 pos, float r)
{
  return 1.0-sat((length(uv-pos) - r)*550.);
}

float cloud(vec2 uv, vec2 pos)
{
  uv.x*=0.8;
  uv*=2.;
  float cr = inC(uv,pos+vec2(0.1,0.),0.05);
  float cl = inC(uv,pos-vec2(0.1,0.),0.05);
  float base = float(abs(uv.x-pos.x)<0.1&&(abs(uv.y-pos.y)<0.051));
  float cm = inC(uv,pos+vec2(0.05,0.05),0.07);
  float cb = inC(uv,pos+vec2(-0.05,0.02),0.07);

  return float(sat(cr+cl+cm+cb+base));
}

float rdrMask(vec2 uv)
{
  float c = cloud(uv, vec2(sin(iTime*0.5)*0.5,0.3));
  float c2=cloud(uv*vec2(-1.,1.)*0.5, vec2(0.3*sin(iTime*0.1),0.2));

  float inCircle = 1.0-sat((length(uv) - 0.4)*500.);
  return inCircle * float((1.-c)*(1.-c2));
}


vec3 rdrWater(vec2 uv)
{
  float shape = sin(uv.y*20.)*0.1;
  float thick =0.4*(0.1-uv.y);
  float f =float(inRange(uv.x,shape-thick, shape+thick));

  vec3 col =mix(green, blue, sin(iTime+uv.y*50.)*0.2+rand(uv.yx));
  float light =dot(normalize(uv+vec2(0.+0.1*sin(iTime+pow(1.0+uv.y*3., 2.)*103.),0.)),vec2(0.,-1.));
  return col*f*pow(light,5.);
}
vec3 rdr(vec2 uv)
{

  float isUp = float(sat((0.1*sin(uv.x*10.-4.)*250.+uv.y*450.)));
  vec2 uvo = uv;
  vec3 col;
  uvo+= vec2(sin(-iTime)*0.1,cos(-iTime)*0.1);
  for(int i= 0;i<10;++i)
  {
    col += vec3(0.02*float(10-i))*float(length(uvo)<(0.05*float(i)));
  }
  vec3 water = rdrWater(uv)*(1.-isUp);
  return water+mix(green,blue,uv.y*4.+0.3)*isUp+col*(0.8*isUp);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord.xy / iResolution.xx;
    vec2 ouv = uv;
  vec2 center = vec2(0.5)*iResolution.xy/iResolution.xx;
  uv = uv - center;

  uv*=1.6;
  vec3 col =0.7* (1.-length(uv+vec2(0.,-0.3)))*vec3(0.21,0.12,0.657);

  col = col/pow(lenny(col),0.1);
  col = mix(col, rdr(uv), rdrMask(uv));
  col += vec3(0.3,uv.y,0.56)*(rand(uv)*0.1+0.5)*lenny(uv);
  col +=( 1.-length(2.*uv+vec2(0.,-0.3)))*0.5;
  col *= vec3(0.7,0.6,0.55)*1.7;
  fragColor = vec4(col, 1.0);
}

//	Simplex 3D Noise 
//	by Ian McEwan, Ashima Arts
//
vec4 permute(vec4 x){return mod(((x*34.0)+1.0)*x, 289.0);}
vec4 taylorInvSqrt(vec4 r){return 1.79284291400159 - 0.85373472095314 * r;}

float snoise(vec3 v){ 
  const vec2  C = vec2(1.0/6.0, 1.0/3.0) ;
  const vec4  D = vec4(0.0, 0.5, 1.0, 2.0);

// First corner
  vec3 i  = floor(v + dot(v, C.yyy) );
  vec3 x0 =   v - i + dot(i, C.xxx) ;

// Other corners
  vec3 g = step(x0.yzx, x0.xyz);
  vec3 l = 1.0 - g;
  vec3 i1 = min( g.xyz, l.zxy );
  vec3 i2 = max( g.xyz, l.zxy );

  //  x0 = x0 - 0. + 0.0 * C 
  vec3 x1 = x0 - i1 + 1.0 * C.xxx;
  vec3 x2 = x0 - i2 + 2.0 * C.xxx;
  vec3 x3 = x0 - 1. + 3.0 * C.xxx;

// Permutations
  i = mod(i, 289.0 ); 
  vec4 p = permute( permute( permute( 
             i.z + vec4(0.0, i1.z, i2.z, 1.0 ))
           + i.y + vec4(0.0, i1.y, i2.y, 1.0 )) 
           + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));

// Gradients
// ( N*N points uniformly over a square, mapped onto an octahedron.)
  float n_ = 1.0/7.0; // N=7
  vec3  ns = n_ * D.wyz - D.xzx;

  vec4 j = p - 49.0 * floor(p * ns.z *ns.z);  //  mod(p,N*N)

  vec4 x_ = floor(j * ns.z);
  vec4 y_ = floor(j - 7.0 * x_ );    // mod(j,N)

  vec4 x = x_ *ns.x + ns.yyyy;
  vec4 y = y_ *ns.x + ns.yyyy;
  vec4 h = 1.0 - abs(x) - abs(y);

  vec4 b0 = vec4( x.xy, y.xy );
  vec4 b1 = vec4( x.zw, y.zw );

  vec4 s0 = floor(b0)*2.0 + 1.0;
  vec4 s1 = floor(b1)*2.0 + 1.0;
  vec4 sh = -step(h, vec4(0.0));

  vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
  vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

  vec3 p0 = vec3(a0.xy,h.x);
  vec3 p1 = vec3(a0.zw,h.y);
  vec3 p2 = vec3(a1.xy,h.z);
  vec3 p3 = vec3(a1.zw,h.w);

//Normalise gradients
  vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
  p0 *= norm.x;
  p1 *= norm.y;
  p2 *= norm.z;
  p3 *= norm.w;

// Mix final noise value
  vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
  m = m * m;
  return 42.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1), 
                                dot(p2,x2), dot(p3,x3) ) );
}


