bool InsideSquare(vec2 uv, float radius, vec2 origin)
{

    if(uv.y > radius + origin.y || uv.y < -radius + origin.y || uv.x > radius + origin.x || uv.x < -radius + origin.x)
        return false;
    else
        return true; 
}

bool InsideCircle(vec2 uv, float radius, vec2 origin)
{

    if(dot(uv,uv) < (radius)*(radius))
        return true;
    else
        return false; 
}

bool InsideCircle1(vec2 uv, float radius, vec2 origin)
{

    if(dot(uv,uv) < radius*radius)
        return true;
    else
        return false; 
}


