//Array of lights in our scene
const vec3 LIGHT_POS[] = vec3[](vec3(5.0));

//---------------------------------------------------------------------------------------
// Transformations
//---------------------------------------------------------------------------------------

mat4 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);

    return mat4(
        vec4(c, s, 0, 0),
        vec4(-s, c, 0, 0),
        vec4(0, 0, 1.0, 0),
        vec4(0, 0, 0, 1.0)
    );
}

mat4 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);

