//---------------------------------------------------------------------------------------
// Signed Distance Functions
//---------------------------------------------------------------------------------------

/* sphere centred at 0, with radius r */
float sphere(vec3 p, float r) {
    return length(p) - r;
}

/* cube centred at 0, parallel to xyz axis, with side length 2a */
float cube(vec3 p, float a) {
    return max(abs(p.x), max(abs(p.y), abs(p.z))) - a;
}

/* cylinder along x axis, with radius r */
float cylinder_x(vec3 p, float r) {
	return length(vec2(p.y, p.z)) - r;
}

/* cylinder along x axis, with radius r, x coordinate [0, l] */
float bounded_cylinder_x(vec3 p, float r, float l) {
	return max(cylinder_x(p, r), cube(vec3(p.x - l / 2.0, 0, 0), l / 2.0));
}

/* vertical cone, with peak at 0 pointing upwards, with radius r at depth 1 and with height h*/
float one_bounded_cone_y(vec3 p, float r, float h) {
	float dist = length(vec2(p.x, p.z));

    return max(
        (r * p.y + dist) / (r * r + 1.0),
        cube(vec3(0.0, p.y + h / 2.0, 0.0), h / 2.0)
    );
}

/*
 * R  - radius of outer sphere
 * r  - radius of inner sphere
 *
 * l  - lenght of entry (measured from the center, assumed to be greater than R
 * Re - outer radius of entry
 * re - inner radius of entry
 */
float igloo(vec3 p, float R, float r, float l, float Re, float re) {
    return
        max(
        	max(
                max(
                	min(
               	    	sphere(p, R),
                    	bounded_cylinder_x(p, Re, l)
                	),
					-bounded_cylinder_x(p, re, l + 0.1)
                ),
                -sphere(p, r)
            ),
            -cube(vec3(p.x, p.y + l, p.z), l + 0.1)
        );
}

vec3 cross_prod(vec3 a, vec3 b) {
	return vec3(a.y * b.z - a.z * b.y, a.z * b.x - a.x * b.z, a.x * b.y - a.y * b.x);
}

float scalar_prod(vec3 a, vec3 b) {
	return a.x * b.x + a.y * b.y + a.z * b.z;
}

/* returns halfspace determined by the plane abc, that contains d*/
float halfspace(vec3 p, vec3 a, vec3 b, vec3 c, vec3 d) {
	vec3 n = cross_prod(b - a, c - a);
    if (scalar_prod(n, d - a) > 0.0)
        n = -1.0 * n;
    return scalar_prod(n, p - a) / length(n);

}

/* r - inner radius
 * w - offset
 * h - height
 * t - twisting factor
 */
float corkscrew(vec3 p, float r, float w, float h, float t) {
    float theta = t * p.y;
//    float _x = (p.x) * cos(p.y) + p.z * sin(p.y);
//    float _y =-(p.x) * sin(p.y) + p.z * cos(p.y);
    float _x = p.x + sin(t * p.y);
    float _y = p.z + cos(t * p.y);
	return bounded_cylinder_x(vec3(p.y + h, _x, _y), r, 2.0 * h) / 1.2;
}

/* returns tetrahedron determined by its 4 vertices */
float tetrahedron(vec3 p, vec3 a, vec3 b, vec3 c, vec3 d) {
	return
        max(
        	max(
				halfspace(p, a, b, c, d),
				halfspace(p, b, c, d, a)
            ),
            max(
				halfspace(p, c, d, a, b),
				halfspace(p, d, a, b, c)
            )
        );
}

float scene1(vec3 p) {
    return one_bounded_cone_y(vec3(p.x, p.y - 1.0, p.z), 0.7, 3.0);
}
float scene2(vec3 p) {
	return igloo(vec3(p.x, p.y - 1.0, p.z),
    	2.0,  // R
        1.75, // r
        2.5,  // l
        1.0,  // Re
        0.75);// re
}
float scene3(vec3 p) {
	return tetrahedron(p,
    	vec3(-1.0, -1.0, -1.0),
    	vec3(-1.0, 1.0, 1.0),
    	vec3(1.0, 1.0, -1.0),
    	vec3(1.0, -1.0, 1.0));
}

float scene4(vec3 p) {
	return corkscrew(p,
                     1.1,  // r
                     1.0,  // w
                     3.0,  // h
                     3.0); // t
}

float scene(vec3 p) {
    // Change this to sceneN for N <- {1, 2, 3, 4}
    return scene4(p);
}

