    uv.y *= 2.;
    uv.y -= 0.5;

    col = mix(COLOR1 / 255.0, col, step(noise((uv.x + iTime * 0.1) * 10.) * 0.2,uv.y - 0.7));
    col = mix(COLOR2 / 255.0, col, step(noise((uv.x + iTime * 0.05) * 13.) * 0.2,uv.y - 0.5));
    col = mix(COLOR3 / 255.0, col, step(noise((uv.x + iTime * -0.05) * 15.) * 0.2,uv.y - 0.3));
    col = mix(vec3(0.0), col, step(noise((uv.x + iTime * -0.1) * 18.) * 0.2,uv.y - 0.1));

