void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec3 col = vec3(0.);
    vec2 p = vec2(0.);
    //uv = floor(uv*120.)/120.;
    //float steps = abs(sin(iTime*0.1)*16.)+4.;
    float steps = 32. + sin(iTime*0.1)*31.;
    //float steps = 64.;
    float s = iTime*01.25;
    for (float i;i<steps;i++) {
        float ii = i*02.102/steps;
        p = vec2(sin(s+ii),cos(s+ii))*(0.3+sin((s+ii)*8.)*0.1);
        ii = ii*ii*15.;
        //ii += 40.;
        //ii = ii*20.;
        //ii = (steps*3.)-ii;
        vec2 pv = floor(uv*ii)/ii;
    	if (length(pv+p) < 0.1*(i/steps)) {
            col = vec3(i/(steps-1.));
            col = col*col*col;
        	//col = vec3((i/steps)+(.025));
        }
    }
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 q=7.0*(fragCoord.xy-0.5*iResolution.xy)/max(iResolution.x,iResolution.y);

	for(float i=1.0;i<40.0;i*=1.1)
	{
		vec2 o=q;
		o.x+=(0.5/i)*cos(i*q.y+iTime*0.297+0.03*i)+1.3;		
		o.y+=(0.5/i)*cos(i*q.x+iTime*0.414+0.03*(i+10.0))+1.9;
		q=o;
	}

	vec3 col=vec3(0.5*sin(3.0*q.x)+0.5,0.5*sin(3.0*q.y)+0.5,sin(1.3*q.x+1.7*q.y));
    float f=0.43*(col.x+col.y+col.z);

	fragColor=vec4(f+0.6,0.2+0.75*f,0.2,1.0);
}

