mat4 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);

    return mat4(
        vec4(c, s, 0, 0),
        vec4(-s, c, 0, 0),
        vec4(0, 0, 1.0, 0),
        vec4(0, 0, 0, 1.0)
    );
}

mat4 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);

    return mat4(
        vec4(c, 0, s, 0),
        vec4(0.0, 1.0, 0, 0),
        vec4(-s, 0, c, 0),
        vec4(0, 0, 0, 1.0)
    );
}

//---------------------------------------------------------------------------------------
// Signed Distance Functions
//---------------------------------------------------------------------------------------

float sphere(vec3 pt, float r) {
    return length(pt) - r;
}

float cone(vec3 pt, float r, float h){
    float dist = length(pt.xz);
    float r_dash = r - r * pt.y / h; 
	if (pt.y < 0.0) return 1.0;
    else if (pt.y > h) return length(pt - vec3(0,h,0));
    else return dist - r_dash;
}

float scene(vec3 pt) {
    return cone(pt - vec3(0,-2,0), 2.0, 4.0);
}

//---------------------------------------------------------------------------------------
// Auxiliary helper functions
//---------------------------------------------------------------------------------------

vec3 getNormal(vec3 pt) {
    return normalize(GRADIENT(pt, scene));
}

//---------------------------------------------------------------------------------------
// Illumination and Shading
//---------------------------------------------------------------------------------------

float shade(vec3 eye, vec3 pt, vec3 n) {
    float val = 0.0;

    //Ambient Lighting
    val += 0.2;

    //Cast ray towards our light sources and add it to the illumination depending
    //on the angle with the normal to the surface
    for (int i = 0; i < LIGHT_POS.length(); i++) {
        vec3 l = normalize(LIGHT_POS[i] - pt); 
        val += max(dot(n, l), 0.0);
    }

    return val;
}

vec3 illuminate(vec3 camPos, vec3 rayDir, vec3 pt) {
    vec3 c, n;
    //Using the SDF normal approximation
    n = getNormal(pt);

    //Everything is white
    c = vec3(1.0);
    return shade(camPos, pt, n) * c ;
}

//---------------------------------------------------------------------------------------
// Ray Marching Algorithm
//---------------------------------------------------------------------------------------

vec3 castRay(vec3 camPos, vec3 rayDir) {
    //Using the ray marching approach
    int step = 0;
    float t = 0.0;

    for (float d = 1000.0; step < RENDER_DEPTH && abs(d) > CLOSE_ENOUGH; t += abs(d)) {
        d = scene(camPos + t * rayDir)/2.0;
        step++;
    }

    if (step == RENDER_DEPTH) {
        return vec3(0.0, 0.0, 0.0); //getBackground(rayDir);
    } else {
        return illuminate(camPos, rayDir, camPos + t * rayDir);
    }
}

