    float y = uv.y * 4.0  + iMouse.y/60.0;

    float x2 = uv.x * 2.0 + iMouse.x/120.0  + iTime*0.05 + 0.5;
    float y2 = uv.y * 2.0  + iMouse.y/120.0 + 0.5;


    float noise = 0.6+1.5*PerlinNoise2D(x,y);
    float secNoise = 0.6+2.0*PerlinNoise2D(x2,y2);

    noise = smoothstep(0.2,0.7, noise);
    secNoise = smoothstep(0.2,0.7, secNoise);

    vec3 col = mix(vec3(1,1,1), vec3(0.2,0.54,0.95), noise*secNoise);

    fragColor = vec4(col,1.0);
}

