//---------------------------------------------------------------------------------------
// Signed Distance Functions
//---------------------------------------------------------------------------------------

float cone(vec3 p, vec3 o, float r, float h) {
    if(dot(o, p)<0.0){
    	float v_d = -dot(o,p);
        float h_d = max(length(p + v_d*o)-r, 0.0);
        return sqrt(h_d*h_d+v_d*v_d);
    } else {
    	vec3 H = o*h;
        vec3 R = normalize(p - dot(p,o)*o)*r;
        if(dot(H-R, p-R) < 0.0) return length(p-R);
        if(dot(R-H, p-H) < 0.0) return length(p-H);
        return length(cross(H-p, R-p))/length(R-H);
    }
}

float hemisphere(vec3 p, vec3 up, float r){
	if(dot(up, p)<0.0){
    	float v_d = -dot(up,p);
        float h_d = max(length(p + v_d*up)-r, 0.0);
        return sqrt(h_d*h_d+v_d*v_d);
    } else {
    	return length(p)-r;
    }
}

float semicylinder(vec3 p, vec3 axis, vec3 up, float radius, float height){
    if(dot(up, p)<0.0){
        float vertical = -dot(up, p);
        vec3 rem = p +vertical*up;
        float horizontal = max(max(-dot(rem, axis), dot(rem, axis)-height), 
                               abs(dot(cross(up,axis), rem))-radius);
        return sqrt(vertical*vertical+horizontal*horizontal);
	} else {
        if(dot(axis, p)<0.0){
        	return max(-dot(axis, p), length(p-dot(axis, p)*axis)-radius);
        } else if(dot(axis, p)>height){
            return max(dot(axis, p)-height, length(p-dot(axis, p)*axis)-radius);
        } else {
            return length(p-dot(axis, p)*axis)-radius;
        }
    }
}

float igloo(vec3 p, vec3 up, vec3 front, float R, float r, float door_R, float door_r){
    return max(min(hemisphere(p,up,R), semicylinder(p-2.0*front, front, up, door_R, 2.5*(R-r))), 
               -min(hemisphere(p,up,r), semicylinder(p-2.0*front, front, up, door_r, 2.5*(R-r))));
}


float tetrahedron(vec3 p, vec3 v[4]){
   	float min_signed_dist = 1e10;

    for(int i = 0; i<4; i++){
        vec3 perp = normalize(cross(v[(i+1)%4]-v[i], v[(i+2)%4]-v[i]));
        if(dot(perp, v[(i+3)%4]-v[i])<0.0) perp = -perp;

        float dist = dot(perp, p-v[i]);
        min_signed_dist = min(min_signed_dist, dist);
    }
    return min_signed_dist;
}


float cylinderY(vec3 p, vec3 c, float r){
   	p = p-c;
    return length(p.xz)-r;
}

float spiralY(vec3 p, vec3 c, float r){
    //p.x = p.x-2.0;
    return cylinderY(vec3(p.x*cos(p.y*2.0)-p.z*sin(p.y*2.0), 
                          p.y, p.x*sin(p.y*2.0)+p.z*cos(p.y*2.0)), 
                     vec3(1.0,0.0,0.0), r);
}




