vec2 rot2D(vec2 v, float a)
{
  float cosa=cos(a);float sina=sin(a);
  mat2 rot = mat2(cosa,-sina,sina,cosa);
  return v*rot;
}

float sat(float a)
{
  return clamp(a,0.0,1.0);
}
float lenny(vec2 v)
{
  return abs(v.x)+abs(v.y);
}
int imod(int a, int b)
{
  int res = a/b;
  return a-(res*b);
}

// SDF operations
float _union(float a, float b)
{
  return min(a, b);
}
float _sub(float a, float b)
{
  return max(-a,b);
}

// SDF functions
float sdf_rect(vec2 uv, vec2 sz)
{
  vec2 r = abs(uv)-sz;
  return max(r.x,r.y);
}

float sdf_cir(vec2 uv, float r, float thick)
{
  float li = length(uv)-r;
  float lo = li-thick;
  return _sub(li,lo);
}

float sdf_plaind(vec2 uv)
{
  float left = uv.x;
  return _sub(left, sdf_cir(uv*vec2(.6,1.), -1.,1.075));
}

float sdf_v(vec2 uv)
{
  float res;
  float widthUp = 0.085;
  float pxu = 0.15;
  res = sdf_rect(uv-vec2(-pxu,0.089), vec2(widthUp,0.018));
  res = _union(res, sdf_rect(uv-vec2(pxu-0.02,0.089), vec2(widthUp-0.02,0.018)));
  res = _union(res, sdf_rect(rot2D(uv-vec2(-0.04,0.0005),1.1), vec2(0.1,0.025)));
  res = _union(res, sdf_rect(rot2D(uv-vec2(0.04,0.0005),-1.1), vec2(0.1,0.025)));
  return res;
}

float sdf_d(vec2 uv)
{
  float res = _sub(sdf_plaind(vec2(0.9,1.)*uv+vec2(0.05,0.)),sdf_plaind(uv*vec2(0.9,0.7)));

  res = _union(res, sdf_rect(uv-vec2(-0.015,0.089), vec2(0.03,0.018)));
  res = _union(res, sdf_rect(uv-vec2(-0.015,-0.089), vec2(0.03,0.018)));
  res = _union(res, sdf_rect(uv-vec2(-0.025,-0.02), vec2(0.02,0.07)));
  return res;
}

vec2 myPixel(vec2 uv, vec2 sz)
{
  vec2 tmp = uv / sz;

  uv.x = float(int(tmp.x));
  uv.y = float(int(tmp.y));
  return uv*sz;
}

float rdrDVDtxt(vec2 uv)
{
  float blurMax = 200.;
  float blur = 0.02;
  float realBlur = mix(blurMax,0.,blur);

  float res=sdf_v(uv);
  res = _union(sdf_d(uv-vec2(-0.25,0.)),sdf_v(uv));
  res = _union(res, sdf_d(uv-vec2(0.17,0.)));

  if (res<0.)
    return 0.;
  return sat(1.-(res/blur));
}


vec3 rdrDvd(vec2 uv)
{
  float inC = float(length(uv*vec2(1.,1.5))<0.2);
  float lVid = length(uv*vec2(.9,4.5)*2.+vec2(0.,0.49));
  float cVid = (1.-sat((lVid-0.2)*5.));
  float DVDtxt = rdrDVDtxt(uv*2.-vec2(0.,0.02));
  return (vec3(1.)*inC-cVid*.5)-vec3(DVDtxt);
}
const float PI = 3.141592653927;
vec3 rdr(vec2 uv)
{
  vec2 pos;
  vec3 col[4];

  col[0] = vec3(0.897,0.31,0.21);
  col[1] = vec3(0.897,0.31,0.21).xzy;
  col[2] = vec3(0.897,0.31,0.21).yzx;
  col[3] = vec3(0.1,0.897,0.21);

  pos.x = asin(sin(iTime))*0.5;
  bool isUpX = pos.x > asin(sin(iTime+0.01))*0.5;
  pos.y = asin(sin(2.*iTime+PI))*0.25;
  bool isUpY = pos.y < asin(sin(2.*iTime+PI+0.01))*0.25;
  int colIdx = int(isUpX)+(isUpY ? 1 : 2);
  return rdrDvd(uv-pos)*col[imod(colIdx,4)];
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord.xy / iResolution.xx;
  vec2 center = vec2(0.5)*iResolution.xy/iResolution.xx;
  uv = uv - center;
  uv = uv *2.;
  vec3 col;
  uv = myPixel(uv-vec2(2.), vec2(0.01))+vec2(2.); // minus then plus offset to avoid mod fail around 0
  col = rdr(uv);
  fragColor = vec4(col, 1.0);
}

vec3 background(vec3 rd) {
	float sky = max(0.0, dot(rd, vec3(0.0, 1.0, 0.0)));//sky gradient
	vec3 sky_color = vec3(0.5,0.8,1.0);//sky color
	return pow(sky, 1.0)*sky_color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 color = vec3(.6);
    vec2 cord = fragCoord/iResolution.xy;
    vec3 center = vec3(0.5,0.5,5.0);
    float radius = 0.3;
    float aspect_ratio = iResolution.x/iResolution.y;
    cord.x *= aspect_ratio;
    center.x *= aspect_ratio;
    if (length(cord-vec2(center.x,center.y)) <= radius) {
      color = vec3(0.8,0.3,1);
      vec3 camera = vec3(0.5,0.5,0);
      vec3 point_light = vec3(10.0*sin(iTime*0.5),5.0,-1);
      float z = center.z - sqrt(pow(radius,2.0)-pow(cord.y-center.y,2.0));
      vec3 curr = vec3(cord,z);
      vec3 normal = normalize(curr-center);
      vec3 light = normalize(point_light-curr);
      vec3 view = normalize(camera-curr);
      vec3 h = normalize(light+view);
      vec3 ambient = .1*color;
      vec3 diffuse = vec3(max(dot(normal,light),0.0));
      vec3 specular = vec3(max(0.2*pow(dot(normal,h),200.0),0.0));
      color *= ambient+diffuse+specular;
    } else {
      color = background(vec3(cord,0));
    }
    fragColor = vec4(color,1.0);
}

