#define MAX_STEPS 100
#define MAX_DEPTH 50.
float sphereSDF(vec3 position){
    vec4 sphere1 = vec4(-.8, .5, 1, .5);
    vec4 sphere2 = vec4(.8, .5, 1, .5);
	return min(length(position - sphere1.xyz) - sphere1.w,length(position - sphere2.xyz) - sphere2.w);
}
float cubeSDF(vec3 position){
    float s = .2;
 	vec3 d = abs(position-vec3(0,sin(iTime),cos(iTime))) - vec3(s);
  	return min(max(d.x, max(d.y,d.z)), 0.0)
      + length(max(d,0.0));
}

vec3 getSphereNormal(vec3 p){
    vec2 eps = vec2(0.01,0);
    return normalize(vec3(
    	sphereSDF(p+eps.xyy) - sphereSDF(p-eps.xyy),
    	sphereSDF(p+eps.yxy) - sphereSDF(p-eps.yxy),
    	sphereSDF(p+eps.yyx) - sphereSDF(p-eps.yyx)
    ));
}

vec3 getCubeNormal(vec3 p){
    vec2 eps = vec2(0.01,0);
    return normalize(vec3(
    	cubeSDF(p+eps.xyy) - cubeSDF(p-eps.xyy),
    	cubeSDF(p+eps.yxy) - cubeSDF(p-eps.yxy),
    	cubeSDF(p+eps.yyx) - cubeSDF(p-eps.yyx)
    ));
}


vec4 rayCast(vec3 ro, vec3 rd){
	float depth = 0.;
    vec3 outColor = vec3(.52, .80, .92); //sky color - default
    for(int i = 0;i < MAX_STEPS;++i){
        vec3 position = ro + rd * depth;
        float dist1 = sphereSDF( position );
        float dist2 = cubeSDF( position );
        float minDist;
        if ( dist1 < dist2 ){
            outColor = vec3(1); // color of sphere - white
          	minDist = dist1;
        }else{
            outColor = vec3(0, 0, 1); // color of cube - blue
            minDist = dist2;
        }
        if(minDist < 0.01) break;
        if(depth > MAX_DEPTH){
            outColor = vec3(.52, .80, .92); //sky color - default
            break;
        }
        depth += minDist;
    }
    return vec4(outColor, depth);
}


