#define N 17 // 12 for 60fps

#define shash(p) ( 2.* fract(sin((p)*mat2(127.1,311.7, 269.5,183.3)) *43758.5453123) - 1. )

float h;
float line(vec2 p, vec2 a,vec2 b) { // segment with disc ends: seamless distance to segment

    p -= a, b -= a;
    h = clamp(dot(p, b) / dot(b, b), 0., 1.);         // proj coord on line
    p -= b * h;
    return dot(p,p);                                  // dist² to segment
}

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy, 
          U = u / R.y,
          S = R / R.y, P, _P, K;

    float t = (R.y<250.) ? iTime - 9. : iTime,
        l = 1e9, d,L;
    for ( int k=0; k < N*N; k++) {
        K = shash( vec2(k%N,k/N) - float(N-1)/2. );
        for ( float dt = 0.; dt <= 1.; dt++ )  {
            _P = P;
         // P = vec2(.5+ .1*K) + (t+dt*.1)*vec2(1);  // random start position
            P = vec2(.5) + (t+dt*.1) * normalize(K); // random start direction
            P = mod( P/S, 2.); P = min( P, 2.-P )*S; // wrap virtual P(t) in recursive border "mirrors"
        }
      //l = min(l, dot(P-=U,P));                     // min distance
        d = line(U,_P,P); if (d<l) l=d, L=h;         // min distance to trajectory
    }

    O = vec4( L* smoothstep( 4.*L/R.y,0., sqrt(l) )); // draw tranjectory segment
}

