float noise = 2.0;

//----------------------------------------------------------------------
precision lowp float;
float PHI = 1.61803398874989484820459 * 00000.1; // Golden Ratio   
float PI  = 3.14159265358979323846264 * 00000.1; // PI
float SQ2 = 1.41421356237309504880169 * 10000.0; // Square Root of Two


float gold_noise(in vec2 coordinate, in float seed){
    return fract(tan(distance(coordinate*(seed+PHI), vec2(PHI, PI)))*SQ2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float mouse;
    if (iMouse.x <= 0.0 || iMouse.x >= iResolution.x){
		mouse = iResolution.x / 2.0;
    } else {
        mouse = iMouse.x;
    }

    if (mouse > fragCoord.x - 4.0 && mouse < fragCoord.x + 4.0){
        fragColor = vec4(0.4, 0.0, 0.0, 1.0);
    } else {

        vec2 uv = fragCoord/iResolution.xy;

        vec3 color;
        if (fragCoord.x < iResolution.x * 0.05
            || fragCoord.x > iResolution.x * 0.95){
            color = vec3(0.0);

        } else if (fragCoord.y < iResolution.y * 0.15){
        	color = texture(iChannel1, uv).rgb;

        } else if (fragCoord.y < iResolution.y * 0.85){
            color = texture(iChannel0, uv).rgb;

        } else {
            color = 0.5 + 0.25*cos(iTime*0.5+uv.xyx+vec3(0,4,8));
            //color = vec3(0.0, 0.8, 0.2);
        }

        if ((mouse <= 0.0 && fragCoord.x < iResolution.x / 2.0) 
				|| mouse > fragCoord.x){

            float uniquePos = (fragCoord.x * uv.y) + fragCoord.x;
            float amount = noise * 0.08;

            // using a fictive coordinate as a workaround to fix a pattern bug
			vec2 coordinate = vec2(fragCoord.x, fragCoord.y * 1.001253543);

            // a very low unique seed will lead to slow noise
            // pattern changes on slow moving color gradients
            float uniqueSeed = uniquePos * 0.000000001;

            // average noise luminance to subtract
            float sub = (0.5 * amount);

            // calculating and adding/subtracting the golden noise
            float ran = gold_noise(coordinate, uniqueSeed);
            float add = clamp(ran * amount, 0.0, 1.0);
            color += (add - sub);
        }

        fragColor = vec4(color, 1.0);
    }
}

#define PI 3.1415926535897932384626433832795
#define RENDER_DEPTH 800
#define CLOSE_ENOUGH 0.00001

#define GRADIENT(pt, func) vec3( \
    func(vec3(pt.x + 0.0001, pt.y, pt.z)) - func(vec3(pt.x - 0.0001, pt.y, pt.z)), \
    func(vec3(pt.x, pt.y + 0.0001, pt.z)) - func(vec3(pt.x, pt.y - 0.0001, pt.z)), \
    func(vec3(pt.x, pt.y, pt.z + 0.0001)) - func(vec3(pt.x, pt.y, pt.z - 0.0001)))

//Array of lights in our scene
const vec3 LIGHT_POS[] = vec3[](vec3(5.0));

//---------------------------------------------------------------------------------------
// Transformations
//---------------------------------------------------------------------------------------

mat4 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);

    return mat4(
        vec4(c, s, 0, 0),
        vec4(-s, c, 0, 0),
        vec4(0, 0, 1.0, 0),
        vec4(0, 0, 0, 1.0)
    );
}

mat4 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);

    return mat4(
        vec4(c, 0, s, 0),
        vec4(0.0, 1.0, 0, 0),
        vec4(-s, 0, c, 0),
        vec4(0, 0, 0, 1.0)
    );
}

//---------------------------------------------------------------------------------------
// Signed Distance Functions
//---------------------------------------------------------------------------------------

float sphere(vec3 p, float r) {
    return length(p) - r;
}

float scene(vec3 p) {
    return sphere(p, 2.0);
}

//---------------------------------------------------------------------------------------
// Auxiliary helper functions
//---------------------------------------------------------------------------------------

vec3 getNormal(vec3 pt) {
    return normalize(GRADIENT(pt, scene));
}

//---------------------------------------------------------------------------------------
// Illumination and Shading
//---------------------------------------------------------------------------------------

float shade(vec3 eye, vec3 pt, vec3 n) {
    float val = 0.0;

    //Ambient Lighting
    val += 0.2;

    //Cast ray towards our light sources and add it to the illumination depending
    //on the angle with the normal to the surface
    for (int i = 0; i < LIGHT_POS.length(); i++) {
        vec3 l = normalize(LIGHT_POS[i] - pt); 
        val += max(dot(n, l), 0.0);
    }

    return val;
}

vec3 illuminate(vec3 camPos, vec3 rayDir, vec3 pt) {
    vec3 c, n;
    //Using the SDF normal approximation
    n = getNormal(pt);

    //Everything is white
    c = vec3(1.0);
    return shade(camPos, pt, n) * c ;
}

//---------------------------------------------------------------------------------------
// Ray Marching Algorithm
//---------------------------------------------------------------------------------------

vec3 castRay(vec3 camPos, vec3 rayDir) {
    //Using the ray marching approach
    int step = 0;
    float t = 0.0;

    for (float d = 1000.0; step < RENDER_DEPTH && abs(d) > CLOSE_ENOUGH; t += abs(d)) {
        d = scene(camPos + t * rayDir)/2.0;
        step++;
    }

    if (step == RENDER_DEPTH) {
        return vec3(0.0, 0.0, 0.0); //getBackground(rayDir);
    } else {
        return illuminate(camPos, rayDir, camPos + t * rayDir);
    }
}

//---------------------------------------------------------------------------------------
// Set-up
//---------------------------------------------------------------------------------------

mat3 setCamera(in vec3 origin, in vec3 target, float rotation) {
    //The direction in which the camera is pointing
    vec3 forward = normalize(target - origin);
    vec3 orientation = vec3(sin(rotation), cos(rotation), 0.0);
    vec3 left = normalize(cross(forward, orientation));
    vec3 up = normalize(cross(left, forward));
    return mat3(left, up, forward);
}

