	//float c = sin(l + cos(a * 6.0) * 0.5 + a) * 0.5 + 0.5;
	float c = fract(a*l)*(sin(iTime*2.)*.2+.8);

    fragColor = vec4(smoothstep(.4999,.5,c));
}

#define MAX_DIST 50.0
float g;

mat2 rot(float t)
{
    float s = sin(t);
    float c = cos(t);
    return mat2(c, -s, s, c);
}

//thanks to iq for sharing this knowledge!
float cyl(vec3 p, float ra, float rb, float h)
{
    vec2 d=vec2(length(p.xz)-2.0*ra+rb,abs(p.y)-h);
    return min(max(d.x,d.y),0.0)+length(max(d,0.0))-rb;
}

float smin(float a, float b, float k)
{
    float h = max(k-abs(a-b),0.0)/k;
    return min(a,b)-h*h*k*(1.0/4.0);
}

float h21(vec2 p)
{
    return abs(fract(sin(dot(p,vec2(12.84,13.1523)))*21.129));
}

float snow(vec3 p)
{
    vec3 cell = vec3(4.);
    vec3 mp = p+vec3(cos(iTime/2.-p.z/cell.z)*1.5,iTime,iTime/4.+cos(iTime/3.-p.x/cell.x)*1.5);
    float id = floor(h21(floor(mp.xy/cell.xy))*100.);
    float t = (100.+iTime)*2.*h21(floor(mp.xz/cell.xz));

    float sn= length(mod(mp+vec3(0.,t,0),cell)-cell/2.)-.001;

    if(p.z<2.5) //clip snowflakes that are too close
    g+=0.001/(0.01+sn*sn);
	return sn;
}

vec2 map(vec3 p)
{
    float m = 0.;
    float r =p.y+4.+(p.x*p.x+p.z*p.z)/200.;
    float sn = snow(p);
    vec3 smp = p+vec3(0,2.5,9);
    vec3 smhp = smp+vec3(0,-4.4,0);
    float sm = smin(smin(length(smp)-2.,length(smp+vec3(0,-2.5,0.))-1.4,.4),length(smhp)-.9,.4);
    float smh = min(cyl(smhp+vec3(0,-.6,0),.65,.1,.05),cyl(smhp+vec3(0,-1.4,0),.4,.1,.5));
    float sme = min(length(smhp-vec3(0.4,.24,.9))-.1,length(smhp-vec3(-0.4,.24,.9))-.1);
    vec3 rp = smhp-vec3(0.2,0.0,0.2);
    float hp = 3.1415927/2.;
    rp.xy*=rot(hp);
    rp.yz*=rot(hp*.8);
    float n = cyl(rp,.1,4.,.1);
    smh=min(smh,sme);
    r=smin(r,sm,.9);
    r=min(r,sn);
    r=min(r,smh);
    r=min(r,n);
    if(r==smh)m=1.;
    if(r==n)m=2.;
    return vec2(r,m);
}

vec2 ray(vec3 ro, vec3 rd)
{
    float m = 0.0;
    float t = 0.0;
    for(int i=0;i<128;i++)
    {
        vec3 p=ro+rd*t;
        vec2 s = map(p);
        m=s.y;
        if(s.x<0.00001)break;
        t+=s.x*.8;
        if(t>MAX_DIST)
        {
            t=-1.;
            break;
        }
    }
    return vec2(t,m);
}

vec3 normal(vec3 p )
{
    vec2 e = vec2(0.0005,0.);
    return normalize(vec3(
        map(p+e.xyy).x-map(p-e.xyy).x,
        map(p+e.yxy).x-map(p-e.yxy).x,
        map(p+e.yyx).x-map(p-e.yyx).x
        ));
}

void mainImage( out vec4 c, in vec2 f )
{
    vec2 p = (2.*f-iResolution.xy)/iResolution.y;

    vec3 ro = vec3(0.,.6,2.5);
    vec3 ta = vec3(0,0.5,2.2);
    vec3 cf = normalize(ta-ro);
    vec3 cu = normalize(cross(cf,vec3(0,1,0)));
    vec3 cr = normalize(cross(cu,cf));

    vec3 rd = vec3(p.x*cu+p.y*cr+.4*cf);
    vec2 r = ray(ro,rd);

    vec3 pos = ro+rd*r.x;
    vec3 n = normal(pos);
    vec3 sun = normalize(vec3(0.2,0.5,0.6));
    float dif = clamp(dot(sun,n),0.,1.);
    float sky = clamp(dot(n,vec3(0,1,0)),0.,1.);
    float bou = clamp(dot(n,vec3(0,-1,0)),0.,1.);
    float sha = step(ray(pos+n*0.001,sun).x,0.);

    vec3 col = vec3(0.5,0.5,1.)-(p.y+.3)/5.;
    vec3 mate = vec3(1.);
    if(r.y>.5&&r.y<1.5)mate=vec3(0.1);
    else if(r.y>1.5){mate=vec3(0.89,0.3,0.);}
    if(r.x>0.)
    {
        col=mate*vec3(1.,1.,1.8)*dif+g/2.*(abs(p.y+1.))*sha;
        col+=mate*vec3(0.2,0.25,0.25)*sky;
        col+=mate*vec3(0.1,0.1,0.1)*bou;
    }
    else col+=(g/2.)*(abs(p.y+1.));
    float cp = length(p)-.9;
    if(cp>0.)col=vec3(smoothstep(0.,3./iResolution.y,length(p)-.9)+.6);
    else{
        col+=smoothstep(1.,0.,length(p-.75)+.1)/1.1;
		col-=smoothstep(-.2,4.,length(p)-.95)*100.;
    }
    c.rgb = col;
}

