#define PI 3.141592

mat2 rot(float a)
{return mat2 (cos(a),sin(a),-sin(a),cos(a));}

float cyl (vec3 p, float r, float h)
{return max(length(p.xy)-r,abs(p.z)-h);}

float ring (vec3 p, float r, float w)
{return max(abs(cyl(p, r,1.))-w,abs(p.z)-0.08);}

float SDF (vec3 p)
{
    float r = 5.;
    float w = 0.01;
    float d = 1e10;
	for (int i = 0; i<=6; i++)
    {
        float ratio = float(i)/5.;
        r-=ratio;
        w += ratio*0.1;
        p.xz *= rot(iTime*0.5);
        p.yz *= rot(iTime*0.2);
        d = min(d, ring (p,r,w));
    }  
    return d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.*(fragCoord/iResolution.xy)-1.;
	uv.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3(0.001,0.001,-6.),
        rd = normalize(vec3(uv,1.)),
        col = vec3(0.),
        p = ro;

    float shad = 0.;
    bool hit = false;

    for (float i = 0.; i<64.; i++)
    {
        float d = SDF(p);
        if (d<0.001)
        {
            shad = i/64.;
            hit = true;
            break;
        }

        p+=d*rd;
    }

    if (hit)
    {
        col = vec3(1.-shad);
    }

    fragColor = vec4(col,1.0);
}

#define PI 3.14159265359
const float iter = 6.0;

vec2 tile(vec2 uv) {
	uv *= 3.;
    uv = fract(uv);  
    return uv;
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

vec3 hsv2rgb( vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	return mix( vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float aspect = iResolution.x/iResolution.y;
    vec2 tiling = vec2(aspect, 1.);
    uv.x *= aspect;
    uv.x -= (aspect - 1.) / 2.;


    vec3 color = vec3(0.0);
    vec3 colorr = vec3(0.0);
    vec3 green = vec3(.3, .5, .1);

    if(uv.x > 1. || uv.x < .0) {     
        color = vec3(1.0);
        colorr = green;
    }
    else {
        uv = rotate2d(PI / 4.) * uv;
        vec3 hsl = vec3( uv.x / 2. + iTime * .5, 1.0, uv.y );
		colorr = hsv2rgb(hsl);
        uv = rotate2d(-PI / 4.) * uv;


        for(float i = 0.; i < iter; ++i) {

            vec3 color1 = vec3(step(1./3., uv.x) - step(2./3., uv.x));
            vec3 color2 = vec3(step(1./3., uv.y) - step(2./3., uv.y));

            color = color1 * color2;

            if(color == vec3(1.))
                break;

            float pas = floor((fract(iTime*.2) * iter));
            if(i == pas)
                break;

            uv = tile(uv);
        }
    }

    color *= colorr;
    fragColor = vec4(color, 1.0);

}

