#define PI 3.141592
#define nt iTime*4.


// taken from YX here : https://www.shadertoy.com/view/tdlXW4
// rough shadertoy approximation of the bonzomatic noise texture
vec4 texNoise(vec2 uv)
{
    float f = 0.;
    f += texture(iChannel0, uv*.125).r*.5;
    f += texture(iChannel0, uv*.25).r*.25;
    f += texture(iChannel0, uv*.5).r*.125;
    f += texture(iChannel0, uv*1.).r*.125;
    f=pow(f,1.2);
    return vec4(f*.45+.05);
}

mat2 rot (float a)
{return mat2 (cos(a),sin(a),-sin(a),cos(a));}

// polynomial smooth min from iq
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float sphe (vec3 p, float r)
{return length(p)-r;}

float cyl (vec3 p, float r, float h)
{return max(length(p.xy)-r, abs(p.z)-h);}


float sp (vec3 p)
{   
    p.z -= nt;
    p.xz *= rot(-PI/2.);

    p.y -= sin(p.x*2.+iTime*2.)*p.x*0.2;
    float s =  sphe(p,0.5);
    p.x -= 1.8;
    float c = cyl(p.yzx,-p.x*0.2,1.5);
    return smin(s,c,0.2);
}

float sps (vec3 p)
{
    p.xy *= rot(iTime*0.5);
    float s1 = sp(p+vec3(3.,2.,0.));
    float s2 = sp(p+vec3(0.,-2.,0.));
   	float s3 = sp(p + vec3(-3.,0.,0.));
    return min(s1,min(s2,s3));
}

float g2 = 0.;
float ov (vec3 p)
{
    p.z -= 3.+nt;

    p.x += sin(p.y*5.+.5)*0.2;
    p.y -= sin(p.z*5.+0.1)*0.1;
    p.z += cos(p.x*4.)*0.3;
    float d = sphe(p, 0.8+exp(-fract(iTime)));
    g2 += 0.1/(0.1+d*d);
    return d;
}

float g1 = 0.;
float tunnel(vec3 p)
{
    p.x += texNoise(p.yz*0.05).r;
    p.y += texNoise(p.xz*0.02).r;
    float d =  cyl(p, 5.+sin(nt)*0.15, 1e10);
    g1 += 0.01/(0.01+d*d);
    return d;
}

vec2 path(float t) 
{
	float a = sin(t*.2 + 1.5), b = sin(t*.2);
	return vec2(a, a*b);
}

float SDF (vec3 p)
{
    p.xy -= path(p.z);

    return min(-tunnel(p),min(sps(p), ov(p)));
}

vec3 getcam (vec3 ro, vec3 tar, vec2 uv)
{
    vec3 f = normalize(tar-ro);
    vec3 l = normalize(cross(vec3(0.,1.,0.), f));
    vec3 u = normalize(cross(f,l));
    return normalize(f + l*uv.x + u*uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.*(fragCoord/iResolution.xy)-1.;
	uv.x *= iResolution.x / iResolution.y;

    vec3 ro = vec3(0.001,0.001,-5.+nt),
        tar = vec3(0.,0.,nt),
        p = ro,
        col = vec3(0.);

    ro.xy += path(ro.z);
	tar.xy += path(tar.z);

    vec3 rd = getcam(ro, tar, uv);

    float shad = 0.;
    bool hit = false;

    for (float i =0.; i<64.;i++)
    {
        float d = SDF(p);
        if (d<0.001)
        {
            shad = i/64.;
            hit = true;
            break;
        }
        p+=d*rd*0.7;
    }
    if (hit) 
    {
        col = vec3(1.-shad);
    }
    col += g1 * vec3(1.,0.7,0.7)*0.02;
    col += g2 * vec3(0.8,0.1,length(uv))*0.05;

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define fr float(iFrame)

const float maxDist = 200.;
const float planeDist = 2.;

float rand31(vec3 co)
{
	return fract(sin(dot(co,vec3(65.9898,
	 	78.233, 29.3471))) * 1537.5497);
}

float n(vec3 uv)
{
	vec3 i = floor(uv);
	vec3 luv = fract(uv);
	luv.xyz = smoothstep(0., 1., luv.xyz);
	vec3 d = vec3(1,-1,0);
	vec3 p[8];
	float r[8];

	p[0] = i;
	p[1] = i + d.zzx;
	p[2] = i + d.zxz;
	p[3] = i + d.zxx;
	p[4] = i + d.xzz;
	p[5] = i + d.xzx;
	p[6] = i + d.xxz;
	p[7] = i + d.xxx;

	for(int k = 0; k < 8; k++)
		r[k] = rand31(p[k]);

	float rx[4];

	for(int k = 0; k < 4; k++)
	    rx[k] = mix(r[k], r[k+4], luv.x);

	float rxy[2];

	for(int k = 0; k < 2; k++)
	    rxy[k] = mix(rx[k], rx[k+2], luv.y);

	float rxyz = mix(rxy[0], rxy[1], luv.z);

	return rxyz;
}

float noise(vec3 uv)
{
	float r = 0.;
	float p = 1.;

	for(int i = 0; i < 2; i++)
	{
	    float cr = n(uv * p) / p;
        r += cr;
	    p *= 2.;
	}

	r -= .5;

	return r;
}

float sin01(float x)
{
	return sin(x) * 0.5 + 0.5;
}

float cos01(float x)
{
	return cos(x) * 0.5 + 0.5;
}

vec3 lightPos()
{
	return vec3(1,1,1);
}

float smin(float a, float b, float k)
{
	float h = clamp((a - b)/k + 0.5, 0., 1.);
	float m = h * (1. - h) * 0.5 * k;
	float r = mix(a, b, h) - m;
	return r;
}

vec3 cameraPos()
{
	return vec3(6,8,-6);
	float t = (fr/30.) * 0.05;

	vec3 res = vec3(
		sin(t) * 9.,
		9.,
		-cos(t) * 9.);

	return res;
}

vec3 getRay(vec2 uv)
{
	vec3 cam = cameraPos();
	vec3 origin = vec3(0, 0, 0);
	vec3 look = normalize(origin - cam);
	vec3 upGlob = vec3(0,1,0);
	vec3 right = normalize(cross(upGlob, look));
	vec3 camUp = normalize(-cross(right, look));

	vec3 p = cam + camUp * uv.y + right * uv.x;
	p += look * planeDist;

	vec3 res = p - cam;
	return normalize(res);
}

float getSphere(vec3 p, float size, vec3 origin)
{
	return length(p - origin) - size;
}

float getSphere(vec3 p, float size)
{
	return getSphere(p, size, vec3(0,0,0));
}

float getCube(vec3 p, float size)
{
	p = abs(p);
	return max(max(p.x, p.y), p.z) - size;
}

float getInfCylinder(vec3 p, float size)
{
	float l = length(vec3(p.x, p.y, 0)) - size;
	return l;
}

float getPlane(vec3 p)
{
	return p.y;
}

float inter(float a, float b)
{
	return max(a, b);
}

float un(float a, float b)
{
	return min(a, b);
}

float diff(float a, float b)
{
	return max(a, -b);
}

float getCapsule(vec3 p, vec3 a, vec3 b, float r)
{
	vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h ) - r;
}

vec2 skew(vec2 p, float a)
{
	float ca = cos(a);
	float sa = sin(a);

	mat2 m = mat2(ca, sa, -sa, ca);

	p = p * m;

	return p;
}

float getSd(vec3 p)
{
	vec3 po = p;

    float time = fr / 30.;
    float n = noise(p - vec3(0,time*.5,0));

	float res = n;

	res = inter(res, getCube(po, 1.7));

    if (iMouse.z > 0.)
    {
        float x = smoothstep(0., 1., iMouse.y / iResolution.y)*2.-1.;
        x *= 0.5;
        res -= x;
    }

    return res;
}

vec3 calcNormal(vec3 p )
{
	const float h = 0.03; // or some other value const
	vec2 k = vec2(1,-1);

	return
	normalize(
		k.xyy*getSd( p + k.xyy*h ) +
		 k.yyx*getSd( p + k.yyx*h ) +
		 	k.yxy*getSd( p + k.yxy*h ) +
		 		k.xxx*getSd( p + k.xxx*h ) );
}

float raymarch(vec3 sp, vec3 ray)
{
	float depth = 0.;

	for(int i = 0; i < 150; i++)
	{
		vec3 p = sp + ray * depth;
		float dist = getSd(p);

		if (dist <= 0.001)
		  return depth;

		depth += dist;

		if (depth >= maxDist)
		  return maxDist;
	}

	return maxDist;
}

float getShadow(vec3 p)
{
	vec3 lp = lightPos();
	float ld = length(lp - p);
	vec3 ray = normalize(lp - p);
	float d = raymarch(p + ray*0.2, ray);
	float res = smoothstep(0.1,1., d);
	res = res * 0.3 + 0.7;
	return res;
}

float getSpec(vec3 refRay, vec3 l)
{
	float x = dot(refRay, l);

	x = clamp(x,0.,1.);
	x = pow(x, 7.);

	return x;
}

float getHatch(vec3 l, vec3 p)
{
	float time = fr / 30.;
	p -= vec3(0,time*.5,0);
	vec3 right = normalize(cross(l, vec3(0,1,0)));
	vec3 up = (cross(l, right));

	float u = dot(p, right);
	float v = dot(p, up);
	vec2 uv = vec2(u,v)*0.3;

	float h = texture(iChannel0, uv).r;

	return 1.-h;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = gl_FragCoord.xy / iResolution.xy;
	uv -= 0.5;
	uv.y /= iResolution.x / iResolution.y;
    uv *= 2.;

    vec3 ray = getRay(uv);
    vec3 camPos = cameraPos();
	float d = raymarch(camPos, ray);
	float inf = step(1., maxDist - d);

	vec3 col = vec3(1.);

	if (d > 30.)
	{
	    fragColor = vec4(0);
		return;
	}

	vec3 intP = cameraPos() + d*ray;
	vec3 n = calcNormal(intP);
	vec3 lightDir = normalize(vec3(1));

    float dd = dot(n, lightDir);
    dd = dd *.5 + .5;

    vec3 cuv = (intP - vec3(-1.7))/3.4;

	vec3 res = col * dd * inf * cuv;

	vec3 refRay = reflect(ray, n);

	float spec = getSpec(refRay, lightDir);
	float h = getHatch(lightDir, intP);

	spec *= h;
	res += spec;

    fragColor = vec4(res, 1.0);
}

#define MAX_DIST 100.
#define PREC .01
#define PREC1 .03
#define SHADOW_QUAL 100.

#define LIGHT_POS vec3(2., 2.8, 2.)
#define LIGHT_DIR vec3(-sin(iTime * .81) * .25, -1., +sin(iTime * 4.12) * .125)


float plane(vec3 p, vec4 n) {
    return dot(p, normalize(n.xyz)) - n.w;
}
float planeX(vec3 p, float d) {
    return plane(p, vec4(1., 0., 0., d));
}
float planeY(vec3 p, float d) {
    return plane(p, vec4(0., 1., 0., d));
}
float planeZ(vec3 p, float d) {
    return plane(p, vec4(0., 0., 1., d));
}
float sphere(vec3 p, float r) {
    return length(p) - r;
}

vec4 map(vec3 p) {
    float d = MAX_DIST;
    float m = .0;

    float px1 = planeX(p, -3.);
    float px2 = -planeX(p, 3.);
    float pz1 = -planeZ(p, 3.);
    float pz2 = planeZ(p, -3.);
    float py1 = planeY(p, 0.);
    float py2 = -planeY(p, 4.);
    if (d > px1) {
        d = px1;
        m = 1.;
    }
    if (d > px2) {
        d = px2;
        m = 5.;
    }
    if (d > pz1) {
        d = pz1;
        m = 2.;
    }
    if (d > pz2) {
        d = pz2;
        m = 3.;
    }
    if (d > py1) {
        d = py1;
        m = 0.;
    }
    if (d > py2) {
        d = py2;
        m = 4.;
    }
    float pl = plane(p, vec4(-1., -1., -1., -4.8));
    if (d > pl) {
        d = pl;
        m = 6.;
    }

    float s = sphere(p - vec3(0., 1.25 - pow(fract(iTime * 1.) - .5, 2.) * 3., 0.), .5);
    if (s < d) {
        m = 7.;
        d = s;
    }
	return vec4(d, m, 0., 0.);
}
vec3 normal(vec3 p) {
    vec2 t = vec2(PREC, -PREC) * .001;
    return normalize(
    	t.xyy * map(p + t.xyy).x +
    	t.yxy * map(p + t.yxy).x +
    	t.yyx * map(p + t.yyx).x +
    	t.xxx * map(p + t.xxx).x);
}
vec4 march(vec3 ro, vec3 rd, float maxD) {
    vec4 res;
    float d = 0.;
    float newD = PREC;
    while (newD >= PREC && d < maxD) {
        res = map(ro + rd * d);
        newD = res.x;
        d = min(maxD, d + newD);
    }
 	return vec4(d, res.yzw);
}
float shadow(vec3 ro, vec3 rd, float maxD) {
    float soft = 1.;
    float d = PREC;
    float newD = PREC;
    while (newD >= PREC && d < maxD) {
        newD = map(ro + rd * d).x;
        soft = min(soft, 4. * max(0., newD - PREC) / d);
        d += min(newD, maxD / SHADOW_QUAL);
    }
 	return soft;
}
vec3 color(float c) {
    if (c < 1.) {
    	return vec3(1., 1., 0.) * .8;
    } else if (c < 2.) {
    	return vec3(1., 0., 1.) * .8;
    } else if (c < 3.) {
    	return vec3(.0, 1., .0) * .8;
    } else if (c < 4.) {
    	return vec3(0., 1., 1.) * .8;
    } else if (c < 5.) {
    	return vec3(1., 0., 0.) * .8;
    } else if (c < 6.) {
    	return vec3(0., .0, 1.) * .8;
    } else if (c < 7.) {
    	return vec3(2., 2., 2.);
    } else if (c < 8.) {
    	return vec3(1., 1., 1.) * .1;
    }
}
float reflection(float c) {
    if (c < 6.) {
    	return 0.;
    } else if (c < 8.) {
    	return 1.;
    }
}

vec3 render(vec3 ro, vec3 rd) {
    vec3 col = vec3(0);

    vec4 m = march(ro, rd, MAX_DIST); 
    float hd = m.x;
    vec3 h = ro + rd * (hd - PREC1);
    vec3 n = normal(h);
    vec3 lh = h - LIGHT_POS;
    vec3 diff = dot(n, -normalize(lh)) * color(m.y);
    if (color(m.y).x <= 1.) {
        float shad = clamp(0., 1., shadow(h, normalize(-lh), length(lh)) + .25);
        diff *= shad;
    }

    if (reflection(m.y) > .1 && color(m.y).x <= 1.) {
        vec3 r = rd - 2. * dot(normalize(rd), n) * n;
        vec4 mr = march(h, r, MAX_DIST);
        float hdr = mr.x;
        vec3 hr = h + r * (hdr - PREC1);
        vec3 nr = normal(hr);
        vec3 lhr = hr - LIGHT_POS;
        diff = color(mr.y) * dot(nr, -normalize(lhr)) * (1.8 + dot(rd, n));
	    float shadr = clamp(0., 1., shadow(hr, normalize(-lhr), length(lhr)) + .25);
        diff *= shadr;
    }

    col += diff;


    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) { 
    vec2 uv = fragCoord/iResolution.xy - .5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 ro = vec3(0., 1.25, -2.9);
    float a = iTime * .5;
    ro.xz = mat2(cos(a), sin(a), -sin(a), cos(a)) * ro.xz;
    float cp = 1.;
    vec3 rd = normalize(vec3(uv.x, uv.y, cp));
    rd.xz = mat2(cos(a), sin(a), -sin(a), cos(a)) * rd.xz;

    vec3 col = render(ro, rd);
    col = pow(col, vec3(0.4545));
    fragColor = vec4( col, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float tme = iTime*.5;
    float scale = 0.125;

    vec2 adjVec = vec2((sin(tme)*.5), (cos(tme)*.5));
    vec2 uv = fragCoord/iResolution.xy;    
    vec2 uvM;
    float posMod;

    uvM = mod(uv + normalize(uv-0.5)*adjVec, scale);
    uv = normalize(uvM-vec2(scale/2.))*(adjVec+vec2(.5));

    if(mod(floor((uv.x)/scale),2.) == 0.)
            uvM.x = scale - uvM.x;
    if(mod(floor((uv.y)/scale),2.) == 0.)
            uvM.y = scale - uvM.y;

    uvM += adjVec*length(uvM + adjVec - vec2(0.5));

    // Time varying pixel color
    vec3 col = vec3(0.5 + 0.5*tan((cos(tme)+2.)*8.*(uvM.x/sin(uvM.y+0.2+(cos(tme)/2.+1.)))),
                    0.5 + 0.5*tan((sin(tme*1.2)+2.)*8.*(uvM.x/sin(uvM.y+0.2+(cos(tme)/2.+1.)))),
                    0.5 + 0.5*tan((cos(tme*1.3)+3.)*8.*(uvM.x/sin(uvM.y+0.2+(sin(tme)/2.+1.)))));

    // Output to screen
    fragColor = vec4(col,1.0);
}

float noise2(vec2 uv)
{
    return fract(sin(uv.x * 8989.) * 52423. + cos(uv.y * 4917.) * 68159.);
}

float mh(vec2 v)
{
    return abs(v.x) + abs(v.y);
}

float PI = 3.142;

struct snakeUvs {
    vec2 stuv, cuv, suv, tuv, flipper;
    bool flipped;
};

snakeUvs getSnakeUvs(vec2 wuv, float offset)
{
    // truchet

    vec2 otuv = fract(wuv);
    vec2 tuv = otuv;
    vec2 c = floor(wuv);

    float n = noise2(c);

    bool flip = n < 0.5;

    if (flip) 
    {
        tuv.x = 1. - tuv.x;
        otuv.x = -otuv.x;
    }

    if (mh(tuv) > 1.) 
    {
       tuv -= 1.;
       otuv -= 1.;
    }

    if (flip)
    {
        otuv = vec2(-1. - otuv.x, otuv.y);
    }

    float d = length(tuv);

    vec2 suv;
    suv.y = 2.*atan(tuv.y/tuv.x)/PI - .5;
    suv.x = -(d * 3. - 1.5);


    vec2 flipper = vec2(1.,1.);
    if (mod(c.x+c.y,2.) == 0.) 
    {
        flipper.y = - 1.;
    }
    if ((mod(mh(c),2.) == 0.) ^^ flip )
    {
        flipper.x = - 1.;
    }

    suv *= flipper;


    vec2 stuv = vec2(suv.x, fract(suv.y * 2. + offset)-.5);

    snakeUvs uvs;
    uvs.stuv = stuv;
    uvs.cuv = otuv;
    uvs.suv = suv;
    uvs.tuv = tuv;
    uvs.flipper = flipper;
    uvs.flipped = flip;

    return uvs;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col;

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec2 wuv1 = uv * (6. + 2. * sin(iTime / 5.)) + vec2(0.6,0.5) * iTime;
    vec2 wuv2 = wuv1 + vec2(- 76.5,39.5);

    vec2 ctuv = fract((wuv1 + .25) * 2.);
    vec2 cti = floor((wuv1 + .25) * 2.);

    bool over = mod(mh(cti), 4.) > 2.;


    snakeUvs uvs1 = getSnakeUvs(wuv1, iTime * 3.);
    snakeUvs uvs2 = getSnakeUvs(wuv2, iTime * 1.);

    vec2 stuv1 = uvs1.stuv;
    vec2 stuv2 = uvs2.stuv;

    vec2 stxy1 = uvs1.cuv;
    vec2 stxy2 = uvs2.cuv;

    stuv1.x *= -1.5-uv.y;
    stuv2.x *= 1.5-uv.y;
    stuv1.x *= 2.+sin(wuv1.x / (4.5 + sin(wuv1.y / 30.)));
    stuv2.x *= 2.+sin(wuv2.x / (3.8 + sin(wuv2.y / 70.)));

    //col.rg = stuv1 + stuv2;
    float mask1 = smoothstep(.5,0.45,abs(stuv1.x));
    float mask2 = smoothstep(.5,0.45,abs(stuv2.x));

    if (over)
    {
      mask2 *= 1. - mask1;
    }
    else {
      mask1 *= 1. - mask2;
    }

    vec2 luv = mask1 * stuv1 + mask2 * stuv2;

    float rope = cos(luv.x * 2. * PI / 2.) * sin(fract((luv.x - luv.y) * 2.) * PI);
    col += vec3(.7,.1,.2) * mask1 * rope;
    col += vec3(.4,.1,.6) * mask2 * rope;

    col.rgb += vec3(luv.x);

    vec2 norm = (stxy1 * 5.*(uvs1.stuv.x*sign(uvs1.stuv.x * (length(stxy1) - 0.5))) *  mask1 + stxy2 * 5.*(uvs2.stuv.x*sign(uvs2.stuv.x * (length(stxy2) - 0.5))) * mask2);

    vec2 fwd = normalize(vec2(luv.y, -luv.x));

    norm -= 0.8 * fwd * (fract((luv.x - luv.y) * 3.) - 0.5);


   	float illum1 = clamp(dot(norm, vec2(1. * sin(iTime / 3.),1. * cos(iTime / 3.))), 0.,1.);
   	float illum2 = clamp(dot(norm, vec2(1. * sin(iTime / 5.),1. * cos(iTime / 3.))), 0.,1.);

    col.rgb = col.rgb * 0.4 
        + (col.rgb * 0.7 * illum1 +  illum1 * illum1) * vec3(1.,.5,0.)
        + (col.rgb * 0.7 * illum2 +  illum2 * illum2) * vec3(0.,.5,.8);

    //col.rg = norm;
    //col.b = illum;


    //col.g = length(tuv);
  	//col.rg = suv;

    fragColor.rgb = col;
}

float oneHz(){

   return(fract(iTime)*6.29);
}

float osc( float scale){

  scale *= .5;

  return sin(oneHz()) * scale + scale;
}



vec4 tiler(vec2 i){
    vec3 o = vec3(1.);


    vec2 grid = vec2(fract(i.x),fract(i.y));

    grid -=.5;

    grid.y = fract(length(grid)*(osc(length(i)*2.) +6.) );

    grid.y = smoothstep(.1, .8, grid.y);



    float sawtooth =  fract(length(i));
    // grid += vec2(triangle,triangle);;


    return vec4(grid, sawtooth, 1.0);

}




