mat2 rot(float a) {
    return mat2(cos(a), -sin(a),
                sin(a), cos(a)
               );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float aspect = iResolution.x/iResolution.y;
    uv.x *= aspect;
    uv -= vec2(aspect * .5, 0.5);

    // Time varying pixel color
    vec3 col = vec3(.8,0.,.1);

    float wiggliness = 10.;
    uv.x += sin((uv.y + iTime) * wiggliness) * .2;

    uv = rot(iTime) * uv;

    float p = length(uv);
    p = smoothstep(.2, .22, p);

    col *= fract(uv * 2.).xyx;

    // Output to screen
    fragColor = vec4(col,1.0) + p * .5;
}

#define AA 4.
#define NUM_STRIPES 10.
#define PERIOD 10.
#define COLOR vec3(1., .9, .75)

#define MAX_STEPS 100
#define NEAR .001
#define FAR 5.
#define PI 3.14159265359

vec3 hash(float p) {
    return fract(cos(p) * vec3(12345.6, 23456.7, 34567.8));
}

void tRotate(inout vec2 p, float angle) {
    float s = sin(angle), c = cos(angle);
	p *= mat2(c, -s, s, c);
}

float sdTorus(vec3 p, float r1, float r2) {
    return length(vec2(length(p.xz) - r1, p.y)) - r2;
}

float map(vec3 p) {
    return sdTorus(p.xzy, 1., .2);
}

float trace(vec3 o, vec3 r) {

    float t = 0.;
    for (int i = 0; i < MAX_STEPS; ++i) {
        float d = map(o + r * t);
        t += d;
        if (d < NEAR || t > FAR) break;
    }

    return t;
}

float render(vec2 fragCoord) {
	vec2 uv = (fragCoord.xy * 2. - iResolution.xy) / min(iResolution.y, iResolution.x);

    vec3 r = normalize(vec3(uv, 1.5));
    vec3 o = vec3(.1, -.1, -3);

    vec2 rot = iMouse.z > 0. ? iMouse.xy / iResolution.xy * 2. : vec2(-.2, .2);
    rot *= PI;
    tRotate(r.yz, -rot.y);
    tRotate(r.xz, -rot.x);
    tRotate(o.yz, -rot.y);
    tRotate(o.xz, -rot.x);

    float f = trace(o, r);

    if (f > FAR) return 1.;

    vec3 p = o + f * r;

    vec2 tp = atan(vec2(p.x, length(p.xy)-1.), p.yz) / PI / 2. + .5;
    tp.y = fract(tp.y + tp.x + iTime / PERIOD);
    vec3 h = hash(floor(tp.y * NUM_STRIPES));
    float offset = h.x,
          speed = ceil(h.y * PERIOD) / PERIOD,
          size = h.z * .25 + .125;

    tp.y = mod(tp.y, 1. / NUM_STRIPES) - .5 / NUM_STRIPES;
    tp.x = fract(tp.x + offset + speed * iTime) - .5;
    tp = abs(tp);

   	return step(smoothstep(size, 0., tp.x), tp.y / .4 * NUM_STRIPES);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    float color = 0.;

    vec2 uv = abs(fragCoord - iResolution.xy / 2.) / min(iResolution.y, iResolution.x);

    if (uv.x > .45 || uv.y > .45) {
        color = 0.;
    } else {
        for (float x = 0.; x < AA; ++x) {
            for (float y = 0.; y < AA; ++y) {
                color += render(fragCoord + vec2(x, y) / AA);
            }
        }
    	color /= AA * AA;
    }


	fragColor = vec4(mix(vec3(0), COLOR, color), 1);
}

const float angle = 1.;
const mat2 m1 = mat2(cos(angle),-sin(angle),sin(angle),cos(angle));

vec3 transform(vec3 p) {
    mat2 m2 = mat2(cos(iTime),-sin(iTime),sin(iTime),cos(iTime));
    p.yz *= m1;
    p.xy *= m2;
    return p;
}

const float slopeUB= 2.;
const float g = sin(atan(1.,slopeUB));

float sde(vec3 p) {
    float h = (sin(p.x) + sin(p.y));
    return (p.z-h)*g;

}

vec3 rayMarch(vec3 ro, vec3 rd) {
    vec3 p = ro + rd*0.001;
    while(true) {
        float d = sde(p);
        if (d < 0.0001) {break;}
        p += rd*d;
        if (length(p-ro) > 200.) {break;}
    }
    return p;
}

const vec2 helper = vec2(1e-5,0);

float light(vec3 l, vec3 p) {
    float d = sde(p);
    vec3 norm = normalize(vec3(
        sde(p + helper.xyy) - d,
        sde(p + helper.yxy) - d,
        sde(p + helper.yyx) - d
        ));
    vec3 ld = normalize(l-p);
    float s = max(0.,dot(ld, norm));
    if (s > 0.) {
        if (length(rayMarch(p,ld)-p) < length(l-p)) {
            return 0.;
        }
    }
    return s;
}

const vec3 l = vec3(0,10,5);
const vec3 ro = vec3(0,0,15);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
	vec3 c = vec3(15.*uv,3);
    vec3 ro_ = ro;
    c = transform(c);
    ro_ = transform(ro_);

    vec3 p = rayMarch(ro_, normalize(c - ro_));

    vec3 col = vec3(light(l, p));

    float d = length(p-ro_);
    col -= vec3(smoothstep(0.,200.,d));
    col = min(vec3(1),col);

    fragColor = vec4(col,1.0);
}

#define SAMPLES 2 //samples are quadratic!
#define max_iter 512

float lerp (float a, float b, float fac)
{
    return a * (1. - fac) + b * fac;
}


float brot_iter(vec2 uv)
{
    vec2 n = vec2(uv);
