const float divergence = 1.0;
const float fieldOfView = 1.5;

float random(vec2 p) { return fract(1e4 * sin(17.0 * p.x + p.y * 0.1) * (0.1 + abs(sin(p.y * 13.0 + p.x)))); }
mat2 rot(float a) { float c=cos(a),s=sin(a); return mat2(c,-s,s,c); }
float smoothmin (float a, float b, float r) { float h = clamp(.5+.5*(b-a)/r, 0., 1.); return mix(b, a, h)-r*h*(1.-h); }
vec3 look (vec3 eye, vec3 target, vec2 anchor, float fov) {
    vec3 forward = normalize(target-eye);
    vec3 right = normalize(cross(forward, vec3(0,1,0)));
    vec3 up = normalize(cross(right, forward));
    return normalize(forward * fov + right * anchor.x + up * anchor.y);
}

float hash1( float n ) {
	return fract(sin(n)*43758.5453);
}

float hash2(vec2 co) {
	return fract(sin(dot(co*0.123,vec2(12.9898,78.233))) * 43758.5453);
}

float hash3(vec3 p) {
	p  = fract( p*0.3183099+.1 );
	p *= 17.0;
	return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

float noise(vec3 x) {
	vec3 p = floor(x);
	vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);
	vec2 oi = vec2(0,1);
	return mix(mix(mix( hash3(p),
						hash3(p+oi.yxx),f.x),
				mix( hash3(p+oi.xyx),
						hash3(p+oi.yyx),f.x),f.y),
			mix(mix( hash3(p+oi.xxy),
						hash3(p+oi.yxy),f.x),
				mix( hash3(p+oi.xyy),
						hash3(p+oi.yyy),f.x),f.y),f.z);
}

float fbm (vec3 p, float s) {
	float value = 0.0;
	float amplitude = 1.;
	for (int i = 0; i < 3; ++i) {
		value += amplitude * noise(p);
		p *= 2.;
		p.yx *= rot(iTime*s*.5);
		p.yz *= rot(iTime*s*.3);
		p.xz *= rot(iTime*s*.6);
		amplitude *= .5;
	}
	return value;
}

vec3 camera (vec3 eye) {
    vec2 mouse = iMouse.xy/iResolution.xy*2.-1.;
    if (iMouse.z > 0.5) {
        eye.yz *= rot(mouse.y*3.1415);
        eye.xz *= rot(mouse.x*3.1415);
    }
    return eye;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdCone( in vec3 p, in float a )
{
    float q = length(p.xy);
    return dot(vec2(sin(a),cos(a)),vec2(q,p.z));
}

float sdColumn(vec3 p) {
    float a = atan(p.z, p.x);
    float d = length(p.xz) - (1. - p.y * 0.02 + sin(a * 20.) * .1);
    d = max(d, -p.y);
    d = max(d, p.y - 10.);

    float dCone = sdCone(p.xzy*vec3(1,1,-1)+vec3(0,0,2.), 1.45);
    dCone = max(dCone, -p.y + 9.8);
    dCone = max(dCone, p.y - 10.2);

    d = smoothmin(d, dCone, 0.2);
    d = smoothmin(d, sdBox(p - vec3(0, 10.5, 0), vec3(1.1, 0.3, 1.1)), 0.2);

    return d;
}

float sdTemple(vec3 p) {
    p.xz = abs(p.xz);
    const float sep = 6.;
    float d = sdColumn(p - vec3(sep*0.5, 0, sep*5.5));
    d = min(d, sdColumn(p - vec3(sep*1.5, 0, sep*5.5)));
    d = min(d, sdColumn(p - vec3(sep*2.5, 0, sep*5.5)));
    d = min(d, sdColumn(p - vec3(sep*2.5, 0, sep*4.5)));
    d = min(d, sdColumn(p - vec3(sep*2.5, 0, sep*3.5)));
    d = min(d, sdColumn(p - vec3(sep*2.5, 0, sep*2.5)));
    d = min(d, sdColumn(p - vec3(sep*2.5, 0, sep*1.5)));
    d = min(d, sdColumn(p - vec3(sep*2.5, 0, sep*0.5)));
    const float minisep = 5.;
    d = min(d, sdColumn(p - vec3(minisep*0.5, 0, minisep*4.5)));
    d = min(d, sdColumn(p - vec3(minisep*1.5, 0, minisep*4.5)));
    d = min(d, sdColumn(p - vec3(minisep*1.5, 0, minisep*3.5)));
    d = min(d, sdColumn(p - vec3(minisep*1.5, 0, minisep*2.5)));
    d = min(d, sdColumn(p - vec3(minisep*1.5, 0, minisep*1.5)));
    d = min(d, sdColumn(p - vec3(minisep*1.5, 0, minisep*0.5)));

    float dTop = sdBox(p - vec3(0,11.5,0), vec3(sep*2.5+1.1, 1., sep*5.5+1.1));
    dTop = min(dTop, sdBox(p - vec3(0, 13.5, sep*5.5-3.5), vec3(sep*2.5+1.1, 1., 4.6)));
    dTop = max(dTop, -sdBox(p, vec3(sep*2.5-1.1, 20., sep*5.5-1.1)));    
    dTop += fbm(p, 0.)*.1;
    d = min(d, dTop);

    vec3 q = p;
    q.x -= (sep*2.5+3.);
    q.y -= 14.5;
    q.xy *= rot(.2);
    float dTri = q.y;
    dTri = max(dTri, -(p.y - 14.5));
    dTri = max(dTri, p.x - (sep*2.5+1.));
    dTri = max(dTri, abs(p.z - sep*5.5 - .7) - 1.);
    dTri += fbm(p, 0.)*.1;
    d = min(d, dTri);

    const float stepsep = 1.;
    float dSteps = sdBox(p - vec3(0, -0.3, 0), vec3(sep*3.+0.*stepsep, .6, sep*6.+0.*stepsep)) + smoothstep(0.05, 0., abs(fract(p.x*.4)-.5))*.1;
    dSteps = min(dSteps, sdBox(p - vec3(0, -0.9, 0), vec3(sep*3.+1.*stepsep, .6, sep*6.+1.*stepsep)) + smoothstep(0.05, 0., abs(fract(p.x*.4+.5)-.5))*.1);
    dSteps = min(dSteps, sdBox(p - vec3(0, -1.5, 0), vec3(sep*3.+2.*stepsep, .6, sep*6.+2.*stepsep)) + smoothstep(0.05, 0., abs(fract(p.x*.4)-.5))*.1);
    dSteps = min(dSteps, sdBox(p - vec3(0, -2.1, 0), vec3(sep*3.+3.*stepsep, .6, sep*6.+3.*stepsep)) + smoothstep(0.05, 0., abs(fract(p.x*.4+.5)-.5))*.1);
    dSteps += fbm(p, 0.)*.2;
    d = min(d, dSteps);

    float dGround = p.y + 2.4;
    dGround += fbm(p, 0.)*.1;
    d = min(d, dGround);

    return d;
}

float sdCloud (vec3 p) {
	p.y -= 40.-20.*sin(6.283/8.*clamp(length(p.xz)/200.,0.,1.));
	float shape = abs(p.y+5.*fbm(p*.2, .01))-5.*(noise(p*.1+iTime*.1)-.4);
	shape = mix(1.,shape, clamp(length(p.xz),0.,1.));
	return shape;
}

float geometry (vec3 pos) {
    pos = camera(pos);
    float scene = sdTemple(pos);
    scene = min(scene, sdCloud(pos));
    return scene;
}

float raymarch ( vec3 eye, vec3 ray ) {
    float dither = random(ray.xy+fract(iTime));
    float total = dither;
    const int count = 50;
    for (int index = count; index > 0; --index) {
        float dist = geometry(eye+ray*total);
        dist *= 0.9+.1*dither;
        total += dist;
        if (dist < 0.001 * total)
            return float(index)/float(count);
    }
    return 0.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 diverg = vec2(divergence, 0.);
    vec3 eye = vec3(10,1,50);
    vec3 target = vec3(-10,15,0);

    vec2 uv = 2.*(fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 eyeLeft = eye - diverg.xyy;;
    vec3 eyeRight = eye + diverg.xyy;
    vec3 rayLeft = look(eyeLeft, target, uv, fieldOfView);
    vec3 rayRight = look(eyeRight, target, uv, fieldOfView);
    float red = raymarch(eyeLeft, rayLeft);
    float cyan = raymarch(eyeRight, rayRight);
    fragColor = vec4(red,vec2(cyan),1);
}

