#define PI     3.14159265
#define TWO_PI 6.28318530

float pipe1(float r, vec2 p, float l)
{
	float v = max(abs(p.x) - r, abs(p.y) - l);
	return min(v, max(abs(p.x) - 0.07, abs(abs(p.y) - (l - 0.04))));
}

float pipe2(float r, vec2 p, float rad, vec2 d)
{
	return max(max(abs(length(p) - rad) - r, p.x * d.x), p.y * d.y);
}

// Based on https://thebookofshaders.com/07/ with some small changes to get the effect I wanted.
float backgroundDE(in vec2 st)
{
    int N = 4;

    // Angle and radius from the current pixel
    float a = atan(st.x, st.y) + PI;
    float r = TWO_PI / float(N);

    // Shaping function that modulate the distance
    float d = cos(floor(1.0 + a / r) * r - a) * length(st);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord / iResolution.xy;
    st.x *= iResolution.x / iResolution.y;

    // Move the space
    st.x += 0.25 + sin(iTime * 0.5) * 0.15;

    // Remap the space to -1.0 to 1.0
    st = st * 2.0 - 1.0;

    float d = backgroundDE(st - vec2(0.03, 0.0));

    // PI text
    d = min(d, pipe1(0.03, st, 0.4));
    d = min(d, pipe2(0.03, st - vec2(0.1, 0.15), 0.2, vec2(-0.5, 0.1)));
    d = min(d, pipe1(0.03, st - vec2(0.65, 0.0), 0.4));

    //3.14 text
    //3
    d = min(d, pipe2(0.01, st - vec2(1.1, 0.11), 0.25, vec2(-0.5, 0.1)));
    d = min(d, pipe2(0.01, st - vec2(1.1, -0.4), 0.25, vec2(-0.5, 0.1)));
    //.
	d = min(d, pipe2(0.00, st - vec2(1.55, -0.51), 0.0, vec2(-0.5, 0.1)));
    //1
    d = min(d, pipe1(0.03, st - vec2(1.85, -0.1), 0.45));
    //4
    d = min(d, pipe1(0.03, st - vec2(2.2, 0.1), 0.25));
    d = min(d, pipe1(0.13, st - vec2(2.5, -0.1), 0.01));
    d = min(d, pipe1(0.03, st - vec2(2.6, -0.1), 0.45));

    // The smoothstep with the small interval is used to achive a simple anti aliasing.
    vec3 color = vec3(1.0 - smoothstep(0.05, 0.06, d) * 1.0 - vec3(1.0, 0.0, 0.0));
    fragColor = vec4(color, 1.0);
}

const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 5.0;
const float MAX_DIST = 25.0;
const float EPSILON = 0.0001;

mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

mat3 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, -s, 0),
        vec3(s, c, 0),
        vec3(0, 0, 1)
    );
}

float intersectSDF(float distA, float distB) {
    return max(distA, distB);
}

float unionSDF(float distA, float distB) {
    return min(distA, distB);
}

float differenceSDF(float distA, float distB) {
    return max(distA, -distB);
}

float boxSDF(vec3 p, vec3 b) {
  	vec3 q = abs(p) - b;
  	return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sphereSDF(vec3 p, float r) {
    return length(p) - r;
}

float sceneSDF(vec3 p) {
    p = rotateY(iTime / 2.0) * p;
    return unionSDF(
        differenceSDF(
            boxSDF(p, vec3(1.0, 1.0, 1.0)),
            unionSDF(
                sphereSDF(p + vec3(1.0, -1.0, 1.0), 1.66),
                sphereSDF(p + vec3(-1.0, 1.0, -1.0), 1.66)
            )
        ),
        sphereSDF(p, 0.5)
    );
}

float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}

vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

mat3 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat3(s, u, -f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(8.0, 5.0 * sin(0.2 * iTime), 7.0);

    mat3 viewToWorld = viewMatrix(eye, vec3(0.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0));

    vec3 worldDir = viewToWorld * viewDir;

    float dist = shortestDistanceToSurface(eye, worldDir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        fragColor = vec4(1.0, 1.0, 1.0, 1.0);
		return;
    }

    vec3 p = eye + dist * worldDir;

    float depth = (2.0 * dist) / (MAX_DIST + MIN_DIST);

    vec3 color = vec3(depth);

    fragColor = vec4(color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;
	st=st*2.0-1.0;
    float len = length(abs(st)-vec2(sin(iTime)));
    fragColor.xyz=vec3(fract(len*10.0));
}

