float circle(vec2 uv, vec2 center, float radius) {
    return length(uv-center)*length(uv-center) - radius*radius;
}

float line(vec2 uv, vec2 a, vec2 b) {
    return ((b.y-a.y)*uv.x + (a.x-b.x)*uv.y + (b.x*a.y-a.x*b.y))/sqrt((b.x-a.x)*(b.x-a.x)+(b.y-a.y)*(b.y-a.y));
}

float sum(float a, float b, float alpha) {
    return (a+b+sqrt(a*a + b*b - 2.0*alpha*a*b))*(1.0/(1.0+alpha));
}

float mul(float a, float b, float alpha) {
    return (a+b-sqrt(a*a + b*b - 2.0*alpha*a*b))*(1.0/(1.0+alpha));
}

// Our FV model
float map(vec2 uv, float alpha) {
    //vec2 pos1 = vec2(-cos(iTime)/2.0, 0.0);
    //vec2 pos2 = vec2(0.0, sin(iTime)/2.0);

    //return mul(circle(uv, pos1, 0.5), circle(uv, pos2, 0.5), alpha);

    vec2 a = vec2(-0.5, mix(0.0,-0.5,0.5+cos(iTime)/2.0));
    vec2 b = vec2(0.0, 0.0);
    vec2 c = vec2(mix(-0.5,0.5,0.5+cos(iTime)/2.0), -0.5);

    float r = 0.75 + cos(iTime)/4.0;

    return sum(mul(line(uv, a, b), line(uv, b, c), alpha), circle(uv, b, r), alpha);
}

// Calculating the Multi-Dimennsional Image
vec4 mdi(vec2 uv1, vec2 uv2, vec2 uv3, float value1, float value2, float value3) {
    float Ai = uv1.y*(value2-value3)-uv2.y*(value1-value3)+uv3.y*(value1-value2);
    float Bi = -(uv1.x*(value2-value3)-uv2.x*(value1-value3)+uv3.x*(value1-value2));
    float Ci = uv1.x*(uv2.y-uv3.y)-uv2.x*(uv1.y-uv3.y)+uv3.x*(uv1.y-uv2.y);
    float Di = uv1.x*(uv2.y*value3-uv3.y*value2)-uv2.x*(uv1.y*value3-uv3.y*value1)+uv3.x*(uv1.y*value2-uv2.y*value1);

    float Nx = Ai / sqrt(Ai*Ai + Bi*Bi + Ci*Ci + Di*Di);
    float Ny = -Bi / sqrt(Ai*Ai + Bi*Bi + Ci*Ci + Di*Di);
    float Nz = -Ci / sqrt(Ai*Ai + Bi*Bi + Ci*Ci);
    float Nt = Di / sqrt(Ai*Ai + Bi*Bi + Ci*Ci + Di*Di);

    int flag1 = 0;
    int flag2 = 0;
    int flag3 = 0;
    if (value1>=0.0) flag1 = 1;
    if (value2>=0.0) flag2 = 1;
    if (value3>=0.0) flag3 = 1;

    int flag_sum = flag1 + flag2 + flag3;
    if (flag_sum < 2) return vec4((Nx+1.0)/2.0, (Ny+1.0)/2.0, (Nz+1.0)/2.0, (Nt+1.0)/2.0); //xyz representation
    return vec4(0.5, 0.5, 1.0, 0.0); //+Z normal
}

vec4 calcImage(vec2 uv, float alpha) {
    vec2 uv1 = uv;
    vec2 uv2 = vec2(uv.x, uv.y+0.005);
    vec2 uv3 = vec2(uv.x+0.005, uv.y);

    float value1 = map(uv1, alpha);
    float value2 = map(uv2, alpha);
    float value3 = map(uv3, alpha);

    return mdi(uv1, uv2, uv3, value1, value2, value3);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 vUV = fragCoord.xy / iResolution.xy;
	vec2 vWindow = vUV * 2.0 - 1.0;
	vWindow.x *= iResolution.x / iResolution.y;

    //vec4 value = circle_mdi(vWindow, vec2(sin(iTime)*0.5, cos(iTime)*0.5), 0.5);
    //vec4 value = line_mdi(vWindow, vec2(sin(iTime),1), vec2(cos(iTime),-1));
    vec4 value = calcImage(vWindow, 0.0);
    vec3 col = vec3(value.x, value.y, value.z);

    fragColor = vec4(col, 1.0);
}

