// ========== Rotation ==========

mat3 rotateX(float pitch){
    float cPitch = cos(pitch), sPitch = sin(pitch);
    return mat3(	1.0,	0.0,	0.0,
                0.0,	cPitch,	sPitch,
                0.0,	-sPitch,	cPitch);
}

mat3 rotateY(float yaw){
    float cYaw = cos(yaw), sYaw = sin(yaw);
    return mat3(	cYaw,	0.0,	sYaw,
                0.0,	1.0,	0.0,
                -sYaw,	0.0,	cYaw);
}

mat3 rotateZ(float roll){
    float cRoll = cos(roll), sRoll = sin(roll);
    return mat3(	cRoll,	-sRoll,	0.0,
                sRoll,	cRoll,	0.0,
                0.0,	0.0,	1.0);
}

mat3 eulerToMatrix(float roll, float pitch, float yaw){
    //return rotateX(pitch) * rotateY(yaw) * rotateZ(roll);
    return  rotateZ(roll) * rotateX(pitch) * rotateY(yaw);
}

//Converts a unit vector into polar coordinates , vec2(Yaw, Pitch).
vec2 toPolar(vec3 point) {
    return vec2(atan(point.x,point.z),asin(point.y));
}

// ========== ==========

// ========== Ray Constructor ==========

ray getRay(vec2 uv, camera camera) {
    ray ray;
    ray.origin = camera.origin;
    vec3 center = ray.origin + camera.forward * camera.zoom;
    vec3 intersection = center + (uv.x * camera.right) + ( uv.y * camera.up );
    ray.direction = normalize(intersection - ray.origin);
    return ray;   
}

