// ========== Hash Functions ==========

#define HD 43758.5453123
#define HE 12.989824344
#define HF 78.233235235
#define HG 44.230257362
#define HH 83.597411735

float hash11(float st) {
    #if defined(NOISE_TEXTURE_3D)
    return texelFetch( NOISE_TEXTURE_3D, ivec3(vec3(HF,HG,HH)*st)&31,0).x;
    #elif defined(NOISE_TEXTURE_2D)
    return texelFetch( NOISE_TEXTURE_2D, ivec2(vec2(HF,HG)*st)&255,0).x;
    # else
    return fract(sin(st*HE)*HD); 
    #endif
}

float hash21(vec2 st) {
    #if defined(NOISE_TEXTURE_3D)
    return texelFetch( NOISE_TEXTURE_3D, ivec3(st*HE,(st.x*st.y)*HE) &31,0).x;
    #elif defined(NOISE_TEXTURE_2D)
    return texelFetch( NOISE_TEXTURE_2D, ivec2(st*HE)&255,0).x;
    # else
    return fract(sin(dot(st,vec2(HE,HF)))*HD);
    #endif
}

float hash31(vec3 st) {
    #if defined(NOISE_TEXTURE_3D)
    return texelFetch( NOISE_TEXTURE_3D, ivec3(st*HE)&31,0).x;
    #elif defined(NOISE_TEXTURE_2D)
    return texelFetch( NOISE_TEXTURE_2D, ivec2(st.x*HE+st.z*HF,st.y*HG+st.z*HH)&255,0).x;
    # else
    return fract(sin(dot(st,vec3(HE,HF,HG)))*HD);
    #endif
}


//FIXME: find alternativs with texture lookup
vec3 hash13(float st) {
    return fract(sin(st*vec3(HE,HH,HG))*HD);
}

vec3 hash23(vec2 st) {
    return fract(sin(st.xyx*vec3(HE,HF,HH))*HD);
}

vec3 hash33(vec3 st) {
    return fract(sin((st+vec3(HD,HH,HE))*vec3(HH,HE,HG))*HD);
}

// ========== ==========

// ========== Helper Functions ==========

//Simple remap of value and range to new value in new range
float map(float value, float min1, float max1, float min2, float max2) {
    return (value - min1) / (max1 - min1) * (max2 - min2) + min2;
}

//Quintic interpolation curve
vec2 quinticInterpolation(vec2 x) {
    return x*x*x*(x*(x*6.-15.)+10.);
}

float quinticInterpolation(float x) {
    return x*x*x*(x*(x*6.-15.)+10.);
}

// ========== ==========

// ========== Coherent Noise ==========


// - Value Noise, 2d, Unsigned
float valueNoise2du(vec2 samplePoint) {
    vec2 pointI = floor(samplePoint);
    vec2 pointF = fract(samplePoint);
    float bl = hash21(pointI + vec2(0.0,0.0) );
    float br = hash21(pointI + vec2(1.0,0.0) );
    float fl = hash21(pointI + vec2(0.0,1.0) );
    float fr = hash21(pointI + vec2(1.0,1.0) );
    //vec2 u = smoothstep(0.0,1.0,pointF);
    vec2 u = quinticInterpolation(pointF);
    float b = mix(bl,br,u.x);
    float f = mix(fl,fr,u.x);

    return mix(b,f,u.y);
}

// - Value Noise, 1d, Unsigned
float valueNoise1du(float samplePoint) {
    float pointI = floor(samplePoint);
    float pointF = fract(samplePoint);
    float b = hash11(pointI);
    float f = hash11(pointI + 1.0 );
    float u = quinticInterpolation(pointF);
    return mix(b,f,pointF);
}

// - Value Noise, 1d, Unsigned, Wraped
float valueNoise1duw(float samplePoint, float wrap) {
    float pointI = floor(samplePoint);
    float pointF = fract(samplePoint);
    float a = hash11(mod(pointI, wrap));
    float b = hash11(mod(pointI + 1.0 ,wrap));
    float u = quinticInterpolation(pointF);
    return mix(a,b,u);
}

// ========== ==========

// ========== Distance Feilds ==========

// All primitives are centered at the origin.

//Sphere - exact
float sdSphere( vec3 p, float s )
{
	return length(p)-s;
}

//Box - exact
float sdBox( vec3 p, vec3 b )
{
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float sdTorus( vec3 p, vec2 t )
{
    return length( vec2(length(p.xz)-t.x,p.y) )-t.y;
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
	vec3 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h ) - r;
}

// ========== ==========

// ========== Ray Marcher ==========

rayMarchHit GetRayMarchHit(vec3 position, float minSurfaceDistance, float time) {
    rayMarchHit hit;
    hit.distance = RAY_MAX_DISTANCE;

    float s1 = 5.0;

    vec3 id = hash33( floor((position+0.5*s1)/s1) );
    vec3 positionRepeat = mod(position+0.5*s1,s1)-0.5*s1;

    float s2 = (s1/10.0);

    float newDistance;

    #if defined(DEBUG_MORE_SHAPES)
	if (hash31(id) < 0.25) {
    	newDistance= sdSphere(positionRepeat,s2);
    } else if (hash31(id) < 0.5) {
    	newDistance = sdBox(positionRepeat, vec3(s2));
    } else if (hash31(id) < 0.75) {
    	newDistance = sdTorus( positionRepeat, vec2(s2,s2*0.2) );
    } else {
        newDistance = sdCapsule( positionRepeat, vec3(0.0,-s2*0.5,0.0), vec3(0.0,s2*0.5,0.0) ,s2*0.25 );
    }
    #else
    	newDistance= sdSphere(positionRepeat,s2);
    #endif

    if (newDistance < hit.distance) {
        hit.origin = position;
    	hit.distance = newDistance;
        hit.materialId = MAT_NEON;
        #if defined(DEBUG_FLICKER)
        	hit.neon = normalize(id+ vec3(0.001)) * ( 1.0-valueNoise1duw((time+hash31(id)*10.0)*100.0,200.0)*0.5); //add here to remove artifacts with 0 length Ids
        #else
        	hit.neon = normalize(id+ vec3(0.001));
        #endif
	}

    return hit;
}

rayMarchHit RayMarch(ray ray, float maxDistance, float minSurfaceDistance, int maxSteps, float time)
{
    rayMarchHit hit;
    float lastDistance = 0.0;
    float accumilatedDistance = 0.0;
	vec3 accumilatedNeon = vec3(0.0);

    for(int i=0; i<maxSteps; i++) {

        vec3 currentPoint = ray.origin + ray.direction*accumilatedDistance;
        hit = GetRayMarchHit(currentPoint, minSurfaceDistance, time);
        if (hit.distance > minSurfaceDistance) {
            accumilatedNeon += (1.0-accumilatedDistance/maxDistance) * (hit.neon) * max(1.0-hit.distance/2.0,0.0) * 0.02;
        } else {
            #if defined (DEBUG_WHITE_NEON)
            	accumilatedNeon += vec3((1.0-accumilatedDistance/maxDistance));
            #else
            	accumilatedNeon = abs(accumilatedNeon-vec3(0.005));
            #endif
        }

		hit.iteration = i;
		lastDistance = hit.distance;

        #if defined (DEBUG_RAY_DITHER)
        	accumilatedDistance += mix(hit.distance*0.5,hit.distance*0.9, hash31(fract(currentPoint)));
        #else 
        	accumilatedDistance += hit.distance;
        #endif

        if(accumilatedDistance >= maxDistance) {
            hit.surfaceNormal = ray.direction;
            hit.materialId=MAT_SKY;
            break;
        }
        //terminate if we are very close or bounce back out if we end up within an object. 
        else if(hit.distance >= 0.0 && hit.distance < minSurfaceDistance) {
            break;
        }
    }
    hit.distance = accumilatedDistance;
    hit.neon = accumilatedNeon;
    return hit;
}

