void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
    float time = iTime;
    //vec3 debugOverlay = vec3(0.0);

    // Normalized Pixel coordinates (from -0.5 to +0.5, center at 0,0)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    // Normalized Mouse coordinates (from -0.5 to +0.5, center at 0,0)
    vec2 mouse = ((iMouse.xy-0.5*iResolution.xy)/iResolution.y);// - vec2(0.5,0.5)*(iMouse.w*0.1);

    //Snap to center on startup.
    if((iMouse.x <= 1.0 && iMouse.y <= 1.0)) {
        mouse = vec2(0.0,0.0);
    }

    camera camera = getCamera(mouse, time);

    // --
    ray ray = getRay(uv, camera);
    rayMarchHit hit = RayMarch(ray, RAY_MAX_DISTANCE, OBJECT_MIN_SURFACE_DISTANCE, RAY_MAX_STEPS, time);

    //float yaw3 = atan(ray.direction.x,ray.direction.z)/PI;
    //debugOverlay = vec3(abs(fract( (yaw3-yaw1)*0.5+0.5)*2.0-1.0));

    vec3 colour;

    // ---- Render
	//Distance
    colour = vec3(hit.distance/RAY_MAX_DISTANCE);
    colour = vec3(hit.neon);
    //colour = vec3(camera.yaw);

    // ---- 
    float exposure = 1.0;

    // Gamma
    colour = pow( exposure * colour, vec3(1.0/2.2) );

    //Clamp
    colour = clamp(colour,0.0,1.0); 

    // colour grading
    //colour = colour*vec3(1.0,0.8,1.0);
    // compress        
    colour = 1.7*colour/(1.0+colour);
    colour = colour*colour*(3.0-2.0*colour);

    fragColour = vec4(colour,1.0);
    //ragColour = vec4(mix(colour,debugOverlay,0.5),1.0);

}

vec2 rotate(vec2 uv,float rot)
{
    // rotation matrix
    float s = sin(-rot);
    float c = cos(-rot);
    return mat2x2(c,s,-s,c)*uv;
}

vec3 calcColor(vec2 uv, float t)
{
    vec2 uv2 = rotate(uv, sin(t*0.2)*6.28);
    float d;
    // mod
    vec2 uv3 = mod(uv2, 0.3) / 0.3;
    // lattice
    if(uv3.x < 0.1 || uv3.y < 0.1)
    {
        return vec3(1.0,1.0,1.0);
    }
    return vec3(abs(uv3.x),abs(uv3.y),sin(t)*0.5+0.5);
}

vec4 motionBlur(vec2 uv, float Freq)
{
    float doubleFreq = Freq * Freq;
    vec3 col = vec3(.0,.0,.0);
    for(float i=0.0;i<Freq;i++)
    {
        // ∫x dx = (1/2)x^2 + C
        col += calcColor(uv, iTime - i / Freq * 0.1 )*((Freq - i) * 2.0 / doubleFreq);
    }

    return vec4(col,1.0);

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates
     vec2 uv = (2.0*fragCoord - iResolution.xy ) / iResolution.y;

    // Output to screen
    fragColor = motionBlur(uv,20.0);
}

#define MAX_STEPS 10
#define MAX_DIST 100.
#define SURF_DIST .01


/**
 * Rotation matrix around the X axis.
 */
mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

/**
 * Rotation matrix around the Y axis.
 */
mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

/**
 * Rotation matrix around the Z axis.
 */
mat3 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, -s, 0),
        vec3(s, c, 0),
        vec3(0, 0, 1)
    );
}

float opUnion( float d1, float d2 ) {  return min(d1,d2); }

float opSubtraction( float d1, float d2 ) { return max(-d1,d2); }

float opIntersection( float d1, float d2 ) { return max(d1,d2); }

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float GetDist(vec3 p) {
    p = rotateZ((iTime + 1000.) / 6.) * rotateY((iTime + 500.) / 3.) * rotateX(iTime / 2.) * p;

    float d1 = sdSphere(p, 6.);
    float c = sin(iTime / 2.);
    float d2 = (sin(2. * p.x * c)*cos(2.*p.y * c)*sin(2.*p.z * c));
    return d1 + d2;
}


vec3 GetNormal(vec3 p) {
    float d = GetDist(p);
    vec2 e = vec2(SURF_DIST, 0);
    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}   

float RayMarch(vec3 ro, vec3 rd) {    
    float dO = 0.;

    for (int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if (dO > MAX_DIST || dS < SURF_DIST) break;
    }

    return dO;
}

float GetLight(vec3 p) {
    vec3 lightPos = vec3(10, 10, -20);
    vec3 lightV = normalize(lightPos - p);
    vec3 normal = GetNormal(p);

    float dif = clamp(dot(normal, lightV), 0., 1.);    

	float d = RayMarch(p+normal*SURF_DIST*10000., lightV);
    if (d < length(lightPos - p)) dif *= .5;
    return dif;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 0, -20);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    float d = RayMarch(ro, rd);
    vec3 p = ro + rd * d;

    if (d < 26.)
    	col = vec3(GetLight(p));

    fragColor = vec4(col,1.0);
}

//Base values modified with depth later
float intensity = 1.0;
float radius = 0.05;

//Distance functions from 
//https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float triangleDist(vec2 p){ 
    const float k = sqrt(3.0);
    p.x = abs(p.x) - 1.0;
    p.y = p.y + 1.0/k;
    if( p.x+k*p.y>0.0 ) p=vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0, 0.0 );
    return -length(p)*sign(p.y);
}

float boxDist(vec2 p){
    vec2 d = abs(p)-1.0;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0);
}

float circleDist( vec2 p){
  return length(p) - 1.0;
}

//https://www.shadertoy.com/view/3s3GDn
float getGlow(float dist, float radius, float intensity){
    return pow(radius/dist, intensity);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

	vec2 uv = fragCoord/iResolution.xy;
    float widthHeightRatio = iResolution.x/iResolution.y;
    vec2 centre;
    vec2 pos;

    float t = iTime * 0.05;

    float dist;
    float glow;
    vec3 col = vec3(0);

    //The spacing between shapes
    float scale = 500.0;
    //Number of shapes
    float layers = 15.0;

    float depth;
    vec2 bend;

    vec3 purple = vec3(0.611, 0.129, 0.909);
    vec3 green = vec3(0.133, 0.62, 0.698);

    float angle;
    float rotationAngle;
    mat2 rotation;

    //For movement of the anchor point in time
    float d = 2.5*(sin(t) + sin(3.0*t));

    //Create an out of frame anchor point where all shapes converge to    
    vec2 anchor = vec2(0.5 + cos(d), 0.5 + sin(d));

    //Create light purple glow at the anchor loaction
    pos = anchor - uv;
    pos.y /= widthHeightRatio;
    dist = length(pos);
    glow = getGlow(dist, 0.25, 3.5);
    col += glow * vec3(0.6,0.4,1.0);

	for(float i = 0.0; i < layers; i++){

        //Time varying depth information depending on layer
        depth = fract(i/layers + t);

        //Move the focus of the camera in a circle
        centre = vec2(0.5 + 0.2 * sin(t), 0.5 + 0.2 * cos(t));

        //Position shapes between the anchor and the camera focus based on depth
        bend = mix(anchor, centre, depth);

        pos = bend - uv;
    	pos.y /= widthHeightRatio;

        //Rotate shapes
       	rotationAngle = 3.14 * sin(depth + fract(t) * 6.28) + i;
        rotation = mat2(cos(rotationAngle), -sin(rotationAngle), 
                        sin(rotationAngle),  cos(rotationAngle));

        pos *= rotation;

        //Position shapes according to depth
    	pos *= mix(scale, 0.0, depth);

        float m = mod(i, 3.0);
        if(m == 0.0){
        	dist = abs(boxDist(pos));
        }else if(m == 1.0){
        	dist = abs(triangleDist(pos));
        }else{
        	dist = abs(circleDist(pos));
        }

        //Get glow from base radius and intensity modified by depth
    	glow = getGlow(dist, radius+(1.0-depth)*2.0, intensity + depth);

        //Find angle along shape and map from [-PI; PI] to [0; 1]
        angle = (atan(pos.y, pos.x)+3.14)/6.28;
        //Shift angle depending on layer and map to [1...0...1]
		angle = abs((2.0*fract(angle + i/layers)) - 1.0);

        //White core
    	//col += 10.0*vec3(smoothstep(0.03, 0.02, dist));

        //Glow according to angle value
     	col += glow * mix(green, purple, angle);
	}

    //Tone mapping
    col = 1.0 - exp(-col);

    //Gamma
    col = pow(col, vec3(0.4545));

    //Output to screen
    fragColor = vec4(col,1.0);
}

/* 
	PARTS :
	* CONSTANTS
	* UTILS
	* OBJECT DEFINITION
	* SCENE
	* RAY MARCHING
	* MAIN
*/

/*
	CONSTANTS
*/

#define MAX_STEPS 100
#define SURFACE_DIST 1.5 / iResolution.y
#define MAX_DIST 100.
#define PI 3.14159

#define TYPE_LIGHT 0
#define TYPE_SPHERE 1
#define TYPE_PLANE 2
#define TYPE_CAPS 3
#define TYPE_TORUS 4
#define TYPE_BOX 5
#define TYPE_CYL 6

#define MAX_OBJECTS 10
#define MAX_REFLECT 5

/*
	UTILS
*/

mat3 rotY(float t)
{
    return mat3(
        cos(t), 0, sin(t),
        0, 1, 0,
        -sin(t), 0, cos(t));
}

/*
	OBJECT DEFINITION
*/

struct obj {
    int type;
    vec4 v;
    vec4 w;
    vec3 color;
    float ref;
};

obj light(vec3 pos, vec3 color)
{
    return obj(TYPE_LIGHT, vec4(pos, 0), vec4(0), color, 0.);
}

obj sphere(vec3 pos, float r, vec3 color, float ref)
{
    return obj(TYPE_SPHERE, vec4(pos, r), vec4(0), color, ref);
}

obj plane(vec3 dir, float offset, vec3 color, float ref)
{
    return obj(TYPE_PLANE, vec4(dir, offset), vec4(0), color, ref);
}

obj capsule(vec3 pos1, vec3 pos2, float r, vec3 color, float ref)
{
    return obj(TYPE_CAPS, vec4(pos1, r), vec4(pos2, 0), color, ref);
}

obj torus(vec3 pos, float r1, float r2, vec3 color, float ref)
{
    return obj(TYPE_TORUS, vec4(pos, 0), vec4(r1, r2, 0, 0), color, ref);
}

obj box(vec3 pos, vec3 size, vec3 color, float ref)
{
    return obj(TYPE_BOX, vec4(pos, 0), vec4(size, 0), color, ref);
}

obj cylinder(vec3 pos1, vec3 pos2, float r, vec3 color, float ref)
{
    return obj(TYPE_CYL, vec4(pos1, r), vec4(pos2, 0), color, ref);
}

//get distance from an object by its type
float getObjDist(vec3 p, obj o)
{
    switch (o.type) {
    case TYPE_SPHERE:
        return length(p - o.v.xyz) - o.v.w;
    case TYPE_PLANE:
        return dot(o.v.xyz, p) + o.v.w;
    case TYPE_TORUS:
        p -= o.v.xyz;
        float x = length(p.xz) - o.w.x;
        return length(vec2(x, p.y)) - o.w.y;
    case TYPE_BOX:
        p -= o.v.xyz;
        return length(max(abs(p) - o.w.xyz, 0.));
    case TYPE_CAPS:
        vec3 ab1 = o.w.xyz - o.v.xyz;
        vec3 ap1 = p - o.v.xyz;
        float t1 = dot(ap1, ab1) / dot(ab1, ab1);
        t1 = clamp(t1, 0., 1.);
        vec3 c1 = o.v.xyz + t1 * ab1;
        return length(p - c1) - o.v.w;
    case TYPE_CYL:
        vec3 ab2 = o.w.xyz - o.v.xyz;
        vec3 ap2 = p - o.v.xyz;
        float t2 = dot(ap2, ab2) / dot(ab2, ab2);
        vec3 c2 = o.v.xyz + t2 * ab2;
        float d = length(p - c2) - o.v.w;
        float y = (abs(t2 - .5) - .5) * length(ab2);
        float e = length(max(vec2(d, y), 0.));
        float i = min(max(d, y), 0.);
        return e + i;
    }
    return MAX_DIST;
}

/*
	SCENE
*/

obj scene[MAX_OBJECTS];
int obj_count;

/* 
	RAY MARCHING
*/

//get distance from closest object in scene
float getDist(vec3 p)
{
    float d = MAX_DIST;
    for (int i = 0; i < obj_count; i++) {
        d = min(d, getObjDist(p, scene[i]));
    }
    return d;
}

//get the normal vector of a point relative to the scene
vec3 getNormal(vec3 p)
{
    float d = getDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
                     getDist(p - e.xyy),
                     getDist(p - e.yxy),
                     getDist(p - e.yyx));

    return normalize(n);
}

struct hit {
    float dO;
    float dS;
    int index;
};

//return the closest object in scene and its distance
hit getDist2(vec3 p)
{
    float d = MAX_DIST;
    float t;
    int hit_index = -1;
    for (int i = 0; i < obj_count; i++) {
        t = getObjDist(p, scene[i]);
        if (t < d) {
            hit_index = i;
            d = t;
        }
    }
    return hit(0., d, hit_index);
}

//ray march in a direction until an object is hit
hit rayMarch(vec3 ro, vec3 rd)
{
    float dO = 0.;
    hit last;
    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + dO * rd;
        last = getDist2(p);
        dO += last.dS;
        if (last.dS < SURFACE_DIST || dO > MAX_DIST)
            break;
    }
    last.dO = dO;
    return last;
}

//calculate the light influence on a point
vec3 getLight(vec3 p, obj light)
{
    vec3 l = normalize(light.v.xyz - p);
    vec3 n = getNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);

    hit h = rayMarch(p + n * SURFACE_DIST * 2., l);
    float ld = length(light.v.xyz - p);

    if (h.dO < ld) {
        dif *= .1;
    }
    return dif * light.color;
}

/*
	MAIN
*/

//add light from every sources
vec3 getPointColor(vec3 p, vec3 hitColor)
{
    vec3 col = vec3(0);
    for (int i = 0; i < obj_count; i++) {
        if (scene[i].type == TYPE_LIGHT) {
            col += getLight(p, scene[i]);
        }
    }
    return col * hitColor;
}

//main calculation : hit and bounce over objects
vec4 getColor(vec2 uv, vec3 ro, mat3 dir)
{
    vec3 rd = normalize(vec3(uv.x, uv.y, 1.)) * dir;

    hit h = rayMarch(ro, rd);
    vec3 p = ro + rd * h.dO;
    vec3 col = vec3(0);
    float ref = 1.;
    int i = 0;
    vec3 n;
    while (h.index >= 0 && i++ < MAX_REFLECT) {
        col += ref * getPointColor(p, scene[h.index].color) * (1. - scene[h.index].ref);
        ref *= scene[h.index].ref;
        if (ref > .01) {
            n = getNormal(p);
            rd -= 2. * dot(rd, n) * n;
            h = rayMarch(p + rd * SURFACE_DIST * 2., rd);
            p += (h.dO + SURFACE_DIST * 2.) * rd;
        }
        else {
            break;
        }
    }

    return vec4(col, 1);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    //normalize uv
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    //build scene
    int i = 0;
    scene[i++] = plane(vec3(0, 1, 0), 0., vec3(1.), .2);
    scene[i++] = sphere(vec3(0, .5 + cos(iTime) * .2, -2), .5 + cos(iTime) * .2, vec3(0., 1., 0.), .5);
    scene[i++] = box(vec3(0, .3, 0), vec3(.6, .3, .6), vec3(1., 0., 0.), .2);
    scene[i++] = torus(vec3(0, 1. + sin(iTime) * .2, 0), .5, .1, vec3(1., 0., 0.), .2);
    scene[i++] = capsule(vec3(0, .3, 0), vec3(0, .5 + cos(iTime) * .2, -2), .08 + cos(iTime) * .03, vec3(1., 1., 1.), 0.);
    scene[i++] = cylinder(vec3(0, 0, 0), vec3(0, 1. + sin(iTime) * .2, 0), .2, vec3(1., 1., 1.), .2);
    scene[i++] = light(vec3(2, 4, 0), vec3(.7, .3, .3));
    scene[i++] = light(vec3(2, 4, .5), vec3(.3, .7, .3));

    obj_count = i;

    //specify camera
    vec3 pos = vec3(cos(iTime - PI / 2.) * 3., 1. + cos(iTime * .3) * .5, sin(iTime - PI / 2.) * 3.);
    mat3 dir = rotY(-iTime);

    fragColor = getColor(uv, pos, dir);
}

#define MAX_DIST 20.0
float smin(float a, float b, float k)
{
    float h = max(k-abs(a-b),0.0)/k;
    return min(a,b)-h*h*k*(1.0/4.0);
}


float elipsoid(vec3 p, vec3 r)
{
    float k=length(p/r);
    float k1 = length(p/(r*r));
    return k*(k-1.)/k1;
}

float box(vec3 p, vec3 b, float r)
{
    vec3 d = abs(p)-b;
    return length(max(d,0.0))-r + min(max(d.x,max(d.y,d.z)),0.0);

}

float cyl (vec3 p, float h, float r)
{
    vec2 d = abs(vec2(length(p.xz),p.y))-vec2(h,r);
    return min(max(d.x,d.y),0.0)+length(max(d,0.0));

}

float sphere(vec3 p, float r)
{
    return length(p)-r;
}

vec2 map(vec3 p)
{
    float f=clamp((1.+cos(iTime/3.)),0.0,1.0);
    float r = 0.0;

    float id = mod(floor((9.5+iTime)/18.85),2.);

    float s = elipsoid(p+vec3(0,1.1,0.),vec3(3.+cos(length(p+p.x))/2.+sin(length(p+p.z))/2.,.3+f*2.,3));
    float b=box(p,vec3(1.,1.,1.),.05);
    float c = cyl(p+vec3(0,3.05,0),5.+p.y,2.);


    vec3 sp=p+vec3(0.,2.36-f*2.,0.);
    float snowman  =smin(sphere(sp,1.),
                        smin(sphere(sp+vec3(0.,-.3-f,0),.7),
                           sphere(sp+vec3(0.,.7-f*3.,0.),.5),.2),.2);


    float ta = snowman;
    if(id==1.)ta=b;

   	float i=mix(s,ta,f);
    r=min(i,c);

    float m=0.0;

    if(r==i)
    {
        m=2.+f;
    }
    else if(r==c)m=1.;

    return vec2(r,m);
}

vec3 normal(vec3 p)
{
    float eps = 0.0005;
    return normalize(vec3(
        map(vec3(p.x+eps,p.y,p.z)).x-map(vec3(p.x-eps,p.y,p.z)).x,
        map(vec3(p.x,p.y+eps,p.z)).x-map(vec3(p.x,p.y-eps,p.z)).x,
        map(vec3(p.x,p.y,p.z+eps)).x-map(vec3(p.x,p.y,p.z-eps)).x

    ));

}

vec2 ray(vec3 ro, vec3 rd)
{
    float m = 0.0;
    float t = 0.0;
    for(int i=0;i<128;i++)
    {
        vec3 p = ro+rd*t;
        vec2 h = map(p);
        m=h.y;
        if(h.x<0.0001)break;
        t+=h.x;
        if(t>MAX_DIST)break;
    }

    if(t>MAX_DIST)t=-1.;

    return vec2(t,m);
}

vec3 color(vec3 ro, vec3 rd, vec2 r)
{
    vec3 col =vec3(-0.1-rd.y,0.3,1.4);

    if(r.x>0.0)
    {
        vec3 p = ro+rd*r.x;
        vec3 n=normal(p);
        vec3 mate = vec3(0.2);
        if(r.y<.5)
        {
            mate=vec3(.1,.1,0.1);
        }
        else if(r.y<1.5){

            mate=vec3(0.1,0.1,0.2);
        }
        else if(r.y<3.5)
        {
            vec3 wc = vec3(.0,.2,1.+(1.+cos(iTime-p.x*2.)*.2+sin(iTime-p.z*4.)/2.));
            vec3 sc = vec3(.6,1.,3.)*(1.5);
            mate=mix(wc, sc, r.y-2.);

        }

        vec3 sun = normalize(vec3(-0.2,.25,0.2));
        float dif = clamp(dot(sun,n),0.0,1.0);
        float sha = step(ray(p+n*.001,sun).x,0.0);
        float sky = clamp(0.5+0.5*dot(n,vec3(0.,1.,0.)),0.,1.);
        float bou = clamp(0.5+0.5*dot(n,vec3(0.,-1.,0.)),0.,1.);

        col = mate*dif*sha;
        col+= mate*vec3(0.5,0.2,0.1)*sky;
        col+= mate*vec3(0.3,0.2,0.1)*bou*.5;
    }

    return col;

}

vec3 render(vec3 ro, vec3 rd, vec2 r)
{

    vec3 col =  color(ro,rd,r);
    vec3 p = ro+rd*r.x;
    vec3 n = normal(p);

    if(r.x>0.0)
    {

		vec3 nc = col;
        if(r.y>=2.)
        {
            nc=color(p,n,r);
            rd = reflect(rd,n);
            ro=p+n*.001;
            r = ray(ro,rd);
            p=ro+r.x*rd;
            vec3 c = color(p,normal(p),r);
            nc+=c/10.;
        }
        col=mix(col,mix(col,nc,(1.+(2.-r.y))),1.-((2.-r.y)));

    }

    col=pow(col,vec3(.454545));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    float dist = -8.+(1.+sin(iTime/10.))/2.;
    vec3 ro = vec3(dist*cos(iTime/5.),2.5,dist*sin(iTime/5.));
    vec3 ta = vec3(0.,0.,0.);

    vec3 camForward = normalize(ta-ro);
    vec3 camUp = normalize(cross(camForward,vec3(0,1,0)));
    vec3 camRight = normalize(cross(camUp,camForward));

    vec3 rd = normalize(uv.x*camUp+uv.y*camRight+2.*camForward);

    vec2 r = ray(ro,rd);

	vec3 col = render(ro,rd,r); 


    fragColor = vec4(col,1.0);
}

#define zoom 10.

float len(vec2 v, vec2 id)
{
    float d1 =  abs(v.x) + abs(v.y);
    float d2 = length(v);
    return mix(d1, d2, length(id / zoom) + sin(iTime*1.3));
}

float xor(float a, float b)
{
    //return 1. - abs(1. - (a + b));
    //return a * (1.-b) + b*(1.-a);
    return a + b;
}

void mainImage( out vec4 col, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;

    float angle = .7853981634;
    float sa = sin(angle);
    float ca = cos(angle);
    uv *= mat2(ca, sa, -sa, ca);

    vec2 guv = uv * zoom;
    vec2 p = fract( guv ) -.5;
    vec2 id = floor(guv);

    float d = 0.;

    for (float x = -1.; x <= 1.; x++) {
    for (float y = -1.; y <= 1.; y++) {
    	vec2 offset = vec2(x,y);
        float time_delay = length(id + offset);
        float cutoff = .5 + .5*(sin(time_delay - iTime) * .5 + .5);
    	d = xor(d, smoothstep(cutoff, cutoff*.99, len( p - offset , id + offset)) );
    }}
    col.g = mod(d, 3.) - 1.;
    col.b = 1. - mod(d + 1., 3.);
    col.r = 1. - mod(d + 2., 3.);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Brightness Variable
    float brightness = 0.10;

    // Time varying pixel color + add brightness by 10%
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)) + brightness;

    // Output to screen
    fragColor = vec4(col,1.0);
}

float rand (vec2 p)
{
    return fract(sin(dot(p.xy,vec2(12389.1283,8941.1283)))*12893.128933);
}

float noise(in vec2 v)
{
    vec2 i=floor(v);
    vec2 f = fract(v);

    float a = rand(i);
    float b = rand(i+vec2(1,0));
    float c = rand(i+vec2(0,1));
    float d = rand(i+vec2(1,1));
    vec2 u = f*f*(3.0-2.0*f);
    return mix(a,b,u.x)+(c-a)*u.y*(1.0-u.x)+(d-b)*u.x*u.y;
}

float fbm(vec2 p)
{
    float ret = 0.;
    float amp = .4;
    int oct = 8;
    for(int i=0;i<oct;i++)
    {
        ret+=amp*noise(iTime+20.*p);
        p*=2.;
        amp*=.5;
    }

    return ret+.15;
}

float map(in vec3 p)
{    
    return length(p)-1.+cos(sin(iTime-p.y*20.)/5.+sin(iTime-p.z*20.)/5.+sin(iTime-p.x*20.)/5.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.*fragCoord-iResolution.xy)/iResolution.y;


    float time = iTime/4.;
	vec3 ro = vec3(sin(time)*2.,1.,cos(time));
    vec3 ta = vec3(0.0,0.,0.);
    vec3 ww = normalize(ta-ro);
    vec3 uu =normalize(cross(ww, vec3(0,1,0)));
    vec3 vv = normalize(cross(uu,ww));

   	vec3 rd = normalize(p.x*uu+p.y*vv+2.*ww);
	vec3 pos = ro;
    float m = 1000.0;
    float t = 0.0;
    for(int i=0;i<128;i++)
    {
        pos = ro+rd*t;

       	float h = map(pos);
        m= min(m,h);
        if(h<.00001)break;

        t+=h/30.;
    }


    float d = 400.;
    vec3 col =vec3(m)*vec3(fbm(pos.xy/d),fbm(pos.yz/d),fbm(pos.zx/d))*(2.5-(length(p)-.1));
    fragColor = vec4(pow(col,vec3(1.5)),1.0);
}

