vec2 map(vec3 pos)
{
    vec2 t = vec2(0.0, 0.0);

    // ground
    t.x = pos.y;

    // strange shape
    vec3 sp = pos + vec3(1.0, -1.0, 2.0);
    sp.xy *= rot(PI/2.0);

