{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0); 
}

float sdTriPrism( vec3 p, vec2 h )
{
    vec3 q = abs(p);
    return max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
}

float sdSphere(vec3 p, float r)
{
    return length(p)-r;
}

float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdVerticalCapsule( vec3 p, float h, float r )
{
    p.y -= clamp( p.y, 0.0, h );
    return length( p ) - r;
}


float SuitUp(vec3 p)
{
    float t = 0.0;

    p.zx *= rot(PI/4.0);

    p.x += sin(p.y*10.)*0.03; // primitive to cloth

    vec3 bodyP = p + vec3(0.0, 1.6, 0.0);
    float body = sdBox(bodyP, vec3(1.0, 1.0, .2));

    vec3 LarmP = p + vec3(1.35, 2.8, 0.0);
    LarmP.xy *= rot(PI/5.);
    float Larm = sdBox(LarmP, vec3(1.0, 2.0, .2));

    vec3 RarmP = p + vec3(-1.2, 2.85, 0.0);
    RarmP.xy *= rot(PI/3.0);
    float Rarm = sdBox(RarmP, vec3(2.0, 1.0, .2));

    vec3 neckP = p + vec3(0.0, 0.8, 0.0);
    neckP.xy *= rot(PI/3.0);
    float neck = sdTriPrism(neckP, vec2(.5, 1.0));

    vec3 topNeckP = p + vec3(0.0, .8, 0.0);
    vec3 topNeckPsub = topNeckP + vec3(0.0, 0.0, 0.1);
    float topNeck = sdCappedCylinder(topNeckP, .5, .5);
    topNeck = sSDF(sdCappedCylinder(topNeckPsub, .5, .6), topNeck);

    t = uSDF(body, Larm);
    t = uSDF(t, Rarm);
    t = sSDF(neck, t);
    t = uSDF(t, topNeck);

    return t;
}

float head(vec3 p)
{
    float t = 0.0;

    p.zx *= rot(PI/4.0); 
    float yMask = max(p.y*4.0-1.0, 0.0);
    p.z -= sin(iTime+dot(p.x, p.y)*10.)*0.1*yMask; // magic hair

    vec3 sP = p + vec3(0.0, -0.3, 0.0);
    float s = sdVerticalCapsule(sP, 1.0, .5);

    vec3 jawP = sP + vec3(0.0, .35, 0.0);
    float j = sdCappedCylinder(jawP, .5, .4);

    vec3 jawMaskP = jawP + vec3(0.0, 0.0, 0.5);
    float jm = sdSphere(jawMaskP, .4);
    j = opIntersection(j, jm);

    vec3 neckP = sP + vec3(0.0, 1.0, 0.0);
    float n = sdCappedCylinder(neckP, .3, .8);

    t = uSDF(s, j);
    t = uSDF(t, n);

    return t;
}

float map(vec3 pos)
{
    float t = 0.0;

    t = SuitUp(pos);
    t = min(t, head(pos));

    return t;
}

float CastRay(vec3 ro, vec3 rd)
{
    float t = 0.0;

    for(int i=0 ; i<64 ; i++)
    {
        float ray = map(ro + rd * t);

        if(ray < (0.0001*t))
        {
            return t;
        }

        t += ray;
    }

    return -1.0;
}

vec3 GetNormal (vec3 p) 
{ 
    vec2 e = vec2(0.01, 0.0); 
    return normalize(vec3(
        map(p+e.xyy)-map(p-e.xyy),
        map(p+e.yxy)-map(p-e.yxy),
        map(p+e.yyx)-map(p-e.yyx)
        )); 
}

vec3 Render(vec3 ro, vec3 rd, vec2 uv)
{
    float t = CastRay(ro, rd);

    vec3 col = vec3(0.0);

    if(t == -1.0)
    {
        vec2 vignetteUV = uv;
        vignetteUV.y -= 0.2;
        col = vec3(2.0 - length(vignetteUV))*.15;


    }
    else
    {
        float maskY = rd.y*4.0+.5;
        vec3 N = GetNormal(ro+rd*t);
        vec3 L = vec3(1.0, 0.0, 0.0);
        float light = dot(N,L);

        col = mix(vec3(0.0), vec3(N+1.0), max(rd.y*4.0-0.2, 0.0));



        //col += N*max(rd.y*4.0-.5, 0.0)+ .2 * maskY;

    }

    return col;
}

vec3 GetViewDir(vec2 uv, vec3 cp, vec3 ct)
{
    vec3 forward = normalize(ct - cp);
    vec3 right = normalize(cross(vec3(sin(iTime*.5)*.03, -1.0, 0.0), forward));
    vec3 up = normalize(cross(right, forward));

    return normalize(uv.x * right + uv.y * up + 2.0 * forward);
}



