float farClip = 20.0;
float pi = 3.14159;
vec3 black = vec3(0.02);
vec3 red = vec3(0.4, 0.0, 0.0);

float join( float d1, float d2 ) {
	return min(d1,d2);
}

float subtraction( float d1, float d2 ) {
	return max(-d1,d2);
}

float intersection( float d1, float d2 ) {
	return max(d1,d2);
}

float box(vec3 p, vec3 b) {
	vec3 q = abs(p) - b;
	return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float cylinder( vec3 p, vec3 c ) {
	return length(p.xz-c.xy)-c.z;
}

float bigP(vec3 p) {
	vec3 p1 = p + vec3(0.2, 0.1, 0.0);
	float d = box(p1, vec3(0.1, 0.5, 0.1));

	p1 = p + vec3(-0.1, -0.1, 0.0);
	d = join(
		d,
		subtraction(
			cylinder(p1.xzy, vec3(0.0, 0.0, 0.1)),
			intersection (
				cylinder(p1.xzy, vec3(0.0, 0.0, 0.3)),
				box(p1 + vec3(-0.3, 0.0, 0.0), vec3(0.3, 0.3, 0.1))
			)
		)
	);

	d = join(
		d,
		join(
			box(p + vec3(0.0, -0.3, 0.0), vec3(0.1)),
			box(p + vec3(0.0, 0.1, 0.0), vec3(0.1))
		)
	);

	return d;
}

float bigR(vec3 p) {
	float d = bigP(p);

	vec3 p1 = vec3(p.x + p.y / 2.3, p.y, p.z);

	d = join(
		d,
		box(p1 + vec3(-0.05, 0.3, 0.0), vec3(0.105, 0.3, 0.1))
	);

	return d;
}

vec2 map(vec3 p) {
	p = vec3(-p.z, p.y - 0.2, p.x);

	float d = box(p + vec3(0.0, 0.25, 0.0), vec3(0.85, 0.65, 0.1));
	if (d > 0.1) {
		return vec2(d, 0.0);
	}

	float d1 = join(
		bigR(p + vec3(0.45, 0.0, 0.0)),
		bigP(p + vec3(-0.45, 0.0, 0.0))
	);

	float d2 = box(p + vec3(0.4, 0.85, 0.0), vec3(0.35, 0.1, 0.1));

	if (d1 < d2) {
		return vec2(d1, 1.0);
	} else {
		return vec2(d2, 2.0);
	}
}

vec3 calcNormal(vec3 p) {
	vec2 e = vec2(0.0001, 0.0);
	return normalize(vec3(
		map(p + e.xyy).x - map(p - e.xyy).x,
		map(p + e.yxy).x - map(p - e.yxy).x,
		map(p + e.yyx).x - map(p - e.yyx).x));
}

vec2 marchRay(vec3 ro, vec3 rd) {
	float t = 0.0;
	float g = 0.0;
	for(int i = 0; i < 100; i++) {
		vec3 p = ro + t * rd;
		vec2 res = map(p);
		float h = res.x;
		g = res.y;
		if (h < 0.0001) return vec2(t, g);
		t += h;
		if (t > farClip) return vec2(0.0, 0.0);
	}
	return vec2(t, g);
}

float gain(float x, float k) 
{
    float a = 0.5*pow(2.0*((x<0.5)?x:1.0-x), k);
    return (x<0.5)?a:1.0-a;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

	float angle1 = gain(fract(iTime / 5.0), 5.0) * 2.0 * pi;
	vec2 rot = vec2(angle1, 0.0);
	if (iMouse.z > 0.0) {
		rot = 8.0 * (iMouse.xy - 0.5 * iResolution.xy) / iResolution.xy;
	}

	float r = 3.4;

	vec3 ro = vec3(r * cos(rot.x), rot.y, r * sin(rot.x));
	vec3 ta = vec3(0.0, 0.0, 0.0);

	// camera axes
	vec3 ww = normalize(ta - ro);
	vec3 uu = normalize(cross(ww, vec3(0,1,0)));
	vec3 vv = normalize(cross(uu, ww));

	vec3 rd = normalize(p.x*uu + p.y*vv + 1.5*ww);

	vec2 res = marchRay(ro, rd);
	float t = res.x;
	float g = res.y;	

	vec3 col = vec3(1.0);

	if (t > 0.0) {
		col = vec3(0.0);
		vec3 p = ro + t * rd;

		vec3 n = calcNormal(p);
        vec3 l = normalize(vec3(1.0, 0.1, -0.2));
		vec3 r = reflect(-l, n);

		float ndotl = max(dot(n,l), 0.0);
		float rdotv = max(dot(-rd, r), 0.0);

		vec3 alb = black;
		if (g == 2.0) {
			alb = red;
		}

		col = alb * (ndotl * 0.7 + 0.3) + vec3(1.0) * pow(rdotv, 1000.0);
	}

	col = pow(col, vec3(1.0 / 2.2));

    fragColor = vec4(col,1.0);
}

