const float PI = 3.14159265359;

/*************************************************/
/*                                               */
/*            Vector field function              */
/*                                               */
/*************************************************/

vec2 f(vec2 uv)
{ 
    vec2 v = vec2(0.,5.);

    v.x =cos(uv.y) / tan(uv.y);
    v.y =sin(uv.y * uv.x + iTime+length(uv)*5.);

    return v;
}


