//cartesian to poloar coordinate
float xy2polar(vec2 xy)
{
    return atan(xy.x, xy.y);
}

//self explnatory function
float rad2deg(float rad)
{
    return ((rad + PI)/(2.*PI)) * 360.;
}

//for the math being it : https://en.wikipedia.org/wiki/HSL_and_HSV#HSV_to_RGB
vec3 hsv2rgb(float hue)
{
    float hue1 = hue/60.;
    float x = 1. * (1.-abs((mod(hue1,2.))-1.));
    vec3 col = vec3(0.);

    if (hue1 >= 0. && hue1 <= 1.) {
        col.x = 1.;
        col.y = x;
    } else if (hue1 >= 1. && hue1 <= 2.) {
        col.x = x;
        col.y = 1.;
    } else if (hue1 >= 2. && hue1 <= 3.) {
        col.y = 1.;
        col.z = x;
    } else if (hue1 >= 3. && hue1 <= 4.) {
        col.y = x;
        col.z = 1.;
    } else if (hue1 >= 4. && hue1 <= 5.) {
        col.x = x;
        col.z = 1.;
    } else if (hue1 >= 5. && hue1 <= 6.) {
        col.x = 1.;
        col.z = x;
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    //image zoom
    uv *= 10.;

    //applying vector function
    vec2 v = f(uv);

    //coloring
    float phi = xy2polar(v);
    float deg = rad2deg(phi);
    vec3 polarColor = hsv2rgb(deg);
    vec3 col = vec3(polarColor);

    fragColor = vec4(col,1.0);
}

float DistLine(vec2 p, vec2 a, vec2 b) {

    vec2 ap = p - a;
    vec2 ab = b - a;

    float t = clamp(dot(ap, ab) / dot(ab, ab), 0.0, 1.0);

    return length(ap - ab * t);

}

float N21(vec2 p) {

 	p = fract(p * vec2(233.34, 851.73));
    p += dot(p, p+23.45);

    return fract(p.x * p.y);

}

vec2 N22(vec2 p) {

	float n = N21(p);

    return vec2(n, N21(p + n));

}

vec2 GetPos(vec2 id, vec2 offset) {

    vec2 n = N22(id + offset) * iTime;

    return offset + sin(n) * 0.4;

}

float Line(vec2 p, vec2 a, vec2 b) {

    float d = DistLine(p, a, b);
    float m = smoothstep(0.03, 0.01, d);

    m *= smoothstep(1.2, 0.8, length(a - b));

    return m;

}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;
    float m = 0.0;

    uv *= 5.0;

    vec2 gv = fract(uv) - 0.5;
    vec2 id = floor(uv);

    vec2 p[9];

    int i = 0;
    for (float y = -1.0; y <= 1.0; y++) {
    	for (float x = -1.0; x <= 1.0; x++) {
            vec2 offset = vec2(x, y);
    		p[i++] = GetPos(id, offset);
    	}
    }

    float t = iTime * 10.0;
    for (int i = 0; i < 9; i++) {
    	m += Line(gv, p[4], p[i]);

        vec2 j = (p[i] - gv) * 20.0;
        float sparkle = 0.5 / dot(j, j);

        m += sparkle * (sin(t + p[i].x * 5.0) * 0.5 + 0.5);
    }

    m += Line(gv, p[1], p[3]);
    m += Line(gv, p[1], p[5]);
    m += Line(gv, p[7], p[3]);
    m += Line(gv, p[7], p[5]);

    vec3 col = vec3(m);

    // if (gv.x > 0.48 || gv.y > 0.48) col = vec3(1, 0, 0);

    fragColor = vec4(col, 1.0);

}

#define PI 3.1415926535897932384626433832795
#define RENDER_DEPTH 800
#define CLOSE_ENOUGH 0.00001

#define GRADIENT(pt, func) vec3( \
    func(vec3(pt.x + 0.0001, pt.y, pt.z)) - func(vec3(pt.x - 0.0001, pt.y, pt.z)), \
    func(vec3(pt.x, pt.y + 0.0001, pt.z)) - func(vec3(pt.x, pt.y - 0.0001, pt.z)), \
    func(vec3(pt.x, pt.y, pt.z + 0.0001)) - func(vec3(pt.x, pt.y, pt.z - 0.0001)))

//Array of lights in our scene
const vec3 LIGHT_POS[] = vec3[](vec3(5.0));

//---------------------------------------------------------------------------------------
// Transformations
//---------------------------------------------------------------------------------------

mat4 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);

    return mat4(
        vec4(c, s, 0, 0),
        vec4(-s, c, 0, 0),
        vec4(0, 0, 1.0, 0),
        vec4(0, 0, 0, 1.0)
    );
}

mat4 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);

    return mat4(
        vec4(c, 0, s, 0),
        vec4(0.0, 1.0, 0, 0),
        vec4(-s, 0, c, 0),
        vec4(0, 0, 0, 1.0)
    );
}

//---------------------------------------------------------------------------------------
// Signed Distance Functions
//---------------------------------------------------------------------------------------

float sphere(vec3 p, float r) {
    return length(p) - r;
}

float cone(vec3 p, float alpha)
{
 	return (sin(alpha)*length(p.xy) + cos(alpha)*p.z);   
}

float scene(vec3 p) {
    return cone(p, 2.0);
}

//---------------------------------------------------------------------------------------
// Auxiliary helper functions
//---------------------------------------------------------------------------------------

vec3 getNormal(vec3 pt) {
    return normalize(GRADIENT(pt, scene));
}

//---------------------------------------------------------------------------------------
// Illumination and Shading
//---------------------------------------------------------------------------------------

float shade(vec3 eye, vec3 pt, vec3 n) {
    float val = 0.0;

    //Ambient Lighting
    val += 0.2;

    //Cast ray towards our light sources and add it to the illumination depending
    //on the angle with the normal to the surface
    for (int i = 0; i < LIGHT_POS.length(); i++) {
        vec3 l = normalize(LIGHT_POS[i] - pt); 
        val += max(dot(n, l), 0.0);
    }

    return val;
}

vec3 illuminate(vec3 camPos, vec3 rayDir, vec3 pt) {
    vec3 c, n;
    //Using the SDF normal approximation
    n = getNormal(pt);

    //Everything is white
    c = vec3(1.0);
    return shade(camPos, pt, n) * c ;
}

//---------------------------------------------------------------------------------------
// Ray Marching Algorithm
//---------------------------------------------------------------------------------------

vec3 castRay(vec3 camPos, vec3 rayDir) {
    //Using the ray marching approach
    int step = 0;
    float t = 0.0;

    for (float d = 1000.0; step < RENDER_DEPTH && abs(d) > CLOSE_ENOUGH; t += abs(d)) {
        d = scene(camPos + t * rayDir)/2.0;
        step++;
    }

    if (step == RENDER_DEPTH) {
        return vec3(0.0, 0.0, 0.0); //getBackground(rayDir);
    } else {
        return illuminate(camPos, rayDir, camPos + t * rayDir);
    }
}

//---------------------------------------------------------------------------------------
// Set-up
//---------------------------------------------------------------------------------------

mat3 setCamera(in vec3 origin, in vec3 target, float rotation) {
    //The direction in which the camera is pointing
    vec3 forward = normalize(target - origin);
    vec3 orientation = vec3(sin(rotation), cos(rotation), 0.0);
    vec3 left = normalize(cross(forward, orientation));
    vec3 up = normalize(cross(left, forward));
    return mat3(left, up, forward);
}

//---------------------------------------------------------------------------------------
// Output
//---------------------------------------------------------------------------------------

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    //Normalize the 2d coordinates [-1, 1]
    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;

    //Fix the x axis to original resolution
    p.x *= iResolution.x / iResolution.y;

    //Setting the camera - origin, lookat
    vec3 origin = vec3(10.0*sin(iTime), 1.8, 10.0*cos(iTime));
    vec3 look = vec3(0.0);


    mat3 camera = setCamera(origin, look, 0.0);
    vec3 direction = camera * normalize(vec3(p.xy, 2.0));

    vec3 color = castRay(origin, direction);

    fragColor = vec4(color, 0.0);
}

float mandelbrot( vec2 c, vec2 offset )
{
    // Camera
    c += offset / iResolution.xy;
    c.x -= .65;
    c /= exp( .3*iTime );
    c += vec2( .34848, .510404 );

    // Iteration of z = z^2 + c
    vec2 z = vec2( 0 );
    float i = 0.;
    for( ; i < 150.; ++i ) {
        z = vec2(
            z.r*z.r - z.g*z.g + c.r,
            2.*z.r*z.g + c.g
        );  
        if (length(z) > 40.) break;
    }

    if (i == 150.) return 0.;

    i = i - log(length(z)) / log(40.);

    return i / 150.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 3.*( fragCoord/iResolution.yy - vec2(.5 * iResolution.x/iResolution.y, .5) );

    // MSAA
    float a = mandelbrot(uv, vec2(-.75, .25));
    float b = mandelbrot(uv, vec2(-.25,-.75));
    float c = mandelbrot(uv, vec2( .25, .75));
    float d = mandelbrot(uv, vec2( .75,-.25));

    fragColor = 0.25 * vec4(a + b + c + d);
}

