f=fract(f);
l=length(vec2(length(f.rg),f.b)-.5);
f=f.gbr-vec3(1,1,0);
l=min(l,length(vec2(length(f.rg),f.b)-.5));
f=f.gbr-vec3(-1,1,-1);
return min(l,length(vec2(length(f.rg),f.b)-.5))-.1;
}

