#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01

float GetDist(vec3 p) {
    float y = abs(sin(iTime * 5.)) + 1.;
    float x = sin(iTime);
    float z = cos(iTime) + 6.;
    vec4 sphere = vec4(x, y, z, 1);
    float dS = length(p - sphere.xyz)-sphere.w;
    float dP = p.y;
    float d = min(dS,dP);
    return d;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p);
    vec2 e = vec2(.01, 0);
    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}   

float RayMarch(vec3 ro, vec3 rd) {    
    float dO = 0.;

    for (int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if (dO > MAX_DIST || dS < SURF_DIST) break;
    }

    return dO;
}

float GetLight(vec3 p) {
    vec3 lightPos = vec3(2, 4, 0);
    lightPos.x += sin(iTime);
//    vec3 lightPos = vec3(0, 10, 6);
//    lightPos.xz += vec2(sin(iTime), cos(iTime)) * 2.;
    vec3 lightV = normalize(lightPos - p);
    vec3 normal = GetNormal(p);

    float dif = clamp(dot(normal, lightV), 0., 1.);

    float d = RayMarch(p+normal*SURF_DIST*2., lightV);
    if (d < length(lightPos - p)) dif *= .1;

    return dif;

}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) /iResolution.y;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 1, 0);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    float d = RayMarch(ro, rd);
    vec3 p = ro + rd * d;

    float diffuseL = GetLight(p);

    col = vec3(diffuseL);

    fragColor = vec4(col,1.0);
}

#define MAX_DIST 100.
float g;

mat2 rot(float x)
{
    float s = sin(x);
    float c = cos(x);
    return mat2(c, -s, s, c);
}

float smin(float d1, float d2, float k)
{
    float h = clamp(0.5+0.5*(d2-d1)/k,0.0,1.0);
    return mix(d2,d1,h)-k*h*(1.0-h);
}

float box(vec3 p, vec3 b)
{
  vec3 q = abs(p) - b;
  return min(max(q.x,max(q.y,q.z)),0.0)+length(max(q,0.0));
}

vec2 map(vec3 p)
{
    float m=0.;
    float d = 3.;
    float r = 1000.;
    float pr = .3;
    for(float i=0.;i<6.;i++)
    {
        p.yz*=rot(iTime/2.*pr+.1);
        pr+=.15;
        p.z+=1.5;
        p.x-=1.5;
        p.y+=1.;
        p=abs(p);
        r=smin(r,length(p)-d,.7);

        bool ev = mod(i,2.)==0.;

        if(ev)
        r=smin(r,length(p-abs(vec3(1.5+cos(iTime)*pr)))-d/3.,.7);
        p.xy *=rot(.5);


        if(mod(i,2.)!=0.)
        r=smin(r,box(p,vec3(1,2.+cos(iTime/4.)*11.,1)),.6);
    }
    g+=0.01/(0.01+r*r);
    return vec2(r,m);
}

vec2 ray(vec3 ro, vec3 rd)
{
    float t =0., m=0.;

    for(int i=0;i<100;i++)
    {

        vec3 p = ro+rd*t;
        vec2 s = map(p);
        m=s.y;
        if(s.x<0.00001)break;
        t+=s.x;
        if(t>MAX_DIST){t=-1.;break;}

    }

    return vec2(t,m);
}

vec3 normal(vec3 p)
{
    vec2 e = vec2(0.0005,0.);
    return normalize(vec3(
        map(p+e.xyy).x-map(p-e.xyy).x,
        map(p+e.yxy).x-map(p-e.yxy).x,
        map(p+e.yyx).x-map(p-e.yyx).x
        ));
}

void mainImage( out vec4 c, in vec2 f )
{
    vec2 uv = (2.*f-iResolution.xy)/iResolution.y+vec2(.1,0);
	float d = 22.5;
    vec3 ro = vec3(d,0,d);
    vec3 ta = vec3(0,0.,0);
    vec3 cf = normalize(ta-ro);
    vec3 cu = normalize(cross(cf,vec3(0,1,0)));
    vec3 cr = normalize(cross(cu,cf));
    vec3 rd = normalize(uv.x*cu+uv.y*cr+2.*cf);

    vec2 r = ray(ro,rd);
    vec3 p = ro+rd*r.x;
    vec3 col = vec3(1.);

    vec3 sun = normalize(ro);
    float dif = clamp(dot(normal(p),sun),0.0,1.0);

    if(r.x>0.)col=vec3(1.0)*dif;
    else col/=g;
    c.rgb=col;
}

