const float esc = 20.0;
const int depth = 420;
const float p = 30.0;

vec3 mandel(vec2 z0)
{
    float k = 0.0;
    vec2 z = vec2(0.0);
    for(int i = 0; i < depth; ++i)
    {
        vec2 z1 = vec2(abs(z.x), abs(z.y));
        z = vec2(z1.x*z1.x-z1.y*z1.y, z1.x*z1.y*2.0) + z0;
        if (length(z) > esc)
            break;
        k += 1.0;
    }
    float mu = k + 1.0 - log2(log(length(z)));
    return sin(mu*0.1 + vec3(0.0,0.5,1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
