        t = p - t;
    uv *= vec2(1.0, -1.0) * 3.0;
    uv += vec2(-1.0, 1);
    float scale = 0.5 + pow(2.0, t);
    vec2 offset = vec2(-1.76, -0.025);
    uv += offset*scale;
    uv /= scale;
    fragColor = vec4(mandel(uv), 1.0);
}

