vec3 rotate(vec3 p, float angle, vec3 axis){
    vec3 a = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float r = 1.0 - c;
    mat3 m = mat3(
        a.x * a.x * r + c,
        a.y * a.x * r + a.z * s,
        a.z * a.x * r - a.y * s,
        a.x * a.y * r - a.z * s,
        a.y * a.y * r + c,
        a.z * a.y * r + a.x * s,
        a.x * a.z * r + a.y * s,
        a.y * a.z * r - a.x * s,
        a.z * a.z * r + c
    );
    return m * p;
}

float df1( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float df2( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz) - t.x, p.y);
  return length(q)-t.y;
}

float df3( vec3 p, float s )
{
  return length(p)-s;
}

float map(vec3 p , float s){

    p.xy = mod(p.xy, 10.) - 1. * 5.;
    return df3(p, s);
}

float dfm(vec3 p, float s){

    p.y -= 0.8;
    vec3 q = rotate(p, radians(iTime * 5.0)*10., vec3(0.0, 1.0, 0.05));
    //vec3 q2 = rotate(p, radians(5.0)*20., vec3(0.0, 1.0, 0.1));
    float d3 = df3(q, 2.0);
    float d4 = df2(q, vec2(3.5, 0.07));
    float m2 = min(d3, d4);
    p.y += 3.0;
    float d1 = df1(p, vec3(2., 0., 2.), 0.3);
    float d2 = df3(p, 2.0);
    float m =  max(d1, d2);
    return min(m, m2);
    //return max(-d1, d2); // d1が重なっていないd2部分を描く
    //return max(d1, -d2); // d2が重なっていないd1部分を描く


}


int dfc(vec3 p, float s){

    p.y -= 0.8;
    vec3 q = rotate(p, radians(iTime * 5.0)*10., vec3(0.0, 1.0, 0.05));
    //vec3 q2 = rotate(p, radians(5.0)*20., vec3(0.0, 1.0, 0.1));
    float d3 = df3(q, 2.0);
    float d4 = df2(q, vec2(3.5, 0.1));
    float m2 = min(d3, d4);
    p.y += 3.0;
    float d1 = df1(p, vec3(2., 0., 2.), 0.3);
    float d2 = df3(p, 2.0);
    float m =  max(d1, d2);
   float mm =  min(m, m2);


    int color = d4 < d3 ? 0 : 
    				   mm < d3 ? 1 : 2;
    return color;
}

vec3 normal(vec3 p, float s){

    vec2 e = vec2(0.0001, 0);
    float d = dfm(p, s);
    vec3 n = d - vec3(
        dfm(p - e.xyy, s),
        dfm(p - e. yxy, s),
        dfm(p - e. yyx, s));
    return normalize(n);
}

