const vec3 colorf = vec3(.0);
const vec3 colorf2 = vec3(.8, .3, .2);
const vec2 zoompoint = vec2(-0.77568377, 0.13646737);

vec3 hsv2rgb( float c )
{
    vec3 rgb = clamp( abs(mod(c*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	return rgb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 position = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.y; 
    highp float zoom = 1.0;

    //highp float tz = 0.5 * (1. - cos(iTime * 0.5));
    //zoom = pow(2.2, 15.0 * tz);
    highp float real = position.x * 2.7 / zoom;
    highp float imag = position.y * 2.7 / zoom;    
    //real += zoompoint.x;
	//imag += zoompoint.y;

    //mandelbrot set
    highp vec2 z = vec2(0.0);
    highp vec2 dz = vec2(0.0);
    highp vec2 c = vec2(real, imag);// + vec2(sin(iTime), cos(iTime));
    highp float rz = 0.0;
    highp float rdz = 0.0;
    int nri = 0;

    //julia set
	z = vec2(real, imag);
	c = vec2(-.4, -.6) + vec2(sin(iTime * fract(position.x * 10000.)), 2.56 * cos(iTime * .5));


    //int nri;
    for (int i = 0; i < 500; i++)
    {
        if(rz > 1024.0)
            break;

        // Z' -> 2*Z*Z' + 1
        dz = 2.0 * vec2(z.x * dz.x - z.y * dz.y, z.x * dz.y + z.y * dz.x) + vec2(1.0, 0.0);

        // Z -> Z^2 + c			
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;

        nri = i;

        rz = dot(z, z);	//is the modulo squared	
    }

    //https://www.mi.sanu.ac.rs/vismath/javier/b3.htm (COLORING ALGORITHMS)
    rdz = dot(dz, dz);
    highp float d = sqrt(rz / rdz) * log(rz);
    highp vec3 color;
    if (rz < 4.0)
        color = colorf;
    else {
        float nic = float(nri) + 1. - log2(log2(rz));					//normalized iteration count
        //color = hsv2rgb((nic * .015));								//multicolored
        color = .5 + .5 * cos(3.0 + nic * .2 + vec3(0.0,0.6,1.0));	//like on wiki?
        //color = 0.2 + mix(colorf, colorf2, fract(float(nri) * .02));	//(discrete) escape time coloring: Base the color on the number of iterations
       // d = clamp(pow(4.0 * d, 0.3), 0.0, 10.0);						//(~continuous: if the bailout is larger then it is smoother) dem based on the Hubbard-Douady potential
        //color = 0.5 + 0.5 * cos(colorf2 + hsv2rgb(d) * 2.0);
    }

    // Output to screen
    fragColor = vec4(color, 1.0);
}

// Thank you Dave_Hoskins for the hash! <3
vec2 hash22(vec2 p)
{
	uvec2 q = uvec2(ivec2(p))*uvec2(1597334673U, 3812015801U);
	q = (q.x ^ q.y) * uvec2(1597334673U, 3812015801U);
	return vec2(q) * (1.0 / float(0xffffffffU));
}

float worley(vec2 uv)
{
    uv *= 5.;
    uv += iTime*.25;

    vec2 id = floor(uv);
    vec2 gv = fract(uv);

    float minDist = 100.;
    for (float y = -1.; y <= 1.; ++y)
    {
        for(float x = -1.; x <= 1.; ++x)
        {
            vec2 offset = vec2(x, y);
            vec2 h = hash22(id + offset) * .8 + .1; // .1 - .9
    		h = (((sin(h*iTime)+1.)*.5)*.8+.1) + offset;
            float p = length(gv - h);
            if (p < minDist)
            {
                minDist=p;
            }
        }
    }

    return minDist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspectRatio = iResolution.x/iResolution.y;
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= aspectRatio;

    vec3 col = vec3(0.);
    float w = worley(uv);
    col += 1.-w;
    col.r *= smoothstep(1.7, .0, length(uv-(sin(vec2(.7, .5)+iTime)+1.)*.5));
    uv.x = 1.2-uv.x;
    col.g *= smoothstep(1.7, .0, length(uv-(cos(.5+iTime)+1.)*.5));
    col.b *= 1.-sin(col.r+col.g);

    fragColor = vec4(col, 1.0);
}

float f(vec2 uv) {
    float t = atan(uv.y, uv.x);
    float r = length(uv);
    if(t > .0)
    	t = -t;
 	return r - (.6 + sin(t) * sin(t + .75) + fract(sin(t*.8)) + cos(t) - .6*(pow(abs(cos(t)), 4.)));       
}

vec2 grad2(vec2 uv) {
 	vec2 h = vec2( 0.001, 0.0 );
    return vec2(f(uv+h.xy) - f(uv-h.xy),
                f(uv+h.yx) - f(uv-h.yx)) / (2.0*h.x);
}
float sdf(vec2 uv, vec2 g) {
 	float v = f(uv);
    float de = v/length(g);
    return de; 
}

