#define CCOUNT 4.0
#define CELL_FILL .95  

vec2 AgridID(vec2 uv)
{
    uv.y /=0.5;

    return vec2(floor(uv));
}

vec2 BgridID(vec2 uv)
{
    uv.y /= 0.5;

    return vec2(floor((uv-0.5)));
}

vec2 AgridUV(vec2 uv, float cells)
{
	return vec2(mod(uv.x, 1.0), mod(uv.y*2.0, 1.0));
}

vec2 BgridUV(vec2 uv, float cells)
{
	return vec2(mod(uv.x-0.5, 1.0), mod((uv.y*2.0)-0.5, 1.0));
}

float los( vec2 pos, vec2 s ) 
{
    vec2 abspos = abs(pos - vec2(0.5,0.5));

    return sign( abspos.x*s.y + abspos.y*s.x - s.x*s.y );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 mouseUV = iMouse.xy/iResolution.xy;

    vec2 ar = vec2(1.0, iResolution.y/iResolution.x);

    float CELLS = iTime*CCOUNT + CCOUNT;

    mouseUV *= ar;
    mouseUV *= CELLS;

    uv *= ar;
    uv *= CELLS;

    vec2 aguv = AgridUV(uv,CELLS);
    vec2 bguv = BgridUV(uv,CELLS);

    //Just to make circles render within cells instead of ovals
    //aguv.y *= ar.y;
    //aguv.y += 0.5 * ar.y;

    //bguv.y *= ar.y;
    //bguv.y += 0.5 * ar.y;


    //vec3 acol = vec3(floor(1.335 - distance(aguv - 0.5, vec2(.0,.0))));
    //vec3 bcol = vec3(floor(1.335 - distance(bguv - 0.5, vec2(.0,.0))));

    //vec3 acol = vec3(aguv.x, aguv.y, 0.0);
    //vec3 bcol = vec3(bguv.x, bguv.y, 0.0);

    float alos = 1.0-los(aguv, vec2(0.5*CELL_FILL));
    float blos = 1.0-los(bguv, vec2(0.5*CELL_FILL));

    vec2 agid = AgridID(uv);
    vec2 bgid = BgridID(uv);

    vec2 amgid = floor(AgridID(mouseUV));
    vec2 bmgid = floor(BgridID(mouseUV));

    vec3 acol = vec3(alos);
    vec3 bcol = vec3(blos);

    vec3 col = max(acol,bcol);
    //vec3 col = acol;

    vec3 overlay = vec3(1.0,0.0,0.0);
    vec3 overlay2 = vec3(0.0,1.0,0.0);

    if(agid == amgid)
    {
        if(alos >= 1.0)
        	col*=overlay;
    }

    if(bgid == bmgid)
    {
        if(blos >= 1.0)
        	col*=overlay2;
    }

    if(bgid.x < 0.0 || bgid.y < 0.0)
    {
        col *= 1.0-blos;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

