const vec3 lightDir = vec3(-0.577, 0.577, 0.577);
const int accuracy = 16;

vec3 trans(vec3 p){
    // return p;
    return mod(p, 4.0) - 2.0;
}

vec3 rotate(vec3 p, float angle, vec3 axis){
    vec3 a = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float r = 1.0 - c;
    mat3 m = mat3(
        a.x * a.x * r + c,
        a.y * a.x * r + a.z * s,
        a.z * a.x * r - a.y * s,
        a.x * a.y * r - a.z * s,
        a.y * a.y * r + c,
        a.z * a.y * r + a.x * s,
        a.x * a.z * r + a.y * s,
        a.y * a.z * r - a.x * s,
        a.z * a.z * r + c
    );
    return m * p;
}

float distanceCube(vec3 p){
    // p = rotate(p,iTime,vec3(0.0,0.0,1.0));
	vec3 e = trans(p);
    // e = rotate(e,iTime,vec3(0.0,0.0,1.0));

    vec3 q = abs(e);
    vec3 cube = vec3(1.5,1.2,0.5);

    return length(max(q - cube, 0.0));
}

vec3 getNormal(vec3 p){
    float d = 0.0001;
    return normalize(vec3(
        distanceCube(p + vec3(  d, 0.0, 0.0)) - distanceCube(p + vec3( -d, 0.0, 0.0)),
        distanceCube(p + vec3(0.0,   d, 0.0)) - distanceCube(p + vec3(0.0,  -d, 0.0)),
        distanceCube(p + vec3(0.0, 0.0,   d)) - distanceCube(p + vec3(0.0, 0.0,  -d))
    ));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy*2.0-iResolution.xy) / min(iResolution.x,iResolution.y);

    // camera
    vec3 cPos = vec3(0.0,  0.0,  2.0);
    vec3 cDir = vec3(0.0,  0.0, -1.0);
    vec3 cUp  = vec3(0.0,  1.0,  0.0);
    vec3 cSide = cross(cDir, cUp);
	float targetDepth = 1.2;     

    cPos.z = cPos.z - iTime;

    vec3 ray = normalize(cSide * uv.x + cUp * uv.y + cDir * targetDepth);

    // marching loop
    float distance = 0.0;
    float rLen = 0.0;
    vec3  rPos = cPos;
    for(int i = 0; i < accuracy; i++){
        distance = distanceCube(rPos);
        rLen += distance;
        rPos = cPos + ray * rLen;
    }

    // Output to screen
    if(abs(distance) < 0.001){
        vec3 normal = getNormal(rPos);
        float diff = clamp(dot(lightDir, normal), 0.5, 1.0);
        fragColor = vec4(vec3(diff), 1.0);
    }else{
        fragColor = vec4(vec3(0.2),1.0);
    }
}

//Based on code and formulas from The Book of Shaders:
//https://thebookofshaders.com/07/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy / iResolution.xy;
    vec3 color = vec3(0.0);

    vec2 borders1   = step(vec2(0.1), st);
    vec2 borders2   = step(vec2(0.1), 1.0 - st);
    float pct = borders1.x * borders1.y * borders2.x * borders2.y;

    if (pct == 1.0)
    {
        vec2 st2 = st * 2.0 - 1.0;
        float d = length(max(abs(st2) - 0.7, 0.0) );
        color += vec3(fract(d * 15.0));

        vec2 pos = vec2(0.5) - st;
        float a = atan(pos.y, pos.x) * 2.0;

        color += vec3(cos(a), sin(a * iTime * 0.1), sin(iTime * 0.1));
    }
    else
    {
    	color = vec3(pct);
    }

    fragColor = vec4(pow(color, vec3(0.4545)), 1.0);
}

#define MAX_STEPS 1000
#define MAX_DIST 100.0
#define SURF_DIST 0.0001

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float GetDist(vec3 p) {
    vec4 s = vec4(0.0, 1.0, 6.0, 1.0);
	s.y += sin(iTime);

    float sphereDist = length(p - s.xyz) - s.w;
    float planeDist = p.y;

    float d = smin(sphereDist, planeDist, 0.8);
    return d;

}

float RayMarch(vec3 ro, vec3 rd) {
	float dO = 0.0;

    for (int i = 0; i < MAX_STEPS; i++) {
    	vec3 p = ro + rd * dO;
        float dS = GetDist(p);
        dO += dS;

        if (dO > MAX_DIST || dS < SURF_DIST) {
            break;
        }
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(0.01, 0);

    vec3 n = d - vec3(
    	GetDist(p - e.xyy),
        GetDist(p - e.yxy),
        GetDist(p - e.yyx)
    );

    return normalize(n);
}


float GetLight(vec3 p) {
	vec3 lightPos = vec3(0.0, 5.0, 6.0);
    lightPos.xz += vec2(sin(iTime), cos(iTime)) * 2.0;
    vec3 l = normalize(lightPos - p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0.0, 1.0);

    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.0);

    vec3 ro = vec3(0.0, 1.0, 0.0);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1.0));

    float d = RayMarch(ro, rd);

    vec3 p = ro + rd * d;

    float dif = GetLight(p);
    col = vec3(dif);


    fragColor.rgb = col;
    fragColor.a = 1.0;
}

const float pi = 4. * atan(1.);

float phi(float x) {
	float s = sin(pi * x);
    return s*s;
}

void mainImage( out vec4 O, in vec2 U )
{
    vec2 p = U / iResolution.x;

    p -= vec2 (0.5, 0.3);
    p *= 8.;

    vec2 pm = iMouse.xy / iResolution.x;

    // heart-shaped polar curve :
    // x = sin^3(t)
    // y = cos(t) - cos^4(t)

    float sc = 2.;
    int nb_pts = 50;
    float dt = 2. * pi / float (nb_pts);

    O = vec4(0.);

    for (int k=0; k < nb_pts; k++) {
        float t = sc * (iTime + float(k) * dt);
        float c = cos(t), s = sin(t);
        vec2 m = (1. + phi(0.7 * iTime) / 2.) * vec2(1.5 * s*s*s, 0.5 + c - c*c*c*c);
        float e = smoothstep (.1, .0, length(p-m));

        // blending between red and blue suggests that
        // blood's color depends on whether it enters or
        // exits the heart.

        O += vec4(mix (vec3(e, 0., 0.), vec3(0., 0., e), (1. + s) / 2.), 1.);
    }
}

//++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
// Temporal Golden Noise Demo by Bapho
//++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++

float noise = 2.0;

//----------------------------------------------------------------------
precision lowp float;
float PHI = 1.61803398874989484820459 * 00000.1; // Golden Ratio   
float PI  = 3.14159265358979323846264 * 00000.1; // PI
float SQ2 = 1.41421356237309504880169 * 10000.0; // Square Root of Two


float gold_noise(in vec2 coordinate, in float seed){
    return fract(tan(distance(coordinate*(seed+PHI), vec2(PHI, PI)))*SQ2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float mouse;
    if (iMouse.x <= 0.0 || iMouse.x >= iResolution.x){
		mouse = iResolution.x / 2.0;
    } else {
        mouse = iMouse.x;
    }

    if (mouse > fragCoord.x - 4.0 && mouse < fragCoord.x + 4.0){
        fragColor = vec4(0.4, 0.0, 0.0, 1.0);
    } else {

        vec2 uv = fragCoord/iResolution.xy;

        vec3 color;
        if (fragCoord.x < iResolution.x * 0.05
            || fragCoord.x > iResolution.x * 0.95){
            color = vec3(0.0);

        } else if (fragCoord.y < iResolution.y * 0.15){
        	color = texture(iChannel1, uv).rgb;

        } else if (fragCoord.y < iResolution.y * 0.85){
            color = texture(iChannel0, uv).rgb;

        } else {
            color = 0.5 + 0.25*cos(iTime*0.5+uv.xyx+vec3(0,4,8));
            //color = vec3(0.0, 0.8, 0.2);
        }

        if ((mouse <= 0.0 && fragCoord.x < iResolution.x / 2.0) 
				|| mouse > fragCoord.x){

            float uniquePos = (iResolution.x * fragCoord.y) + fragCoord.x;
            float amount = noise * 0.08;

            // a very low unique seed will lead to slow noise
            // pattern changes on slow moving color gradients
            float uniqueSeed = uniquePos * 0.000000001 * iTime;

            // using a fictive coordinate as a workaround to fix a pattern bug
			vec2 coordinate = vec2(fragCoord.x, fragCoord.y * 1.001253543);

            // average noise luminance to subtract
            float sub = (0.5 * amount);

            // calculating and adding/subtracting the golden noise
            float ran = gold_noise(coordinate, uniqueSeed);
            float add = clamp(ran * amount, 0.0, 1.0);
            color += (add - sub);
        }

        fragColor = vec4(color, 1.0);
    }
}

float circle(vec2 positionFragment,vec2 positionObject,float radius){
    return length(positionFragment-positionObject) - radius;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //from -1 to 1
    vec2 pos=( 2.*fragCoord - iResolution.xy )/iResolution.y;
    vec3 col=vec3(0.);
    for(int i=0;i<9;i+=1){
        float r=radians(float(i+1)*360.*iTime*.1);
        vec2 circlePosition=vec2(cos(r),sin(r))*.5;
        //red or green or blue
        col[i%3]+=step(0.,-circle(pos,circlePosition,0.5));
    }

    //repeat
    col=mod(col,2.0);
    //fold
    col=1.0-abs(1.0-col);

    // Output to screen
    fragColor = vec4(col,1.0);
}

float phase(vec2 pos)
{
    //number of windings, essentially the velocity
    float n = 5.; //necessarily integer for the wave function to be continuous, thus vorticity is quantized
	return n*( 0.5*iTime + atan(pos.x, pos.y));
}

vec2 vortex_psi(vec2 pos)
{   
    //it is zero at pos = 0, so it is continuos, even if atan(pos.x, pos.y) aka the phase is not
    return 0.5*length(pos)*vec2(sin(phase(pos)), cos(phase(pos)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    if(mod(iTime,4.) < 2.)
    {
        //the wave function
        vec2 psi = vortex_psi(4.*(uv - vec2(0.5)));

        vec2 red = vec2(1,0), green = vec2(-0.5,sqrt(3.)*0.5), blue = vec2(-0.5,-sqrt(3.)*0.5);

        vec3 RGB =  (0.5 + vec3(dot(psi, red),dot(psi, green),dot(psi, blue)))/1.5;
        // Output to screen
        fragColor = vec4(RGB*pow(abs(RGB),vec3(0.2)),1.0);
    }
    else
    {
         //the phase
         float phase = mod(phase(4.*(uv - vec2(0.5))),2.*3.14159); //it is periodic
          // Output to screen
         fragColor = vec4(vec3(phase)/(2.*3.14159),1.0);
    }

}

// SHADERTOBER 15 Legend
// Poulet vert 15-10-2019
// used iq menger spone code for fractal crap in the background

#define PI 3.14159

mat2 rot (float a) { float c=cos(a),s=sin(a); return mat2(c,-s,s,c); }

float uSDF(float s1, float s2) { return min(s1, s2);}

float sSDF( float d1, float d2 ) { return max(-d1,d2); }

float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0); 
}

float sdSphere(vec3 p, float r)
{
    return length(p)-r;
}

float sdBox( vec3 p, float s )
{
  vec3 q = abs(p) - vec3(s);
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdCross( vec3 p )
{
  float da = sdBox(p.xyz, vec3(3.,1.0,1.0));
  float db = sdBox(p.yzx, vec3(1.0,3.,1.0));
  float dc = sdBox(p.zxy, vec3(1.0,1.0,3.));
  return min(da,min(db,dc));
}

vec3 opRep(vec3 p, vec3 c)
{
     return mod(p+0.5*c,c)-0.5*c;
}

float opU(float d1, float d2)
{
    return min(d1, d2);
}

vec2 opU2( vec2 d1, vec2 d2 )
{
    return (d1.x < d2.x) ? d1 : d2;
}

float sdTriPrism( vec3 p, vec2 h )
{
    vec3 q = abs(p);
    return max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
}

// Scene setup

vec2 map(vec3 pos)
{

    vec2 t = vec2(pos.y, 0.0);

    vec3 cp = pos + vec3(0.0, -1.0, 0.0);
    cp = opRep(cp, vec3(1.0));
    float c = sdBox(cp, vec3(1.0));

    float s = 1.0;
    for(int m=0; m<3; m++)
    {
        vec3 p = pos + vec3(0.0, iTime, 0.0);
        vec3 a = mod(p*s, 2.0)-1.0;
        s*= 3.1;
        vec3 r = 1.0 - 3.0 * abs(a);

        float cro = sdCross(r)/s;

        c = max(c, -cro);
    }

    float subBox = sdBox(pos+vec3(0.0, -1.2, 0.0), .5);
    c = max(c, -subBox);

    vec2 shape = vec2(c, 1.0);

    vec3 trip = pos + vec3(0.21, -1.0, 0.0);
    float tri = sdTriPrism(trip, vec2(.25, .1));
    trip = pos + vec3(-0.21, -1.0, 0.0);
    tri = opU(tri, sdTriPrism(trip, vec2(.25, .1)));
    trip = pos + vec3(-0.0, -1.37, 0.0);
    tri = opU(tri, sdTriPrism(trip, vec2(.25, .1)));

    t = shape;
    t = opU2(t, vec2(tri, 2.0));

    return t;
}

vec2 CastRay(vec3 ro, vec3 rd)
{
    float t = 0.0;

    for(int i=0 ; i<128 ; i++)
    {
        vec2 ray = map(ro + rd * t);

        if(ray.x < (0.0001*t))
        {
            return vec2(float(i)/128., ray.y);
        }

        t += ray.x;
    }

    return vec2(-1.0, 0.0);
}

vec3 Render(vec3 ro, vec3 rd, vec2 uv)
{
    vec2 t = CastRay(ro, rd);
    vec3 pos = vec3(ro + rd * t.x);

    vec3 col = vec3(0.0);

    if(t.x == -1.0)
    {

        col = vec3(.0); 

    }
    else
    {
        float depth = 1.0 - t.x;

        if(t.y == 0.0)
        {
            col = vec3(depth*.2);
        }
        else if(t.y == 1.0)
        {
            col = vec3(1.0, 1.0, 1.0) * depth * .5;
        }
        else if(t.y == 2.0)
        {
            col = vec3(1.0, .8, 0.0) * depth;
        }
    }

    return col;
}

vec3 GetViewDir(vec2 uv, vec3 cp, vec3 ct)
{
    vec3 forward = normalize(ct - cp);
    vec3 right = normalize(cross(vec3(0.0, -1.0, 0.0), forward));
    vec3 up = normalize(cross(right, forward));

    return normalize(uv.x * right + uv.y * up + 1.0 * forward);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    float time = iTime * .5;

    vec3 cp = vec3(sin(time), 1.5, cos(time));
    vec3 ct = vec3(0.0, 1.2, 0.0);

    vec3 vd = GetViewDir(uv, cp, ct);

    vec2 screenUV = fragCoord.xy / iResolution.xy;

    vec3 col = Render(cp, vd, screenUV);

    // compo

    col.rg -= screenUV.y * .5 * length(uv);
    col -= random(uv)*.1* length(uv) * .4;

    col = clamp(col, 0.0, 1.0);
    col *= 1.0-length(uv)*.5;

    col = sqrt(clamp(col, 0.0, 1.0)) * 1.2;

    fragColor = vec4(col,1.0);
}

#define MUL 2048.0
#define iMUL 2048

#define DETAIL 2

int intSin(int x){
	return int(sin(float(x)/MUL)*MUL); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= vec2(0.5, 0.5);
    // Output to screen
    fragColor = vec4(0.0, 0.0, 0.0 ,1.0);
    /***************************************************************************/
    int posX  = 0;
    int posY  = 0;
    int posZ  = int(iTime*3000.0);

    int dirX = int(uv.x*MUL)/(2*DETAIL);
    int dirY = int(uv.y*MUL)/(2*DETAIL);
    int dirZ = iMUL/(2*DETAIL);

    bool ignore = false;

    for (int i=0; i<25*DETAIL; ++i){
        if (!ignore){
            int rawDist = posX*posX + posY*posY;
            int dist = rawDist + intSin(posX*posY/256 + posZ)*512;

            if (dist > iMUL*iMUL){
            	ignore = true;
				int div = (posZ-int(iTime*3000.0))/25; 

                int cg = (dist-rawDist)/iMUL*2;
                int cb = abs(intSin(posZ));
                int cr = (cb+cg)/2;

                fragColor = vec4(float(cr-div)/MUL,float(cg-div)/MUL,float(cb-div)/MUL, 1.0);
            }
            posX += dirX;
            posY += dirY;
            posZ += dirZ;
        }	
    }  
}

#define MAX_DIST 50.0
#define PI 3.1415927

//as always, thanks to IQ for sharing this knowledge :)

vec2 rotate(vec2 a, float d){
    float s  = sin(d);
    float c = cos(d);

    return vec2(
        a.x*c-a.y*s,
        a.x*s+a.y*c);
}


float box(vec3 p, vec3 b)
{
    vec3 d = abs(p)-b;
    return length(max(d,0.0));
}

float husk(vec3 bp, vec3 p)
{

    return box(bp-vec3(0.,-2.,-4),vec3(10,3.+cos(p.x+p.z+p.y)/4.+bp.x/2.,.1));
}


vec2 map(vec3 p)
{
    p.x-=.5;
    vec3 bp = p+vec3(-4.,0.,0);

    bp.yz=rotate(bp.yz,PI*iTime/2.);

    bp.x+=((bp.y*bp.y)+(bp.z*bp.z))/8.;
    float b = box(bp,vec3(.1,2,2));

    bp.yz=rotate(bp.yz,PI/4.);

    b = min(b,box(bp,vec3(.1,2,2)));


    float stem = box(bp+vec3(.5,0,0),vec3(1.,.5,.5));

    vec2 st = vec2(atan(p.z,p.y), length(p));

    float x = clamp(.5+(p.x+4.5)/10.,0.0,1.0);
    float c=length(p/vec3(2.5-p.x/10.,1.,1.))-2.+(smoothstep(1.,-1.,abs(cos(iTime*10.+p.x*10.+.6)))/10.*x)+
        (smoothstep(1.,-1.,abs(cos(st.x*10.)))/10.)*x;

    float r=min(c,b);
    r=min(r,stem);

    float m=0.0;

    if(r==c)m=1.;
    else if(r==b||r==stem)m=2.;


    return vec2(r,m);
}

vec3 normal(vec3 p)
{
    vec2 e = vec2(0.0001,0.);
    return normalize(vec3(
        map(p+e.xyy).x-map(p-e.xyy).x,
        map(p+e.yxy).x-map(p-e.yxy).x,
        map(p+e.yyx).x-map(p-e.yyx).x
    ));

}

vec2 ray(vec3 ro, vec3 rd)
{
    float t =0.0;
    float m = 0.0;

    for(int i=0;i<128;i++)
    {

        vec3 p=ro+rd*t;
        vec2 h=map(p);
        m=h.y;
        if(h.x<0.00001)break;
        t+=h.x;
        if(t>MAX_DIST)break;
    }

    if(t>MAX_DIST)t=-1.;

    return vec2(t,m);
}

vec3 color(vec3 p, vec3 n, vec2 t)
{
    vec3 c = vec3(0.);
    vec3 mate = vec3(1.32,1,0);
    if(t.y>1.5)
    {
        mate=vec3(0.,.125,0.);
    }
    vec3 sun=normalize(vec3(0.2,0.5,-0.5));
    float dif=clamp(dot(n,sun),0.0,1.0);
    float sha = step(ray(p+n*.001,sun).x,0.0);
    float sky = clamp(0.5+0.5*dot(n,vec3(0,1,0)),0.,1.);
    float bou = clamp(0.5+0.5*dot(n,vec3(0,1,0)),0.,1.);

    c=mate*vec3(0.5,0.6,0.5)*dif*sha;
    c+=mate*vec3(0.2,0.3,.8)*sky;
    c+=mate*vec3(0.2,0.1,0.1)*bou;

    return c;
}

vec3 render(vec3 ro, vec3 rd)
{    
    vec2 st = vec2(atan(rd.y,rd.x), length(ro));

	vec3 c = vec3(0.,.1,0.)*(.5+smoothstep(-0.,1.,cos(st.x*40.+iTime*3.)));

    vec2 t = ray(ro,rd);

    if(t.x>0.)
    {
        vec3 p = ro+rd*t.x;
        vec3 n=normal(p);

        c=color(p,n,t);

    }
   	c=pow(c,vec3(0.454545));

    return c;
}


void mainImage( out vec4 c, in vec2 f )
{
    vec2 uv = (2.*f-iResolution.xy)/iResolution.y;

    float d = 10.;
    vec3 ro = vec3(sin(PI)*d,0,cos(PI)*d);
    vec3 ta = vec3(0.,0,0.);
    vec3 camF =	normalize(ta-ro);
    vec3 camU = normalize(cross(camF,vec3(0,1,0)));
    vec3 camR = normalize(cross(camU,camF));

    vec3 rd = normalize(uv.x*camU+uv.y*camR+2.*camF);

    c.rgb=render(ro,rd);
}

