//from https://www.shadertoy.com/view/XsX3zB
vec3 random3(vec3 c) {
	float j = 4096.0*sin(dot(c,vec3(17.0, 59.4, 15.0)));
	vec3 r;
	r.z = fract(512.0*j);
	j *= .125;
	r.x = fract(512.0*j);
	j *= .125;
	r.y = fract(512.0*j);
	return r-0.5;
}

/* skew constants for 3d simplex functions */
#define F3 0.333333
#define G3 0.1666667

/* 3d simplex noise */
float simplex3d(vec3 p) {
	 /* 1. find current tetrahedron T and it's four vertices */
	 /* s, s+i1, s+i2, s+1.0 - absolute skewed (integer) coordinates of T vertices */
	 /* x, x1, x2, x3 - unskewed coordinates of p relative to each of T vertices*/

	 /* calculate s and x */
	 vec3 s = floor(p + dot(p, vec3(F3)));
	 vec3 x = p - s + dot(s, vec3(G3));

	 /* calculate i1 and i2 */
	 vec3 e = step(vec3(0.0), x - x.yzx);
	 vec3 i1 = e*(1.0 - e.zxy);
	 vec3 i2 = 1.0 - e.zxy*(1.0 - e);

	 /* x1, x2, x3 */
	 vec3 x1 = x - i1 + G3;
	 vec3 x2 = x - i2 + 2.0*G3;
	 vec3 x3 = x - 1.0 + 3.0*G3;

	 /* 2. find four surflets and store them in d */
	 vec4 w, d;

	 /* calculate surflet weights */
	 w.x = dot(x, x);
	 w.y = dot(x1, x1);
	 w.z = dot(x2, x2);
	 w.w = dot(x3, x3);

	 /* w fades from 0.6 at the center of the surflet to 0.0 at the margin */
	 w = max(0.6 - w, 0.0);

	 /* calculate surflet components */
	 d.x = dot(random3(s), x);
	 d.y = dot(random3(s + i1), x1);
	 d.z = dot(random3(s + i2), x2);
	 d.w = dot(random3(s + 1.0), x3);

	 /* multiply d by w^4 */
	 w *= w;
	 w *= w;
	 d *= w;

	 /* 3. return the sum of the four surflets */
	 return dot(d, vec4(52.0));
}
// --------------------------------------------------

vec3 RayMarch(ray ray, float maxDistance, int maxSteps)
{
    float maxStepsf = float(maxSteps);
    float stepSize = maxDistance/maxStepsf;
    vec3 total = vec3(0.0);

    // Stop the shimmering??
    //float currentDistance = stepSize-(ray.origin.z-(floor(ray.origin.z/stepSize)*stepSize));
    float currentDistance = 1.0;

    float strike = 1.0+smoothstep(0.5,1.0,sin(ray.direction.z+iTime*20.0)*sin(ray.direction.x +iTime*30.0)*cos(ray.direction.y +iTime*40.0)*(wrap(iTime)/289.0));

    for(float i=0.0; i<maxStepsf; i++) {
		vec3 currentPoint = ray.origin + ray.direction * currentDistance;

        float s = (1.0-abs(simplex3d(currentPoint)));
        s=s*s*s;
        s=smoothstep(0.1,2.0, s);

        float stepf = ((maxStepsf - i)/maxStepsf);
        s *= stepf;
        vec3 light = sin((currentPoint+vec3(iTime/10.0,iTime/13.0,iTime/15.0))/4.0)*0.5+1.0; //Yes we blow out the colour a little.
        total += light*s;
        currentDistance += stepSize;
    }
    return (total*(3.0/maxStepsf))*strike;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized Pixel coordinates (from -0.5 to +0.5, center at 0,0)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    // Normalized Mouse coordinates (from -0.5 to +0.5, center at 0,0)
    vec2 mouse = ((iMouse.xy-0.5*iResolution.xy)/iResolution.y);// - vec2(0.5,0.5)*(iMouse.w*0.1);
    //Snap to center on startup.
    if(iMouse.x <= 1.0 && iMouse.y <= 1.0) {
        mouse = vec2(0.0,0.0);
    }

    // -- 1st person cammera
    vec3 forward = vec3(
        sin(mouse.x*PI),
    	sin(mouse.y*PI),
    	cos(mouse.x*PI)
    );
    camera camera = getCameraDirection(vec3(0.0,0.0,iTime), forward, 0.5);

    ray ray = getRay(uv, camera);
	vec3 colour = RayMarch(ray, RAY_MAX_DISTANCE, RAY_MAX_STEPS);
    float gamma = 0.8;
    colour = pow(colour, vec3(1.0/gamma));

    fragColor = vec4(colour,1.0);

}

#define PI 3.14159
#define t iTime*PI/4.

vec2 cartesian2polar(vec2 cartesian){
	return vec2(atan(cartesian.x,cartesian.y),length(cartesian.xy));
}

vec2 polar2cartesian(vec2 polar){
	return polar.y*vec2(cos(polar.x),sin(polar.x));
}

vec2 rotate2D(vec2 coords, float amount){
	return polar2cartesian(cartesian2polar(coords)+vec2(amount,0.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv = rotate2D(uv, t);

    vec3 col = vec3(.3);

    int iterationID = 0; 
    float iterations =  max(2. + (-4.*cos(t/2.)-PI/2.),0.);		//how many layers, you can manually just enter 5.0 or 20.0 and see what happens
   	float circleRadius = .25;

    for(int i = 0; i< int(iterations); i++){               //loop for KIFS fractal, keep transforming the uvs inside the inner circles into the uv of the whole circle
 	float distToInner = length(abs(uv)-vec2(0,circleRadius));
    	if(distToInner<circleRadius){			//inner circle
       	uv.y = (uv.y>=0.)?(uv.y-circleRadius):(uv.y+circleRadius);
        uv*=2.;
        iterationID++;
    	uv = rotate2D(uv,-PI/2. + t * float(iterationID)  );
       	}

    }

 	float distToInner = length(abs(uv)-vec2(0,circleRadius));
    	if(distToInner<circleRadius*fract(iterations) ){			//inner circle
       	uv.y = (uv.y>=0.)?(uv.y-circleRadius):(uv.y+circleRadius);
        uv*=2.;
        iterationID++;
    	uv = rotate2D(uv,-PI/2. +  t * float(iterationID)  );
       	}

    if(length(uv)<.5){
    col = vec3(step(0.,uv.x));

        float distToInner = length(abs(uv)-vec2(0,.25));
    	if(distToInner<circleRadius){			//inner circle
        	col = vec3(step(0.,uv.y) ); 

            if(distToInner>circleRadius/2.){ 	//final most inner circle/iris
        		 col = vec3( -col.x +1.); 
            }
    	} 
    }
    fragColor = vec4(col,1.0);
}

