#define SIN(a) smoothstep(-4., 2.5, sin(a))
#define SIN2(a) (SIN(a)*SIN(a))

vec2 rot(vec2 p, float a)
{
    vec2 i = vec2(cos(a), sin(a));
    return vec2(p.x*i.x - p.y*i.y, p.x*i.y + p.y*i.x);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;

    uv.x += .1*sin(uv.y*uv.x*40.);

    uv += rot(uv, length(uv+iTime));

    vec2 tuv = abs(log(-uv*.005 + 0.909));


    float v = 1.01 -.0005;
    // Time varying pixel color
    vec3 col = vec3(
    	SIN2(20./pow(tuv.x,v*v*v)),
    	SIN2(20./pow(tuv.x,v*v)),
    	SIN2(20./pow(tuv.x,v))
    )/length(tuv*7.);

    // Output to screen
    fragColor = vec4(col,1.0);
}

float makeBand(float d, float start, float end, float blur)
{
    float band1 = smoothstep(start - blur, start + blur, d);
    float band2 = smoothstep(end + blur, end - blur, d);
    return band1*band2;
}

float makeRect(vec2 uv, float left, float right, float top, float bottom, float blur)
{
    float verticalBand = makeBand(uv.x ,left,right,blur );
    float horizontalBand = makeBand(uv.y, bottom, top, blur);
    return verticalBand * horizontalBand;
}

float makeDiamond(vec2 uv, float left, float right, float top, float bottom, float blur)
{
    float verticalBand = makeBand(uv.x +uv.y ,left,right,blur );
    float horizontalBand = makeBand(uv.y - uv.x, bottom, top, blur);
    return verticalBand * horizontalBand;
}

float makeCircle(vec2 xy, float r, vec2 p, float blur)
{   

    return (1.0f-smoothstep(r,  r*blur,length(p-xy)));

}

float makeSmileMask(vec2 xy, vec2 p, float r, float blur)
{
    float mask = 0.0f,mask_mouth=0.0f,mask_leftEye=0.0f,mask_rightEye=0.0f;
    //mask = makeCircle(xy,0.2f,vec2(0,0),1.1f);       
    mask = makeCircle(xy,r,p,blur);
    mask_leftEye = makeCircle(xy,0.12f*r,vec2(p.x - 1.0f/3.0f*r,p.y + 1.0f/3.0f*r),1.5f*blur);
    mask_rightEye = makeCircle(xy,0.12f*r,vec2(p.x + 1.0f/3.0f*r,p.y + 1.0f/3.0f*r),1.5f*blur);
    mask_mouth = makeCircle( xy , 0.8f * r , p , blur) - makeCircle(xy,0.75f*r,vec2(p.x,0.2f * r+p.y),blur);
    mask -= (mask_leftEye + mask_rightEye + mask_mouth);
    return mask;
}



void makePalmTreeWithGround(inout vec3 col, in vec2 xy,in vec2 dToTreeOrigin,in float leafNum,in float originCircleRadius, in float palmTreeLengthOffset, in float bendLeafOffset)
{

 	//tree
    float r = originCircleRadius + palmTreeLengthOffset * cos(atan(dToTreeOrigin.y,dToTreeOrigin.x) * leafNum + bendLeafOffset * dToTreeOrigin.x + 2.0f * sin(iTime));    
    col *= smoothstep(r - 0.02f, r, length(dToTreeOrigin));
    float branchEdgeWidth =  0.03f;
    //wave edge on the branch
    branchEdgeWidth += 0.01f + 0.0055f * cos(10.0f * dToTreeOrigin.y * 10.0f);
    //the ground
    branchEdgeWidth += 0.000001f * exp(-12.0f * dToTreeOrigin.y);
    //cut down upper part of the branch
    col *= 1.0f - (1.0f - smoothstep(branchEdgeWidth,branchEdgeWidth+0.02f,abs(dToTreeOrigin.x + 0.2f * sin(2.0f * dToTreeOrigin.y)))) * (1.0f - smoothstep(0.0f,0.1f,dToTreeOrigin.y));   
}

float remap1(float a, float b, float t) //remap t to a - b in a range of 0-1 
{
 	return ((t-a) / (b - a));
}

float remap2(float a , float b, float c, float d , float t)
{
    return remap1(a,b,t) * (d-c) + c;
}

float DistToLine(vec3 ro, vec3 rd, vec3 p)
{
 	return length(cross(p-ro,rd))/ length(rd);   
}

