    //Sun
   	float r_Sun = 0.6f + 0.2f * cos(atan(vToSun.y,vToSun.x) * 10.0f + 20.0f * sin(t)); 
    col *= smoothstep(r_Sun - 0.02f, r_Sun, dToSun);

    //smile face
    float mask=0.0f;
