    mask = makeSmileMask(xy,rSunOrigin,0.05f,1.1f);
	col_SmileFace*=mask;

    //jumping star
    float starMask = makeDiamond(xy-vec2(1.0f * sin(t),0.2f * abs(cos(t * 2.0f))) - vec2(0,-0.3f), -.03f * abs(sin(iTime)) , .03f* abs(sin(iTime)) ,.03f * abs(sin(iTime)),-.03f* abs(sin(iTime)), .002f);
    float star2Mask = makeRect(xy-vec2(1.0f * sin(t *1.5f),0.2f * abs(cos(t * 4.0f))) - vec2(0,-0.3f), -.04f * abs(sin(iTime)) , .04f* abs(sin(iTime)) ,.04f * abs(sin(iTime)),-.04f* abs(sin(iTime)), .002f);

    //river
    float riverX = xy.x;
    float riverY = xy.y;
    riverY +=0.42f;
    float m = 0.03f*sin(t+riverX*8.0f);
    riverY +=m;
    float blur = remap2(-.9f,.9f, 0.002f,0.05f,riverX);
    float riverMask = makeRect(vec2(riverX,riverY),-.9f,.9f, .01f,-.01f,blur);


    col += col_SmileFace+ starMask + star2Mask + riverMask +col_w ;

    fragColor = vec4(col,1.0f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy -.5*iResolution.xy)/iResolution.y;
    vec2 st = vec2(atan(uv.x, uv.y), length (uv));

    float t = iTime;
    float d = 5.*cos(uv.x*t);
    uv = vec2(st.x/6.2831+.5, st.y);

    float x = uv.x*d;
    float m = min(fract(x), fract(1.-x));
    float c = smoothstep(0.,.1, m*.5+.2-uv.y);



    fragColor = vec4(c);
}

const float G = 6.67408e-11;
const float mass = 1.0e9;
const vec3 bhpos = vec3(0, 0, 0); 

float map(vec3 p)
{
    p = min(max(p, -256.0), 256.0);
    p = mod(p, 32.0) - 16.0;
	float s = distance(p, vec3(0, 0, 0.0))-4.0;
    return s;
}
float blackhole(vec3 p)
{
    vec3 p1 = p;
    p.x = p1.y;
    p.y = p1.x;
	vec2 t = vec2(5.0, 0.25);
    vec2 q = vec2(length(p.xz)-t.x,p.y);
    return length(q)-t.y;
}
vec3 getnormal (vec3 p)
{
 const float eps = 0.001;

 return normalize
 ( vec3
 	( map(p + vec3(eps, 0, 0) ) - map(p - vec3(eps, 0, 0)),
 	  map(p + vec3(0, eps, 0) ) - map(p - vec3(0, eps, 0)),
	  map(p + vec3(0, 0, eps) ) - map(p - vec3(0, 0, eps))
 	)
 );
}
vec3 holefeild (vec3 p)
{
 const float eps = 0.001;

 return normalize
 ( vec3
 	( blackhole(p + vec3(eps, 0, 0) ) - blackhole(p - vec3(eps, 0, 0)),
 	  blackhole(p + vec3(0, eps, 0) ) - blackhole(p - vec3(0, eps, 0)),
	  blackhole(p + vec3(0, 0, eps) ) - blackhole(p - vec3(0, 0, eps))
 	)
 );
}
float calcGrav(float r, float m)
{
	return G * m/(r * r);
}
vec4 march(vec3 ori, vec3 dir)
{
    vec3 pos = ori + dir;
    float bhrad = 1.0;
	for(int i = 0; i < 400; ++i)
    {
        float bh = blackhole(pos);
        if(bh < bhrad)
            return vec4(0.0);
		vec3 bhdir = holefeild(pos);
        float grav = calcGrav(bh, mass);
        float cs = map(pos);
       	dir = normalize(dir - bhdir * grav);
        pos += dir * min((bh - bhrad), cs);

        if(cs < 0.001)
            return vec4(pos, cs);
    }
    return vec4(0.0);
}
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}
mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
	vec3 eye = vec3(30.0 * sin(iTime * 0.5), 10.0, 30.0 * cos(iTime * 0.5));
    mat4 viewToWorld = viewMatrix(eye, vec3(0.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0));
    vec3 worldDir = (viewToWorld * vec4(viewDir, 0.0)).xyz;
    vec3 normal, color;

    // Time varying pixel color
    vec4 hit = march(eye, worldDir);
    normal = getnormal(hit.xyz);
    if(hit.w != 0.0)
        color = vec3(0.2, 0.7, 0.8) * max(dot(normal, normalize(vec3(0, 0.5, -0.5))), 0.1);

    // Output to screen
    fragColor = vec4(color,1.0);
}

