vec2 hash22(vec2 p) {
    p = p*mat2(127.1,311.7,269.5,183.3);
	p = -1.0 + 2.0 * fract(sin(p)*43758.5453123);
	return sin(p*6.283);
}
float perlin_noise(vec2 p) {
	vec2 pi = floor(p);
    vec2 pf = p - pi;

    // interpolation
    vec2 w = pf * pf * (3.0 - 2.0 * pf);

    float f00 = dot(hash22(pi + vec2(0.0, 0.0)), pf - vec2(0.0, 0.0));
    float f01 = dot(hash22(pi + vec2(0.0, 1.0)), pf - vec2(0.0, 1.0));
    float f10 = dot(hash22(pi + vec2(1.0, 0.0)), pf - vec2(1.0, 0.0));
    float f11 = dot(hash22(pi + vec2(1.0, 1.0)), pf - vec2(1.0, 1.0));

    // mixing top & bottom edges
    float xm1 = mix(f00, f10, w.x);
    float xm2 = mix(f01, f11, w.x);

    // mixing to point
    float ym = mix(xm1, xm2, w.y); 

    return ym;
}
float perlin_noise1(vec2 p) {
    vec2 count ;
    for (float i = 0.; i < 9.; i++){
        count += hash22 ( floor(p*10.)/10. +vec2(mod(i,3.)-1., i/3.-1.) );
    }
    count /= 9.;
    return count.x;
}
float noise_fBM(float size, int octaves, float persistence, float scale, vec2 coord) {
    float c = 0.0;
    float p = 1.0;
    float n = 0.0;
    for (int i = 1; i <= octaves; i++) {
        c += perlin_noise(coord * size) * p;
        n    += p;
        size *= scale;
        p    *= persistence;
    }
    c /= n;
    return c;
}
/////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////

float clouds(vec2 p) {
    float c = 2.5 * noise_fBM(2.0, 6, 0.5, 2.0, p*vec2(1.,2.1/(pow(p.y,2.))) + vec2(iTime * ( 0.01), 0.0));
 	return c;
}

float water(vec2 p) {
    float c = 4.5 * noise_fBM(2.0, 6, 0.5, 2.0, p*vec2(1.,4.) )*.4+ +1.-p.y*3.2;
 	return c;
}

float bit(float b){
    return floor(b*2.+.4)/2.;
}

vec3 bit(vec3 b){
    return floor(b*2.+.4)/2.;
}

float stepin(float a, float b, float x){
    return a < x && x < b ? 1. : 0.;
}

void wood(inout vec3 canvas, vec2 p)
{
    float cf = (abs(mod(p.y+cos(p.x*10.),.03)*20.-1.));
    cf = (cf*=cf)*(cf*=cf);
    cf = bit(1.-cf*.5);
    p -=  vec2(-0.12,+0.04);
    canvas = mix(canvas, bit(vec3(p.x*2.))+(vec3(.4,.1,0.))*cf, (stepin(0.5,2., -atan(-p.x,p.y))) * step( 0.002, (.01-p.y)*.15+.08-abs(length((p-vec2(0.,0.))*vec2(4.,1.0))-.5)  ));
}

mat2 rotate(float a)
{
	return mat2(cos(a), sin(a), -sin(a), cos(a));
}

void foliage(inout vec3 canvas, vec2 p)
{
	p*= 2.0;
    p -=  vec2(-0.0,-0.18);
    vec3 c1 = vec3(.0,.9,0.0);
    vec3 c2 = vec3(.23,.56,0.23);
    vec3 c = p.x>0.0?c1:c2;
    float cf = abs(mod(p.y,.1)*20.-1.);
    p.x *= 1.+pow(cf,8.);
    float f = .2-length(p*vec2(8.*(0.5-p.y),1.));
    canvas = mix(canvas, c,  step( 0.002, f));
    canvas = mix(canvas, vec3(1.),  step( 0.002, min(f, .004-abs(p.x))));
}

void coconutTree(inout vec3 canvas, vec2 p)
{
    for (int i = 0; i < 5; i++)
		foliage(canvas, rotate(float(5-i)*.56-1.75)*(p-vec2(.0,0.25))+vec2(0.,abs(p.y*.1)));
    wood(canvas, p-vec2(.0,-.07));
}

void johnny(inout vec3 canvas, vec2 p)
{
    p *= vec2(.5,1.);
    p *= vec2(1.5);

	p *= vec2(2.- clamp(iTime*.2-.1,0.,1.));
    p += vec2(0.7,-.4)* vec2(1.- clamp(iTime*.2-.1,0.,1.));

    float t = abs(fract(iTime*.1)*2.-1.);
    float t2 = abs(fract(iTime*.7)*2.-1.);
	float y = cos(t*5.);
    p -=  vec2(t*.2-.1,-0.05);
    p *= 2.;
    float f;

    //head
    vec3 skin = vec3(1.0,0.7,0.5);
    float f0 = .06-length(p*vec2(2.,1.1)-vec2(0.,.05));
    canvas = mix(canvas, skin,  step( 0.002, f0));

    // beard
    float f1 = .05-length(p*vec2(8.*(0.2-p.y),1.));
    canvas = mix(canvas, vec3(0.),  step( 0.002, f1));

    //hair
    f = .05-length(p*vec2(8.*(0.3-p.y),1.)+vec2(0.,-.04));
    f =  .01-abs(f);
    f =  -max(-f,f1);
    canvas = mix(canvas, vec3(0.0),  step( 0.002, f));

    // mouth
    f = .02-length(p*vec2(1.,4.1));
    canvas = mix(canvas, skin+.1,  step( 0.002, f));

    // nose
    f = .03-length(p*vec2(2.,2.1)+vec2(0.,-.06));
    canvas = mix(canvas, skin+.1,  step( 0.002, f));

    // hat
    f =        .03-length(p*vec2(1.0,2.1)+vec2(0.,-.22));
    f = max(f, .03-length(p*vec2(1.5,2.1)+vec2(0.,-.25)));
    canvas = mix(canvas, vec3(1.),  step( 0.002, f));

    // eye white
    f = .01-length(p*vec2(1.,2.1)+vec2((p.x>0.)?-0.01:0.01,-.13));
    canvas = mix(canvas, vec3(1.),  step( 0.002, f));

    // eye black
    f = .007-length(p*vec2(1.,1.1)+vec2((p.x>0.)?-0.01:0.01,-.07));
    canvas = mix(canvas, vec3(0.),  step( 0.002, f));

    ////////////////
    p *= vec2(1.5);

    float side = (p.x>0.)?-1.:1.;
    //arms
    f = .07-length(rotate((t2-0.5+side)*.1)*p*vec2(3.,-0.9)+vec2( side*0.1,-.13));
    canvas = mix(canvas, skin,  step( 0.002, f));
	canvas = mix(canvas, vec3(0.),  step( 0.002, 0.008-abs(f)));

 	//legs
    f = .07-length(rotate((t2-0.5+side)*.03)*p*vec2(3.,-0.9)+vec2((p.x>0.)?-0.05:0.05,-.26));
    canvas = mix(canvas, skin,  step( 0.002, f));
    canvas = mix(canvas, vec3(0.),  step( 0.002, 0.008-abs(f)));

    // body
    f = .07-length(p*vec2(3.,-0.9)+vec2((p.x>0.)?-0.01:0.01,-.13+0.02));
    canvas = mix(canvas, skin,  step( 0.002, f));
 	canvas = mix(canvas, vec3(0.),  step( 0.002, 0.008-abs(f)));

    // pants
    f = .07-length((p)*vec2(3.,-1.7)+vec2((p.x>0.)?-0.01:0.01,-.35));
    canvas = mix(canvas, vec3(1.),  step( 0.002, f));
    canvas = mix(canvas, vec3(0.),  step( 0.002, 0.008-abs(f)));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float re = 400.;

    vec2 r= iResolution.xy;
    uv = fragCoord/iResolution.xy;
    uv = floor(uv*re)/re;
    vec2 cp = vec2(0.,1.)+uv*vec2(1.,-1.);

    vec2 aspectRatio  = vec2( iResolution.x/iResolution.y,1.);
    vec2 p = (uv-.5)*aspectRatio;
	vec2 pt = (uv-.5)*aspectRatio;

    vec3 canvas ;
    canvas = vec3(0.,0.97,0.97);

    float c = clouds(uv);
    vec3 cloud = bit(canvas+ abs(vec3(c))+.5);

    float cb1 = step(.3,clouds(uv))-step(.3,clouds(uv-vec2(1./r)));
    float cb2 = step(.5,clouds(uv))-step(.5,clouds(uv-vec2(1./r)));
    float cb3 = step(.8,clouds(uv))-step(.8,clouds(uv-vec2(1./r)));
    cloud = mix(cloud, vec3(0.8), step( 0.5, cb1 ));
    cloud = mix(cloud, vec3(0.8), step( 0.5, cb2 ));
    cloud = mix(cloud, vec3(0.8), step( 0.5, cb3 ));

    canvas = mix(canvas, cloud, 1.-step( 0.4, cp.y ));

    float w2 = water(uv+vec2(0.,-0.7));
    float w = water(uv);
    canvas = mix(canvas, vec3(0.,0.5,0.97), step( 0.4, cp.y )*bit(w2));
    canvas = mix(canvas, vec3(0.,0.0,0.97), step( 0.45, cp.y ));
    canvas = mix(canvas, vec3(0.,0.0,0.48), step( 0.5, cp.y )*bit(w) );


    float down = step(.63, cp.y);
    float up = 1.-down;

    float pn = bit(perlin_noise(p*20.*vec2(1.,2.))*.4+.35)+.95;

    canvas = mix(canvas, vec3(0.9,.9,0.48), down*step( 0.0, .2-length((cp-vec2(0.,(1.,.5)))*vec2(1.,4.)-.5) ) );
    canvas = mix(canvas, vec3(1.,1.0,0.48)*pn, up *step( 0.0, .2-length((cp-vec2(0.,(1.,.4)))*vec2(1.,2.)-.5) ) );

    coconutTree(canvas, p);
    johnny(canvas, p);

    canvas = mix(canvas, vec3(0.),          step( 0.01, (length(p)-iTime+.3)  ));

    fragColor = vec4(canvas,1.0);
}

