// choose one of the two options and compile the shader again
// PART 1 = with aliasing
// PART 2 = with anti-aliasing
#define PART 1


#if PART == 1

// Function for making a disk :
// 	r = screen resolution
// 	center = position of the disk in the screen
// 	radius = size of the disk
// 	color = color of the disk
// 	pixel = color output (what will be returned by the function)
// 	We will assign all this parameters when calling the function

void disk(vec2 r, vec2 center, float radius, vec3 color, inout vec3 pixel) {

    // "length(r-center)" give us the distance between "r" and "center"
    //  It will only change the color when it's inferior to the radius which will create a disk
	if( length(r-center) < radius) {
		pixel = color; 
	}
}

void ring(vec2 r, vec2 center, float radius, vec3 lineColor, float lineThickness, inout vec3 pixel){

    // For making a ring, we will need to create two disk
    // and then adding color between them
    // 1st disk = "length(r-center) < radius"
    // 2nd disk = "length(r-center) > (radius - lineThickness)"
    if(length(r-center) < radius && length(r-center) > (radius - lineThickness)){        
		pixel = lineColor;        
	}
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 r =  2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y; // Screen resolution in [-1.0, 1.0] scale

    vec3 pixel = vec3(0.1); // Base color
    vec3 col1 = vec3(0.85,0.2,0.25); // red
    vec3 col2 = vec3(0.95,0.92,0.95); // white

    float ringRadius = 0.7; 

    // The math function to have a object moving in circle : 
    // X = originX + cos(angle)*radius;
	// Y = originY + sin(angle)*radius;

    // In this example the origin of the ring is the center 
    // of the screen ([0.0, 0.0]) so it's not important. 
    // I also wanted to have a moving object so I replace
    // "angle" 
    // (the object has a specific position on the ring depending on the angle)
    // by "iTime". 
    // (the object position depend on the time value which is always increasing)
    vec2 mouvCircle = vec2((cos(iTime)*ringRadius),(sin(iTime)*ringRadius));


	// calling functions
    ring(r, vec2(0.0), ringRadius, col2, 0.05, pixel);
    disk(r, mouvCircle, 0.2, col1, pixel);


    // Output to screen
    fragColor = vec4(pixel,1.0);
}


#elif PART == 2

	// In PART 1, we were able to see the pixel around 
	// the disk and the ring, this is aliasing.
	// Here, we will reduce this effect by making the edges
	// fade which will give a nicer look to our objects.

void disk(vec2 r, vec2 center, float radius, vec3 color, inout vec3 pixel) {
	if( length(r-center) < radius) {

        // We will use the "smoothstep" function.
        // This function act like a "lerp" function but 
        // will start slow and end slow (giving a better look) 

        // exemple : ValueA = smoothstep( 0, 1, ValueB)
        // "ValueB" will interpolate the "ValueA" between 0 and 1.

        // Thank to that, we can fade the edges of the disk by 
        // adjusting the brightness of it. 
        vec3 brightness = vec3(smoothstep(radius, radius - 0.06, length(r-center)));
		pixel = vec3(brightness * color); // adding color

	}
}

void ring(vec2 r, vec2 center, float radius, vec3 lineColor, float lineThickness, inout vec3 pixel){
    if(length(r-center) < radius && length(r-center) > (radius - lineThickness)){ 

        // For the ring, we need to put the "smoothstep" function on the two edges.
        // We start by taking the middle of the ring :
        // ((radius - lineThickness) + (radius - (radius - lineThickness))*0.5)
        // and then we use "smoothstep" from the middle of the ring to the two edges.

        // (edge1 to middle) * (middle to edge2) * Intensity

        lineColor = vec3(smoothstep(radius, (radius - lineThickness) + (radius - (radius - lineThickness))*0.5, length(r-center)))
            	* vec3(smoothstep( (radius - lineThickness) + (radius - (radius - lineThickness))*0.5, radius, length(r-center))) * 4.0;

		pixel = vec3(lineColor);        

	}
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 r =  2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;

    vec3 pixel = vec3(0.0);
    vec3 col1 = vec3(0.15, 0.2, 0.95);
    vec3 col2 = vec3(0.95, 0.92, 0.95);

    float ringRadius = 0.7;

    vec2 mouvCircle = vec2((cos(iTime)*ringRadius),(sin(iTime)*ringRadius));


    ring(r, vec2(0.0), ringRadius, col2, 0.09, pixel);
    disk(r, mouvCircle, 0.2, col1, pixel);


    // Output to screen
    fragColor = vec4(vec3(pixel), 1.0);
}

#endif

