	// clamp the domain
	Pi.xyz=Pi.xyz-floor(Pi.xyz*(1./69.))*69.;
	vec3 Pi_inc1=step(Pi,vec3(69.-1.5))*(Pi+1.);

	// calculate the hash ( over -1.0->1.0 range )
	vec4 Pt=vec4(Pi.xy,Pi_inc1.xy)+vec2(50.,161.).xyxy;
	Pt*=Pt;
	Pt=Pt.xzxz*Pt.yyww;
	const vec3 SOMELARGEFLOATS=vec3(635.298681,682.357502,668.926525);
	const vec3 ZINC=vec3(48.500388,65.294118,63.934599);
	vec3 lowz_mod=vec3(1./(SOMELARGEFLOATS+Pi.zzz*ZINC));
	vec3 highz_mod=vec3(1./(SOMELARGEFLOATS+Pi_inc1.zzz*ZINC));
	vec4 hash_x0=fract(Pt*lowz_mod.xxxx)*2.-1.;
	vec4 hash_x1=fract(Pt*highz_mod.xxxx)*2.-1.;
	vec4 hash_y0=fract(Pt*lowz_mod.yyyy)*2.-1.;
	vec4 hash_y1=fract(Pt*highz_mod.yyyy)*2.-1.;
	vec4 hash_z0=fract(Pt*lowz_mod.zzzz)*2.-1.;
	vec4 hash_z1=fract(Pt*highz_mod.zzzz)*2.-1.;

