	hash_y0=((hash_y0*hash_y0*hash_y0)-sign(hash_y0))*JITTER_WINDOW+vec4(0.,0.,1.,1.);
	hash_x1=((hash_x1*hash_x1*hash_x1)-sign(hash_x1))*JITTER_WINDOW+vec4(0.,1.,0.,1.);
