mat2 rot (float a) { float c=cos(a),s=sin(a); return mat2(c,-s,s,c); }
float sat(float a) {return clamp(a, .0, 1.); }

float opU(float s1, float s2) { return min(s1, s2);}

vec2 opU2(vec2 d1, vec2 d2) { return (d1.x < d2.x) ? d1 : d2; }

float opS( float d1, float d2 ) { return max(-d1,d2); }

float opI( float d1, float d2 ) { return max(d1,d2); }



float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0);
}

float sdGuy(vec3 p, float h)
{
    p.y -= h*.5;
    float t = sdBox(p, vec3(.05, h*.5, .025));
    t = opU(t, sdBox(p+vec3(0.0, -h*.5-.025, 0.0), vec3(.025, .025, .025)));
    return t;
}

float det( vec2 a, vec2 b ) { return a.x*b.y-b.x*a.y; }



vec2 sdLine( vec3 p, vec3 a, vec3 b )
{
    vec3 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return vec2( length( pa - ba*h ), h );
}

float sdSphere(vec3 pos, float r)
{
    return length(pos)-r;
}



float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

vec2 smin( vec2 a, vec2 b, float k )
{
    float h = clamp( 0.5 + 0.5*(b.x-a.x)/k, 0.0, 1.0 );
    return vec2( mix( b.x, a.x, h ) - k*h*(1.0-h), mix( b.y, a.y, h ) );
}

float smax( float a, float b, float k )
{
    float h = clamp( 0.5 + 0.5*(b-a)/k, 0.0, 1.0 );
    return mix( a, b, h ) + k*h*(1.0-h);
}

float dot2( in vec3 v ) { return dot(v,v); }
float udTriangle( vec3 p, vec3 a, vec3 b, vec3 c )
{
    vec3 ba = b - a; vec3 pa = p - a;
    vec3 cb = c - b; vec3 pb = p - b;
    vec3 ac = a - c; vec3 pc = p - c;
    vec3 nor = cross( ba, ac );

    return sqrt(
    (sign(dot(cross(ba,nor),pa)) +
     sign(dot(cross(cb,nor),pb)) +
     sign(dot(cross(ac,nor),pc))<2.0)
     ?
     min( min(
     dot2(ba*clamp(dot(ba,pa)/dot2(ba),0.0,1.0)-pa),
     dot2(cb*clamp(dot(cb,pb)/dot2(cb),0.0,1.0)-pb) ),
     dot2(ac*clamp(dot(ac,pc)/dot2(ac),0.0,1.0)-pc) )
     :
     dot(nor,pa)*dot(nor,pa)/dot2(nor) );
}



